INSERT INTO `creature_text` VALUES (4508, 0, 0, 'Woo hoo! Finally getting out of here. It\'s going to be rough though. Keep your eyes peeled for trouble.', 12, 0, 100, 0, 0, 0, 0, 1482, 0, 'willix SAY_READY');
