INSERT INTO `creature_text` VALUES (4509, 0, 0, 'In the throes of the Sundering, Ameth\'Aran was at the whim of the terror that gripped the land. There was little hope for survival.', 12, 7, 100, 1, 0, 0, 0, 1471, 0, 'Sargath');
INSERT INTO `creature_text` VALUES (4509, 1, 0, 'Athrikus came to us. He told us that he could save us from harm. He cast a spell upon us to protect us from harm.', 12, 7, 100, 1, 0, 0, 0, 1472, 0, 'Sargath');
INSERT INTO `creature_text` VALUES (4509, 2, 0, 'When the shaking stopped, his true motives were revealed. We were trapped, and he was slowly draining our powers.', 12, 7, 100, 1, 0, 0, 0, 1474, 0, 'Sargath');
INSERT INTO `creature_text` VALUES (4509, 3, 0, 'There were hundreds imprisoned by his spell. Now only a few remain in their prisons. He would speak to us sometimes.', 12, 7, 100, 1, 0, 0, 0, 1497, 0, 'Sargath');
INSERT INTO `creature_text` VALUES (4509, 4, 0, 'He worried that his power weakened, that soon even we last of his precious soulgems would fade and die.', 12, 7, 100, 1, 0, 0, 0, 1498, 0, 'Sargath');
INSERT INTO `creature_text` VALUES (4509, 5, 0, 'His lieutenant, Ilkrud Magthrull possesses a tome which might indicate the location of the remaining soulgems.', 12, 7, 100, 1, 0, 0, 0, 1475, 0, 'Sargath');
INSERT INTO `creature_text` VALUES (4509, 6, 0, '%s fades into the mist...', 16, 0, 100, 0, 0, 0, 0, 1476, 0, 'Sargath');
