INSERT INTO `creature_text` VALUES (4880, 0, 0, 'Ok, let\'s get started.', 12, 0, 100, 0, 0, 0, 0, 1610, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 1, 0, 'Now let\'s look for the herb.', 12, 0, 100, 0, 0, 0, 0, 1611, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 2, 0, 'Nope, not here...', 12, 0, 100, 0, 0, 0, 0, 1612, 0, '\"Stinky\" Ignatz');
