INSERT INTO `creature_text` VALUES (4880, 7, 0, 'Ok, now let\'s get out of here!', 12, 0, 100, 0, 0, 0, 0, 1617, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 8, 0, 'I can make it from here.  Thanks, $n!  And talk to my employer about a reward!', 12, 0, 100, 0, 0, 0, 0, 1618, 0, '\"Stinky\" Ignatz');
