INSERT INTO `creature_text` VALUES (4880, 10, 2, 'Look out!  The $n attacks!', 12, 0, 100, 0, 0, 0, 0, 1628, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4880, 10, 3, 'I\'m glad you\'re here!  Because I need your help!!', 12, 0, 100, 0, 0, 0, 0, 1631, 0, '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES (4944, 0, 0, '%s places the scroll on the table, and reads...', 16, 0, 100, 0, 0, 0, 0, 1671, 0, 'Captain Garran Vimes');
INSERT INTO `creature_text` VALUES (4944, 1, 0, 'By the Light!  This cursed spy was a good one! I\'m glad he\'s dead.', 12, 0, 100, 5, 0, 0, 0, 1672, 0, 'Captain Garran Vimes');
INSERT INTO `creature_text` VALUES (4944, 2, 0, 'Thank you for this report, $n.  It is now clear that we must tighten our patrols near Theramore.', 12, 0, 100, 0, 0, 0, 0, 1673, 0, 'Captain Garran Vimes');
INSERT INTO `creature_text` VALUES (4944, 3, 0, 'Tesoran! I need someone--Falgran Hastil, preferably--to take care of $n\'s lead on the prints near the Shady Rest Inn.', 12, 0, 100, 1, 0, 0, 0, 1753, 0, 'Captain Garran Vimes');
INSERT INTO `creature_text` VALUES (4948, 0, 0, 'Right away, sir.', 12, 0, 100, 1, 0, 0, 0, 1754, 0, 'Adjutant Tesoran');
INSERT INTO `creature_text` VALUES (4961, 0, 0, 'Now you\'re gonna get it good, $n!', 12, 0, 100, 0, 0, 0, 0, 1961, 0, 'Dashel Stonefist');
INSERT INTO `creature_text` VALUES (4961, 1, 0, 'Okay, okay! Enough fighting. No one else needs to get hurt.', 12, 0, 100, 0, 0, 0, 0, 1712, 0, 'Dashel Stonefist');
INSERT INTO `creature_text` VALUES (4961, 2, 0, 'It\'s okay, boys. Back off. You\'ve done enough. I\'ll meet up with you later.', 12, 0, 100, 0, 0, 0, 0, 1713, 0, 'Dashel Stonefist');
INSERT INTO `creature_text` VALUES (4966, 0, 0, 'Why don\'t we deal with you now, Hendel? Lady Proudmoore will speak to you back in the tower.', 12, 7, 100, 0, 0, 0, 0, 1663, 0, 'hendel SAY_PROGRESS_1_TER');
INSERT INTO `creature_text` VALUES (4966, 1, 0, 'Please... please... Miss Proudmore. I didn\'t mean to...', 12, 7, 100, 0, 0, 0, 0, 1759, 0, 'hendel SAY_PROGRESS_2_HEN');
INSERT INTO `creature_text` VALUES (4966, 2, 0, 'I apologize for taking so long to get here. I wanted Lady Proudmoore to be present also.', 12, 7, 100, 0, 0, 0, 0, 1761, 0, 'hendel SAY_PROGRESS_3_TER');
INSERT INTO `creature_text` VALUES (4966, 3, 0, 'We can only stay a few moments before returning to the tower. If you wish to speak to us more you may find us there.', 12, 7, 100, 0, 0, 0, 0, 1808, 0, 'hendel SAY_PROGRESS_4_TER');
INSERT INTO `creature_text` VALUES (4966, 4, 0, '%s, too injured, gives up the chase.', 16, 0, 100, 0, 0, 0, 0, 8224, 0, 'hendel EMOTE_SURRENDER');
INSERT INTO `creature_text` VALUES (4968, 0, 0, 'Perhaps I should explain...', 12, 0, 100, 1, 0, 0, 0, 33092, 0, 'Lady Jaina Proudmoore to Player');
INSERT INTO `creature_text` VALUES (4968, 1, 0, 'I had arranged for a secret summit between Warchief Thrall and King Wrynn, here in Theramore.', 12, 0, 100, 1, 0, 0, 0, 33093, 0, 'Lady Jaina Proudmoore to Player');
INSERT INTO `creature_text` VALUES (4968, 2, 0, 'The king was to travel here by ship, in secret. But the ship never arrived, and King Wrynn disappeared.', 12, 0, 100, 1, 0, 0, 0, 33094, 0, 'Lady Jaina Proudmoore to Player');
INSERT INTO `creature_text` VALUES (4968, 3, 0, 'Your help enabled us to connect the Defias to the mastermind behind the abduction, Onyxia, who was posing as a member of the royal court.', 12, 0, 100, 1, 0, 0, 0, 33095, 0, 'Lady Jaina Proudmoore to Player');
INSERT INTO `creature_text` VALUES (4968, 4, 0, 'Meanwhile, the king managed to escape his captors, and returned to Stormwind to deal with Onyxia.', 12, 0, 100, 1, 0, 0, 0, 33096, 0, 'Lady Jaina Proudmoore to Player');
INSERT INTO `creature_text` VALUES (4968, 5, 0, 'I will send word that you have discovered the link to the Defias. I was a fool to think them broken with the defeat of Edwin Van Cleef, and King Wrynn will want to make an example of all who remain.', 12, 0, 100, 1, 0, 0, 0, 33097, 0, 'Lady Jaina Proudmoore to Player');
INSERT INTO `creature_text` VALUES (4979, 0, 0, 'Hey, thanks.', 12, 0, 100, 0, 0, 0, 0, 22111, 0, 'theramore guard SAY_QUEST1');
INSERT INTO `creature_text` VALUES (4979, 1, 0, '...receive 50 percent off deserter undergarments? What is this garbage?', 12, 0, 100, 0, 0, 0, 0, 22555, 0, 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES (4979, 1, 1, '...to create a fund for the purchase of hair gel? I like my hair the way it is, thanks!', 12, 0, 100, 0, 0, 0, 0, 22562, 0, 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES (4979, 1, 2, '...the deserters seek a Theramore where citizens will be free to juggle at all hours of the day? What is this nonsense?', 12, 0, 100, 0, 0, 0, 0, 22559, 0, 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES (4979, 1, 3, '...to establish the chicken as the official symbol of Theramore?! These guys are nuts!', 12, 0, 100, 0, 0, 0, 0, 22561, 0, 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES (4979, 1, 4, '...as a deserter, you\'ll enjoy activities like tethered swimming and dog grooming? How ridiculous!', 12, 0, 100, 0, 0, 0, 0, 22556, 0, 'theramore guard SAY_QUEST2');
INSERT INTO `creature_text` VALUES (4979, 2, 0, 'This... this is a joke, right?', 12, 0, 100, 0, 0, 0, 0, 22113, 0, 'theramore guard SAY_QUEST3');
INSERT INTO `creature_text` VALUES (4979, 2, 1, 'I\'d never join anything like this. Better keep this, though. It\'ll come in handy in the privy...', 12, 0, 100, 0, 0, 0, 0, 22116, 0, 'theramore guard SAY_QUEST3');
INSERT INTO `creature_text` VALUES (4979, 2, 2, 'What a bunch of lunatics! You actually believe this stuff?', 12, 0, 100, 0, 0, 0, 0, 22115, 0, 'theramore guard SAY_QUEST3');
INSERT INTO `creature_text` VALUES (4979, 2, 3, 'That\'s the funniest thing I\'ve read in a long time. You\'ve just made my day!', 12, 0, 100, 0, 0, 0, 0, 22114, 0, 'Theramore Guard');
INSERT INTO `creature_text` VALUES (4980, 0, 0, 'W-what do you want? Just leave me alone...', 12, 0, 0, 0, 0, 0, 0, 1674, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 1, 0, 'I swear, I didn\'t steal anything from you! Here, take some of my supplies, just go away!', 12, 0, 0, 1, 0, 0, 0, 1675, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 2, 0, 'I... Well, I may of taken a little thing or two from the inn... but what would an ogre care about that?', 12, 0, 0, 1, 0, 0, 0, 1708, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 3, 0, 'Not one step closer, ogre!', 12, 0, 0, 25, 0, 0, 0, 1710, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 4, 0, 'And I don\'t know anything about this fire of yours...', 12, 0, 0, 1, 0, 0, 0, 1711, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 5, 0, 'What was that? Did you hear something?', 12, 0, 0, 6, 0, 0, 0, 1731, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 6, 0, '%s staggers backwards as the arrow lodges itself deeply in his chest.', 16, 0, 0, 0, 0, 0, 0, 1732, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 7, 0, 'Ugh... Hallan, didn\'t think you had it in you...', 12, 0, 0, 0, 0, 0, 0, 1722, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4980, 8, 0, 'Must be your lucky day. Alright, I\'ll talk. Just leave me alone. Look, you\'re not going to believe me, but it wa... oh, Light, looks like the girl could shoot...', 12, 0, 0, 0, 0, 0, 0, 1725, 0, 'Paval');
INSERT INTO `creature_text` VALUES (4983, 0, 0, 'I noticed some fire on that island over there. A human, too. Let\'s go check it out, $n.', 12, 1, 0, 0, 0, 0, 0, 1669, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 1, 0, 'That\'s Reethe alright. Let\'s go see what he has to say, yeah?', 12, 1, 0, 0, 0, 0, 0, 1670, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 2, 0, 'Just tell us what you know about the Shady Rest Inn, and I won\'t bash your skull in.', 12, 0, 0, 0, 0, 0, 0, 1707, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 3, 0, 'Look here, if you don\'t tell me about the fire--', 12, 0, 0, 0, 0, 0, 0, 1709, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 4, 0, 'Damn it! You\'d better not die on me, human!', 12, 0, 0, 0, 0, 0, 0, 1724, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 5, 0, 'Still with us, Reethe?', 12, 0, 0, 0, 0, 0, 0, 1726, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 6, 0, 'Looks dead to me. Not much use to us like this. You\'d better go back and tell Krog what happened.', 12, 0, 0, 0, 0, 0, 0, 1729, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (4983, 7, 0, 'By the way, thanks for watching my back.', 12, 0, 0, 0, 0, 0, 0, 1730, 0, 'Ogron');
INSERT INTO `creature_text` VALUES (5042, 0, 0, 'You\'re going to be just fine.', 12, 7, 100, 0, 0, 0, 0, 1682, 0, 'Nurse Lillian');
INSERT INTO `creature_text` VALUES (5042, 0, 1, 'Drink this, it will help.', 12, 7, 100, 0, 0, 0, 0, 1679, 0, 'Nurse Lillian');
INSERT INTO `creature_text` VALUES (5042, 0, 2, 'Let me help you with those.', 12, 7, 100, 0, 0, 0, 0, 1685, 0, 'Nurse Lillian');
INSERT INTO `creature_text` VALUES (5045, 0, 0, 'Gladly, sir.', 12, 0, 0, 0, 0, 0, 0, 1721, 0, 'Hallan');
INSERT INTO `creature_text` VALUES (5046, 0, 0, 'Paval Reethe! Found you at last. And consorting with ogres now? No fear, even deserters and traitors are afforded some mercy.', 12, 0, 0, 5, 0, 0, 0, 1714, 0, 'Caldwell');
INSERT INTO `creature_text` VALUES (5046, 1, 0, 'Private, show Lieutenant Reethe some mercy.', 12, 0, 0, 1, 0, 0, 0, 1720, 0, 'Caldwell');
INSERT INTO `creature_text` VALUES (5046, 2, 0, 'Now, let\'s clean up the rest of the trash, men!', 12, 0, 0, 25, 0, 0, 0, 1723, 0, 'Caldwell');
INSERT INTO `creature_text` VALUES (5088, 0, 0, 'Falgran Hastil reporting, sir. I will find where the hoofprints lead and report back to you.', 12, 0, 100, 1, 0, 0, 0, 1752, 0, 'Falgran Hastil');
INSERT INTO `creature_text` VALUES (5089, 0, 0, 'Alright! Alright! We surrender... Just put your weapons down, I\'ll cooperate!', 12, 7, 100, 0, 0, 0, 0, 1756, 0, 'Balos Jacken');
