INSERT INTO `creature_text` VALUES (5734, 0, 0, 'Hmm, it would seem Keever needs a new subject. If that fool Abernathy keeps taking Keever\'s subjects, Keever may have to have a word with him.', 12, 33, 100, 1, 0, 0, 0, 2061, 0, 'Apothecary Keever <Royal Apothecary Society>');
