INSERT INTO `creature_text` VALUES (10162, 0, 0, 'Excellent... it would appear as if the meddlesome insects have arrived just in time to feed my legion. Welcome, mortals!', 14, 0, 100, 22, 0, 0, 0, 5635, 0, 'Lord Victor Nefarius (UBRS)');
