INSERT INTO `creature_text` VALUES (10162, 3, 0, 'Inconceivable!', 14, 0, 100, 22, 0, 0, 0, 21235, 0, 'Lord Victor Nefarius (UBRS)');
INSERT INTO `creature_text` VALUES (10162, 4, 0, 'Your efforts will prove fruitless. None shall stand in our way!', 14, 0, 100, 22, 0, 0, 0, 5668, 0, 'Lord Victor Nefarius (UBRS)');
INSERT INTO `creature_text` VALUES (10162, 5, 0, 'Do not force my hand, children! I shall use your hides to line my boots.', 14, 0, 100, 22, 0, 0, 0, 5667, 0, 'Lord Victor Nefarius (UBRS)');
