INSERT INTO `creature_text` VALUES (10162, 9, 0, 'Taste in my power!', 14, 0, 100, 0, 0, 0, 0, 5726, 0, 'Lord Victor Nefarius (UBRS)');
INSERT INTO `creature_text` VALUES (10162, 10, 0, 'Your victory shall be short lived. The days of both the Alliance and Horde are coming to an end! The next time we meet shall be the last.', 14, 0, 100, 5, 0, 0, 0, 5824, 0, 'Lord Victor Nefarius (UBRS)');
INSERT INTO `creature_text` VALUES (10162, 11, 0, 'The mortals have fallen! The new age of the Blackrock legion is at hand!', 14, 0, 100, 22, 0, 0, 0, 5710, 0, 'Lord Victor Nefarius (UBRS)');
INSERT INTO `creature_text` VALUES (10162, 12, 0, 'In this world where time is your enemy, it is my greatest ally. This grand game of life that you think you play in fact plays you. To that I say...', 14, 0, 100, 1, 0, 0, 0, 9907, 0, 'Lord Victor Nefarius SAY_GAMESBEGIN_1 (BWL)');
INSERT INTO `creature_text` VALUES (10162, 13, 0, 'Let the games begin!', 14, 0, 100, 22, 0, 8280, 0, 35709, 0, 'Lord Victor Nefarius SAY_GAMESBEGIN_2 (BWL)');
INSERT INTO `creature_text` VALUES (10162, 14, 0, 'Ah...the heroes. You are persistent, aren\'t you? Your ally here attempted to match his power against mine - and paid the price. Now he shall serve me...by slaughtering you. ', 14, 0, 100, 23, 0, 8279, 0, 9794, 0, 'Lord Victor Nefarius SAY_VAEL_INTRO (BWL)');
