INSERT INTO `creature_text` VALUES (11502, 0, 0, 'Behold Ragnaros - the Firelord! He who was ancient when this world was young! Bow before him, mortals! Bow before your ending!', 14, 0, 100, 0, 0, 8040, 0, 7657, 0, 'ragnaros SAY_SUMMON_MAJ');
INSERT INTO `creature_text` VALUES (11502, 1, 0, 'TOO SOON! YOU HAVE AWAKENED ME TOO SOON, EXECUTUS! WHAT IS THE MEANING OF THIS INTRUSION???', 14, 0, 100, 0, 0, 8043, 0, 7636, 0, 'ragnaros SAY_ARRIVAL1_RAG');
INSERT INTO `creature_text` VALUES (11502, 2, 0, 'These mortal infidels, my lord! They have invaded your sanctum and seek to steal your secrets!', 14, 0, 100, 0, 0, 8041, 0, 7661, 0, 'ragnaros SAY_ARRIVAL2_MAJ');
INSERT INTO `creature_text` VALUES (11502, 3, 0, 'FOOL! YOU ALLOWED THESE INSECTS TO RUN RAMPANT THROUGH THE HALLOWED CORE? AND NOW YOU LEAD THEM TO MY VERY LAIR? YOU HAVE FAILED ME, EXECUTUS! JUSTICE SHALL BE MET, INDEED!', 14, 0, 100, 0, 0, 8044, 0, 7662, 0, 'ragnaros SAY_ARRIVAL3_RAG');
INSERT INTO `creature_text` VALUES (11502, 4, 0, 'NOW FOR YOU, INSECTS! BOLDLY, YOU SOUGHT THE POWER OF RAGNAROS. NOW YOU SHALL SEE IT FIRSTHAND!\n', 14, 0, 100, 0, 0, 8045, 0, 7685, 0, 'ragnaros SAY_ARRIVAL5_RAG');
INSERT INTO `creature_text` VALUES (11502, 5, 0, 'Come forth, my servants! Defend your master!', 14, 0, 100, 0, 0, 8049, 0, 8572, 0, 'ragnaros SAY_REINFORCEMENTS1');
INSERT INTO `creature_text` VALUES (11502, 6, 0, 'YOU CANNOT DEFEAT THE LIVING FLAME! COME YOU MINIONS OF FIRE! COME FORTH, YOU CREATURES OF HATE! YOUR MASTER CALLS!', 14, 0, 100, 0, 0, 8050, 0, 8573, 0, 'ragnaros SAY_REINFORCEMENTS2');
INSERT INTO `creature_text` VALUES (11502, 7, 0, 'By fire be purged!', 14, 0, 100, 0, 0, 8046, 0, 9426, 0, 'ragnaros SAY_HAND');
INSERT INTO `creature_text` VALUES (11502, 8, 0, 'TASTE THE FLAMES OF SULFURON!', 14, 0, 100, 0, 0, 8047, 0, 9427, 0, 'ragnaros SAY_WRATH');
INSERT INTO `creature_text` VALUES (11502, 9, 0, 'Die, insect!', 14, 0, 100, 0, 0, 8051, 0, 7626, 0, 'ragnaros SAY_KILL');
