INSERT INTO `creature_text` VALUES (11582, 0, 0, 'Now this is an example of what not to summon. Look, it\'s frail and brittle. One good whack from a mace will send bone shards flying.', 12, 0, 100, 0, 0, 0, 0, 5950, 0, 'Scholomance Dark Summoner');
INSERT INTO `creature_text` VALUES (11582, 0, 1, 'Hmmm, this one looks like something that would be better off as a windchime. Take notes class... This is NOT what you want to summon in the heat of battle.', 12, 0, 100, 0, 0, 0, 0, 5954, 0, 'Scholomance Dark Summoner');
INSERT INTO `creature_text` VALUES (11582, 0, 2, 'This one is slightly better than the last. However, it still suffers from the same flimsy bone structure as the others. When you summon one of these, you are on the right path.', 12, 0, 100, 0, 0, 0, 0, 5952, 0, 'Scholomance Dark Summoner');
INSERT INTO `creature_text` VALUES (11582, 0, 3, 'Note the weak binding structure of this one. Be sure to finish your incantations or this is what you will end up with.', 12, 0, 100, 0, 0, 0, 0, 5951, 0, 'Scholomance Dark Summoner');
INSERT INTO `creature_text` VALUES (11582, 0, 4, 'Wow, this one is just plain useless. Let me try again.', 12, 0, 100, 0, 0, 0, 0, 5953, 0, 'Scholomance Dark Summoner');
INSERT INTO `creature_text` VALUES (11583, 0, 0, 'Well done, my minions. The mortals\' courage begins to wane! Now, let\'s see how they contend with the true Lord of Blackrock Spire!!!', 14, 0, 100, 0, 0, 8288, 0, 9973, 0, 'nefarian SAY_AGGRO');
