INSERT INTO `creature_text` VALUES (15215, 0, 0, 'Take this enchanted shard, $n. I will be able to hear the dwarves myself through its twin.', 12, 0, 100, 0, 0, 0, 0, 10742, 0, '');
INSERT INTO `creature_text` VALUES (15220, 0, 0, 'What?  Such a small, frail thing beckons me?  This will not go unpunished!', 12, 0, 100, 0, 0, 0, 0, 10803, 0, 'The Duke of Zephyrs - SAY_DUKE_AGGRO');
INSERT INTO `creature_text` VALUES (15233, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Vekniss Guardian');
INSERT INTO `creature_text` VALUES (15252, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Qiraji Champion');
INSERT INTO `creature_text` VALUES (15263, 0, 0, 'Are you so eager to die? I will be happy to accommodate you...', 14, 0, 100, 0, 0, 8615, 0, 11445, 0, 'skeram SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (15263, 0, 1, 'Cower mortals! The age of darkness is at hand.', 14, 0, 100, 0, 0, 8616, 0, 0, 0, 'skeram SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (15263, 0, 2, 'Tremble! The end is upon you.', 14, 0, 100, 0, 0, 8621, 0, 0, 0, 'skeram SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (15263, 1, 0, 'Let your death serve as an example!', 14, 0, 100, 0, 0, 8617, 0, 11446, 0, 'skeram SAY_SLAY1');
INSERT INTO `creature_text` VALUES (15263, 1, 1, 'Spineless wretches! You will drown in rivers of blood!', 14, 0, 100, 0, 0, 8619, 0, 0, 0, 'skeram SAY_SLAY2');
INSERT INTO `creature_text` VALUES (15263, 1, 2, 'The screams of the dying will fill the air. A symphony of terror is about to begin!', 14, 0, 100, 0, 0, 8620, 0, 0, 0, 'skeram SAY_SLAY3');
INSERT INTO `creature_text` VALUES (15263, 2, 0, 'Prepare for the return of the ancient ones!', 14, 0, 100, 0, 0, 8618, 0, 0, 0, 'skeram SAY_SPLIT');
INSERT INTO `creature_text` VALUES (15263, 3, 0, 'You only delay the inevitable!', 14, 0, 100, 0, 0, 8622, 0, 11447, 0, 'skeram SAY_DEATH');
INSERT INTO `creature_text` VALUES (15277, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Anubisath Defender');
INSERT INTO `creature_text` VALUES (15299, 0, 0, '%s begins to slow!', 41, 0, 100, 0, 0, 0, 0, 11343, 0, 'EMOTE_SLOW');
INSERT INTO `creature_text` VALUES (15299, 1, 0, '%s is freezing up!', 41, 0, 100, 0, 0, 0, 0, 11345, 0, 'EMOTE_FREEZE');
INSERT INTO `creature_text` VALUES (15299, 2, 0, '%s is frozen solid!', 41, 0, 100, 0, 0, 0, 0, 11695, 0, 'EMOTE_FROZEN');
INSERT INTO `creature_text` VALUES (15299, 3, 0, '%s begins to crack!', 41, 0, 100, 0, 0, 0, 0, 11346, 0, 'EMOTE_CRACK');
INSERT INTO `creature_text` VALUES (15299, 4, 0, '%s looks ready to shatter!', 41, 0, 100, 0, 0, 0, 0, 11347, 0, 'EMOTE_SHATTER');
INSERT INTO `creature_text` VALUES (15299, 5, 0, '%s explodes!', 41, 0, 100, 0, 0, 0, 0, 28803, 0, 'EMOTE_EXPLODE');
INSERT INTO `creature_text` VALUES (15305, 0, 0, 'Your treachery only speeds your doom.  This world will fall to darkness!', 14, 0, 100, 0, 0, 0, 0, 10805, 0, 'Lord Skwol - YELL_ROYAL_AGGRO');
INSERT INTO `creature_text` VALUES (15307, 0, 0, 'The Abyssal Council does not tolerate deceit!  Feel our wrath, little $r!', 12, 0, 100, 0, 0, 0, 0, 10686, 0, 'Earthen Templar - SAY_TEMPLAR_AGGRO');
INSERT INTO `creature_text` VALUES (15339, 0, 0, 'I am rejuvinated!', 14, 0, 100, 0, 0, 8593, 0, 0, 0, 'SAY_SUPREME_1');
INSERT INTO `creature_text` VALUES (15339, 0, 1, 'My powers are renewed!', 14, 0, 100, 0, 0, 8595, 0, 0, 0, 'SAY_SUPREME_2');
INSERT INTO `creature_text` VALUES (15339, 0, 2, 'My powers return!', 14, 0, 100, 0, 0, 8596, 0, 0, 0, 'SAY_SUPREME_3');
INSERT INTO `creature_text` VALUES (15339, 1, 0, 'Protect the city at all costs!', 14, 0, 100, 0, 0, 8597, 0, 0, 0, 'SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (15339, 1, 1, 'To your posts. Defend the city.', 14, 0, 100, 0, 0, 8600, 0, 0, 0, 'SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (15339, 1, 2, 'Tresspassers will be terminated.', 14, 0, 100, 0, 0, 8601, 0, 0, 0, 'SAY_INTRO_3');
INSERT INTO `creature_text` VALUES (15339, 2, 0, 'Sands of the desert, rise and block out the sun!', 14, 0, 100, 0, 0, 8598, 0, 11449, 0, 'SAY_AGGRO');
INSERT INTO `creature_text` VALUES (15339, 3, 0, 'You are terminated.', 14, 0, 100, 0, 0, 8602, 0, 11450, 0, 'SAY_SLAY');
INSERT INTO `creature_text` VALUES (15339, 4, 0, 'I...have...failed. ', 14, 0, 100, 0, 0, 8594, 0, 11451, 0, 'SAY_DEATH');
INSERT INTO `creature_text` VALUES (15339, 5, 0, 'The walls have been breached!', 14, 0, 100, 0, 0, 8599, 0, 11720, 0, 'SAY_KURINAXX_DEATH');
INSERT INTO `creature_text` VALUES (15340, 0, 0, '%s senses your fear.', 16, 0, 100, 0, 0, 0, 0, 11441, 0, 'moam EMOTE_AGGRO');
INSERT INTO `creature_text` VALUES (15340, 1, 0, '%s bristles with energy!', 16, 0, 100, 0, 0, 0, 0, 12781, 0, 'moan EMOTE_MANA_FULL');
INSERT INTO `creature_text` VALUES (15341, 0, 0, 'The time of our retribution is at hand! Let darkness reign in the hearts of our enemies!', 14, 0, 100, 0, 0, 8612, 0, 11440, 0, 'rajaxx SAY_WAVE3');
INSERT INTO `creature_text` VALUES (15341, 1, 0, 'No longer will we wait behind barred doors and walls of stone! No longer will our vengeance be denied! The dragons themselves will tremble before our wrath!\n', 14, 0, 100, 0, 0, 8610, 0, 11439, 0, 'rajaxx SAY_WAVE4');
INSERT INTO `creature_text` VALUES (15341, 2, 0, 'Fear is for the enemy! Fear and death!', 14, 0, 100, 0, 0, 8608, 0, 11438, 0, 'rajaxx SAY_WAVE5');
INSERT INTO `creature_text` VALUES (15341, 3, 0, 'Staghelm will whimper and beg for his life, just as his whelp of a son did! One thousand years of injustice will end this day!\n', 14, 0, 100, 0, 0, 8611, 0, 11437, 0, 'rajaxx SAY_WAVE6');
INSERT INTO `creature_text` VALUES (15341, 4, 0, 'Fandral! Your time has come! Go and hide in the Emerald Dream and pray we never find you!\n', 14, 0, 100, 0, 0, 8607, 0, 11436, 0, 'rajaxx SAY_WAVE7');
INSERT INTO `creature_text` VALUES (15341, 5, 0, 'Impudent fool! I will kill you myself!', 14, 0, 100, 0, 0, 8609, 0, 11435, 0, 'rajaxx SAY_INTRO');
INSERT INTO `creature_text` VALUES (15341, 6, 0, 'Attack and make them pay dearly!', 14, 0, 100, 0, 0, 8603, 0, 0, 0, 'rajaxx SAY_UNK1');
INSERT INTO `creature_text` VALUES (15341, 7, 0, 'Crush them! Drive them out!', 14, 0, 100, 0, 0, 8605, 0, 0, 0, 'rajaxx SAY_UNK2');
INSERT INTO `creature_text` VALUES (15341, 8, 0, 'Do not hesitate! Destroy them!', 14, 0, 100, 0, 0, 8606, 0, 0, 0, 'rajaxx SAY_UNK3');
INSERT INTO `creature_text` VALUES (15341, 9, 0, 'Warriors, Captains, continue the fight! ', 14, 0, 100, 0, 0, 8613, 0, 11434, 0, 'rajaxx SAY_UNK4');
INSERT INTO `creature_text` VALUES (15341, 10, 0, 'You are not worth my time, $n!', 14, 0, 100, 0, 0, 8614, 0, 11067, 0, 'rajaxx SAY_DEAGGRO');
INSERT INTO `creature_text` VALUES (15341, 11, 0, 'Breathe your last!', 14, 0, 100, 0, 0, 8604, 0, 11433, 0, 'rajaxx SAY_KILLS_ANDOROV');
INSERT INTO `creature_text` VALUES (15341, 12, 0, 'Soon you will know the price of your meddling, mortals... The master is nearly whole... And when he rises, your world will cease.', 14, 0, 100, 0, 0, 0, 0, 11429, 0, 'rajaxx SAY_COMPLETE_QUEST');
INSERT INTO `creature_text` VALUES (15362, 0, 0, 'The walls of the chamber tremble. Something is happening...', 16, 0, 100, 0, 0, 0, 0, 11191, 0, 'Malfurion Stormrage');
INSERT INTO `creature_text` VALUES (15362, 1, 0, 'Be steadfast, champion. I know why it is that you are here and I know what it is that you seek. Eranikus will not give up the shard freely. He has been twisted... twisted by the same force that you seek to destroy.', 12, 0, 100, 0, 0, 0, 0, 11193, 0, 'Malfurion Stormrage');
INSERT INTO `creature_text` VALUES (15362, 2, 0, 'Are you really surprised? Is it hard to believe that the power of an Old God could reach even inside the Dream? It is true - Eranikus, Tyrant of the Dream, wages a battle against us all. The Nightmare follows in his wake of destruction.', 12, 0, 100, 0, 0, 0, 0, 11194, 0, 'Malfurion Stormrage');
INSERT INTO `creature_text` VALUES (15362, 3, 0, 'Understand this, Eranikus wants nothing more than to be brought to Azeroth from the Dream. Once he is out, he will stop at nothing to destroy my physical manifestation. This, however, is the only way in which you could recover the scepter shard.', 12, 0, 100, 0, 0, 0, 0, 11195, 0, 'Malfurion Stormrage');
INSERT INTO `creature_text` VALUES (15362, 4, 0, 'You will bring him back into this world, champion.', 12, 0, 100, 0, 0, 0, 0, 11196, 0, 'Malfurion Stormrage');
INSERT INTO `creature_text` VALUES (15367, 0, 0, 'Take heart! Your friends will not long mourn your passing! ', 12, 0, 100, 0, 0, 8506, 0, 10986, 0, 'Felendren the Banished to Player');
INSERT INTO `creature_text` VALUES (15369, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Ayamiss frenzy');
INSERT INTO `creature_text` VALUES (15370, 0, 0, '%s sets eyes on $n!', 16, 0, 100, 0, 0, 0, 0, 11074, 0, 'buru EMOTE_TARGET');
INSERT INTO `creature_text` VALUES (15371, 0, 0, 'Move along, $C!', 12, 0, 100, 66, 0, 0, 0, 10992, 0, 'Sunstrider Guardian');
INSERT INTO `creature_text` VALUES (15371, 0, 1, 'Off with you, $n!', 12, 0, 100, 66, 0, 0, 0, 10993, 0, 'Sunstrider Guardian');
INSERT INTO `creature_text` VALUES (15371, 1, 0, 'Do not push it citizen!', 12, 0, 100, 1, 0, 0, 0, 10994, 0, 'Sunstrider Guardian');
INSERT INTO `creature_text` VALUES (15378, 0, 0, 'There is a way...', 12, 0, 100, 0, 0, 0, 0, 10911, 0, 'anachronos the ancient MERITHRA_SAY_1');
INSERT INTO `creature_text` VALUES (15378, 1, 0, 'We will push them back, Anachronos. This I vow. Uphold your end of this task. Let not your hands falter as you seal our fates behind the barrier.', 12, 0, 100, 0, 0, 0, 0, 10908, 0, 'anachronos the ancient MERITHRA_SAY_2');
INSERT INTO `creature_text` VALUES (15378, 2, 0, 'Succumb to the endless dream, little ones. Let it consume you!', 14, 0, 100, 0, 0, 0, 0, 10903, 0, 'anachronos the ancient MERITHRA_YELL_1');
INSERT INTO `creature_text` VALUES (15378, 3, 0, '%s glances at her compatriots.', 16, 0, 100, 0, 0, 0, 0, 10912, 0, 'anachronos the ancient MERITHRA_EMOTE_1');
INSERT INTO `creature_text` VALUES (15379, 0, 0, 'Aye, Fandral, remember these words: Let not your grief guide your faith. These thoughts you hold... dark places you go, night elf. Absolution cannot be had through misguided vengeance.', 12, 0, 100, 0, 0, 0, 0, 10914, 0, 'anachronos the ancient CAELESTRASZ_SAY_1');
INSERT INTO `creature_text` VALUES (15379, 1, 0, 'Do not forget the sacrifices made on this day, night elf. We have all suffered immensely at the hands of these beasts.', 12, 0, 100, 0, 0, 0, 0, 10907, 0, 'anachronos the ancient CAELESTRASZ_SAY_2');
INSERT INTO `creature_text` VALUES (15379, 2, 0, 'Alexstrasza grant me the resolve to drive our enemies back!', 14, 0, 100, 0, 0, 0, 0, 10902, 0, 'anachronos the ancient CAELESTRASZ_YELL_1');
INSERT INTO `creature_text` VALUES (15380, 0, 0, 'Anachronos, this diversion will give you and the young druid time enough to seal the gate. Do not falter. Now, let us see how they deal with chaotic magic.', 12, 0, 100, 0, 0, 0, 0, 10904, 0, 'anachronos the ancient ARYGOS_SAY_1');
INSERT INTO `creature_text` VALUES (15380, 1, 0, 'Let them feel the wrath of the Blue Flight! May Malygos protect me!', 14, 0, 100, 0, 0, 0, 0, 10901, 0, 'anachronos the ancient ARYGOS_YELL_1');
INSERT INTO `creature_text` VALUES (15380, 2, 0, '%s nods knowingly.', 16, 0, 100, 0, 0, 0, 0, 10913, 0, 'anachronos the ancient ARYGOS_EMOTE_1');
INSERT INTO `creature_text` VALUES (15381, 0, 0, 'We must act quickly or all shall be lost!', 12, 0, 100, 0, 0, 0, 0, 10909, 0, 'anachronos the ancient ANACHRONOS_SAY_1');
INSERT INTO `creature_text` VALUES (15381, 1, 0, 'NOW, STAGHELM! WE GO NOW! Prepare your magic!', 12, 0, 100, 0, 0, 0, 0, 10915, 0, 'anachronos the ancient ANACHRONOS_SAY_2');
INSERT INTO `creature_text` VALUES (15381, 2, 0, 'Stay close...', 12, 0, 100, 0, 0, 0, 0, 10917, 0, 'anachronos the ancient ANACHRONOS_SAY_3');
INSERT INTO `creature_text` VALUES (15381, 3, 0, 'The sands of time will halt, but only for a moment! I will now conjure the barrier.', 12, 0, 100, 0, 0, 0, 0, 10930, 0, 'anachronos the ancient ANACHRONOS_SAY_4');
INSERT INTO `creature_text` VALUES (15381, 4, 0, 'FINISH THE SPELL, STAGHELM! I CANNOT HOLD THE GLYPHS OF WARDING IN PLACE MUCH LONGER! CALL FORTH THE ROOTS!', 12, 0, 100, 0, 0, 0, 0, 10920, 0, 'anachronos the ancient ANACHRONOS_SAY_5');
INSERT INTO `creature_text` VALUES (15381, 5, 0, 'It... It is over, Lord Staghelm. We are victorious. Albeit the cost for this victory was great.', 12, 0, 100, 0, 0, 0, 0, 10923, 0, 'anachronos the ancient ANACHRONOS_SAY_6');
INSERT INTO `creature_text` VALUES (15381, 6, 0, 'There is but one duty that remains...', 12, 0, 100, 0, 0, 0, 0, 10924, 0, 'anachronos the ancient ANACHRONOS_SAY_7');
INSERT INTO `creature_text` VALUES (15381, 7, 0, 'Before I leave this place, I make one final offering to you, Lord Staghelm. Should a time arise in which you must gain entry to this accursed fortress, use the Scepter of the Shifting Sands on the sacred gong. The magic holding the barrier together will dissipate and the horrors of Ahn\'Qiraj will be unleashed upon the world once more.', 12, 0, 100, 0, 0, 0, 0, 10925, 0, 'anachronos the ancient ANACHRONOS_SAY_8');
INSERT INTO `creature_text` VALUES (15381, 8, 0, 'Lord Staghelm, where are you going? You would shatter our bond for the sake of pride?\n', 12, 0, 100, 0, 0, 0, 0, 10931, 0, 'anachronos the ancient ANACHRONOS_SAY_9');
INSERT INTO `creature_text` VALUES (15381, 9, 0, 'And now you know all that there is to know, mortal... ', 12, 0, 100, 0, 0, 0, 0, 10935, 0, 'anachronos the ancient ANACHRONOS_SAY_10');
INSERT INTO `creature_text` VALUES (15381, 10, 0, '%s hands the Scepter of the Shifting Sands to Fandral Staghelm.', 16, 0, 100, 0, 0, 0, 0, 10926, 0, 'anachronos the ancient ANACHRONOS_EMOTE_1');
INSERT INTO `creature_text` VALUES (15381, 11, 0, '%s shakes his head in disappointment.', 16, 0, 100, 0, 0, 0, 0, 10933, 0, 'anachronos the ancient ANACHRONOS_EMOTE_2');
INSERT INTO `creature_text` VALUES (15381, 12, 0, '%s kneels down to pick up the fragments of the shattered scepter.', 16, 0, 100, 0, 0, 0, 0, 10934, 0, 'anachronos the ancient ANACHRONOS_EMOTE_3');
INSERT INTO `creature_text` VALUES (15382, 0, 0, 'My forces cannot overcome the Qiraji defenses. We will not be able to get close enough to place your precious barrier, dragon.', 12, 0, 100, 0, 0, 0, 0, 10910, 0, 'anachronos the ancient FANDRAL_SAY_1');
INSERT INTO `creature_text` VALUES (15382, 1, 0, 'It is done, dragon. Lead the way.', 12, 0, 100, 0, 0, 0, 0, 10916, 0, 'anachronos the ancient FANDRAL_SAY_2');
INSERT INTO `creature_text` VALUES (15382, 2, 0, 'Ancient ones guide my hand... Wake from your slumber! WAKE AND SEAL THIS CURSED PLACE!', 12, 0, 100, 0, 0, 0, 0, 10921, 0, 'anachronos the ancient FANDRAL_SAY_3');
INSERT INTO `creature_text` VALUES (15382, 3, 0, 'After the savagery that my people have witnessed and felt, you expect me to accept another burden, dragon? Surely you are mad.', 12, 0, 100, 0, 0, 0, 0, 10927, 0, 'anachronos the ancient FANDRAL_SAY_4');
INSERT INTO `creature_text` VALUES (15382, 4, 0, 'I want nothing to do with Silithus, the Qiraji and least of all, any damned dragons!', 12, 0, 100, 0, 0, 0, 0, 10928, 0, 'anachronos the ancient FANDRAL_SAY_5');
INSERT INTO `creature_text` VALUES (15382, 5, 0, 'My son\'s soul will find no comfort in this hollow victory, dragon. I will have him back. Though it takes a millennia, I WILL have my son back!  ', 12, 0, 100, 0, 0, 0, 0, 10932, 0, 'anachronos the ancient FANDRAL_SAY_6');
INSERT INTO `creature_text` VALUES (15382, 6, 0, '%s falls to one knee - exhausted.', 16, 0, 100, 0, 0, 0, 0, 10922, 0, 'anachronos the ancient FANDRAL_EMOTE_1');
INSERT INTO `creature_text` VALUES (15382, 7, 0, '%s hurls the Scepter of the Shifting Sands into the barrier, shattering it.', 16, 0, 100, 0, 0, 0, 0, 10929, 0, 'anachronos the ancient FANDRAL_EMOTE_2');
INSERT INTO `creature_text` VALUES (15384, 0, 0, 'A Guardian of Icecrown enters the fight!', 41, 0, 100, 0, 0, 0, 0, 32804, 0, 'OLDWorld Trigger (DO NOT DELETE)');
INSERT INTO `creature_text` VALUES (15387, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Qiraji Warrior');
INSERT INTO `creature_text` VALUES (15402, 0, 0, 'Such... dark... energy!  How can this be possible?', 12, 0, 100, 0, 0, 0, 0, 13397, 0, 'Apprentice Mirveda');
INSERT INTO `creature_text` VALUES (15405, 0, 0, 'It\'s been a pleasure, Mr. Anvilward.  I must say your enthusiastic interest in our operations is most... unexpected.', 12, 0, 100, 0, 0, 0, 0, 11718, 0, 'Ley-Keeper Caidanis');
INSERT INTO `creature_text` VALUES (15416, 0, 0, 'Slay them!  Don\'t let the Scourge scum defile our city!', 14, 0, 100, 0, 0, 0, 0, 12861, 0, 'Ranger Jaela to Rotlimb Cannibal');
INSERT INTO `creature_text` VALUES (15420, 0, 0, 'Very well.  Let\'s see what you have to show me, $n.', 12, 0, 100, 0, 0, 0, 0, 11734, 0, 'Prospector Anvilward');
INSERT INTO `creature_text` VALUES (15420, 1, 0, 'What manner of trick is this, $r?  If you seek to ambush me, I warn you I will not go down quietly!  ', 12, 0, 100, 0, 0, 0, 0, 11735, 0, 'Prospector Anvilward');
INSERT INTO `creature_text` VALUES (15420, 2, 0, 'These arcane sanctums of yours are quite... interesting Mr. Caidanis.  I appreciate the information you have given me today.', 12, 0, 100, 1, 0, 0, 0, 11717, 0, 'Prospector Anvilward');
INSERT INTO `creature_text` VALUES (15471, 0, 0, 'They come now. Try not to get yourself killed, young blood.', 14, 0, 100, 0, 0, 0, 0, 11012, 0, 'andorov SAY_ANDOROV_INTRO');
INSERT INTO `creature_text` VALUES (15471, 1, 0, 'Remember, Rajaxx, when I said I\'d kill you last?', 14, 0, 100, 0, 0, 0, 0, 11028, 0, 'andorov SAY_ANDOROV_ATTACK');
INSERT INTO `creature_text` VALUES (15481, 0, 0, 'I SAID GOOD BYE!', 12, 0, 100, 1, 0, 0, 0, 0, 0, 'Spirit of Azuregos');
INSERT INTO `creature_text` VALUES (15509, 0, 0, '%s goes into a killing frenzy!', 16, 0, 100, 0, 0, 0, 0, 24143, 0, 'huhuran EMOTE_FRENZY_KILL');
INSERT INTO `creature_text` VALUES (15509, 1, 0, '%s goes into a berserker rage!', 16, 0, 100, 0, 0, 0, 0, 34057, 0, 'huhuran EMOTE_BERSERK');
INSERT INTO `creature_text` VALUES (15516, 0, 0, 'You will be judged for defiling these sacred grounds!  The laws of the Ancients will not be challenged!  Trespassers will be annihilated!\n', 14, 0, 100, 0, 0, 8646, 0, 11442, 0, 'sartura SAY_AGGRO');
INSERT INTO `creature_text` VALUES (15516, 1, 0, 'I sentence you to death!', 14, 0, 100, 0, 0, 8647, 0, 11443, 0, 'sartura SAY_SLAY');
INSERT INTO `creature_text` VALUES (15516, 2, 0, 'I serve to the last!', 14, 0, 100, 0, 0, 8648, 0, 11444, 0, 'sartura SAY_DEATH');
INSERT INTO `creature_text` VALUES (15537, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Anubisath Warrior');
INSERT INTO `creature_text` VALUES (15550, 0, 0, 'It was... inevitable.', 14, 0, 100, 0, 0, 9169, 0, 13460, 0, 'attumen SAY_KILL1');
INSERT INTO `creature_text` VALUES (15550, 0, 1, 'Another trophy to add to my collection!', 14, 0, 100, 0, 0, 9300, 0, 15333, 0, 'attumen SAY_KILL2');
INSERT INTO `creature_text` VALUES (15550, 1, 0, 'Such easy sport.', 14, 0, 100, 0, 0, 9170, 0, 0, 0, 'attumen SAY_RANDOM1');
INSERT INTO `creature_text` VALUES (15550, 1, 1, 'Amateurs! Do not think you can best me! I kill for a living.', 14, 0, 100, 0, 0, 9304, 0, 0, 0, 'attumen SAY_RANDOM2');
INSERT INTO `creature_text` VALUES (15550, 2, 0, 'Weapons are merely a convenience for a warrior of my skill!', 14, 0, 100, 0, 0, 9166, 0, 13490, 0, 'attumen SAY_DISARMED');
INSERT INTO `creature_text` VALUES (15550, 3, 0, 'Well done, Midnight!', 14, 0, 100, 0, 0, 9173, 0, 15334, 0, 'attumen SAY_MIDNIGHT_KILL');
INSERT INTO `creature_text` VALUES (15550, 4, 0, 'Cowards! Wretches!', 14, 0, 100, 0, 0, 9167, 0, 13459, 0, 'attumen SAY_APPEAR1');
INSERT INTO `creature_text` VALUES (15550, 4, 1, 'Who dares attack the steed of the Huntsman?', 14, 0, 100, 0, 0, 9298, 0, 15378, 0, 'attumen SAY_APPEAR2');
