INSERT INTO `creature_text` VALUES (15954, 7, 0, '%s teleports back into the battle!', 41, 0, 100, 0, 0, 0, 0, 32976, 3, 'Noth EMOTE_TELEPORT_2');
INSERT INTO `creature_text` VALUES (15956, 0, 0, 'Just a little taste...', 14, 0, 100, 0, 0, 8785, 0, 13000, 0, 'anubrekhan SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (15956, 0, 1, 'There is no way out.', 14, 0, 100, 0, 0, 8786, 0, 13002, 0, 'anubrekhan SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (15956, 0, 2, 'Yes, run! It makes the blood pump faster!', 14, 0, 100, 0, 0, 8787, 0, 13003, 0, 'anubrekhan SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (15956, 1, 0, 'Ahh... welcome to my parlor.', 14, 0, 100, 0, 0, 8788, 0, 13004, 0, 'anubrekhan SAY_GREET1');
INSERT INTO `creature_text` VALUES (15956, 1, 1, 'I hear little hearts beating. Yes... beating faster now... soon the beating will stop.', 14, 0, 100, 0, 0, 8790, 0, 13006, 0, 'anubrekhan SAY_GREET2');
INSERT INTO `creature_text` VALUES (15956, 1, 2, 'Where to go? What to do? So many choices that all end in pain, end in death.', 14, 0, 100, 0, 0, 8791, 0, 13007, 0, 'anubrekhan SAY_GREET3');
INSERT INTO `creature_text` VALUES (15956, 1, 3, 'Which one shall I eat first? So difficult to choose. They all smell so delicious...', 14, 0, 100, 0, 0, 8792, 0, 13008, 0, 'anubrekhan SAY_GREET4');
INSERT INTO `creature_text` VALUES (15956, 1, 4, 'Closer now. Tasty morsels. I\'ve been too long without food, without blood to drink.', 14, 0, 100, 0, 0, 8793, 0, 13009, 0, 'anubrekhan SAY_GREET5');
INSERT INTO `creature_text` VALUES (15956, 2, 0, 'Shhh... it will all be over soon.', 14, 0, 100, 0, 0, 8789, 0, 13005, 0, 'anubrekhan SAY_SLAY');
INSERT INTO `creature_text` VALUES (15956, 3, 0, 'Anub\'Rekhan begins to unleash an insect swarm!', 41, 0, 100, 0, 0, 0, 0, 13443, 3, 'Anub\'Rekhan EMOTE_LOCUST');
INSERT INTO `creature_text` VALUES (15978, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Crypt Reaver');
INSERT INTO `creature_text` VALUES (15989, 0, 0, 'Sapphiron lifts off into the air!', 41, 0, 100, 0, 0, 0, 0, 32801, 3, 'Sapphiron');
INSERT INTO `creature_text` VALUES (15989, 1, 0, 'Sapphiron resumes his attacks!', 41, 0, 100, 0, 0, 0, 0, 32802, 3, 'Sapphiron');
INSERT INTO `creature_text` VALUES (15989, 2, 0, '%s takes a deep breath.', 41, 0, 100, 0, 0, 0, 0, 24548, 3, 'Sapphiron');
INSERT INTO `creature_text` VALUES (15989, 3, 0, '%s enrages!', 41, 0, 100, 0, 0, 0, 0, 0, 3, 'Sapphiron');
INSERT INTO `creature_text` VALUES (15990, 0, 0, 'Your forces are nearly marshaled to strike back against your enemies, my liege.', 14, 0, 100, 0, 0, 14467, 0, 12990, 3, 'kelthuzad SAY_SAPP_DIALOG1');
INSERT INTO `creature_text` VALUES (15990, 1, 0, 'It is good that you serve me so faithfully. Soon, all will serve the Lich King and in the end, you shall be rewarded...so long as you do not falter.', 14, 0, 100, 0, 0, 8881, 0, 0, 0, 'kelthuzad SAY_SAPP_DIALOG2_LICH');
INSERT INTO `creature_text` VALUES (15990, 2, 0, 'Yes, master. The time of their ultimate demise draws close.... What is this?', 14, 0, 100, 0, 0, 14468, 0, 12991, 3, 'kelthuzad SAY_SAPP_DIALOG3');
INSERT INTO `creature_text` VALUES (15990, 3, 0, 'Your security measures have failed! See to this interruption immediately!', 14, 0, 100, 0, 0, 8882, 0, 0, 0, 'kelthuzad SAY_SAPP_DIALOG4_LICH');
INSERT INTO `creature_text` VALUES (15990, 4, 0, 'As you command, master!', 14, 0, 100, 0, 0, 14469, 0, 12992, 3, 'kelthuzad SAY_SAPP_DIALOG5');
INSERT INTO `creature_text` VALUES (15990, 5, 0, 'No!!! A curse upon you, interlopers! The armies of the Lich King will hunt you down. You will not escape your fate...', 14, 0, 100, 0, 0, 14484, 0, 13150, 3, 'kelthuzad SAY_CAT_DIED');
INSERT INTO `creature_text` VALUES (15990, 7, 0, 'Pray for mercy!', 14, 0, 100, 0, 0, 14475, 0, 12995, 0, 'kelthuzad SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (15990, 7, 1, 'Scream your dying breath!', 14, 0, 100, 0, 0, 14476, 0, 12996, 0, 'kelthuzad SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (15990, 7, 2, 'The end is upon you!', 14, 0, 100, 0, 0, 14477, 0, 12997, 0, 'kelthuzad SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (15990, 8, 0, 'The dark void awaits you!', 14, 0, 100, 0, 0, 14478, 0, 13021, 0, 'kelthuzad SAY_SLAY1');
INSERT INTO `creature_text` VALUES (15990, 8, 1, '%s cackles maniacally.', 16, 0, 100, 0, 0, 14479, 0, 29805, 0, 'kelthuzad SAY_SLAY2');
INSERT INTO `creature_text` VALUES (15990, 9, 0, 'Agghhhh! Do... not... rejoice! Your victory is a hollow one, for I shall return with powers beyond your imagining!', 14, 0, 100, 0, 0, 14480, 0, 13019, 0, 'kelthuzad SAY_DEATH');
INSERT INTO `creature_text` VALUES (15990, 10, 0, 'Your soul is bound to me, now!', 14, 0, 100, 0, 0, 14472, 0, 13017, 0, 'kelthuzad SAY_CHAIN1');
INSERT INTO `creature_text` VALUES (15990, 10, 1, 'There will be no escape!', 14, 0, 100, 0, 0, 14473, 0, 13018, 0, 'kelthuzad SAY_CHAIN2');
INSERT INTO `creature_text` VALUES (15990, 11, 0, 'I will freeze the blood in your veins!', 14, 0, 100, 0, 0, 14474, 0, 13020, 0, 'kelthuzad SAY_FROST_BLAST');
INSERT INTO `creature_text` VALUES (15990, 12, 0, 'Master, I require aid!', 14, 0, 100, 0, 0, 14470, 0, 12998, 0, 'kelthuzad SAY_REQUEST_AID');
INSERT INTO `creature_text` VALUES (15990, 13, 0, 'Kel\'Thuzad strikes!', 41, 0, 100, 0, 0, 0, 0, 32803, 0, 'Kel\'Thuzad EMOTE_PHASE_TWO');
INSERT INTO `creature_text` VALUES (15990, 14, 0, 'Minions, servants, soldiers of the cold dark! Obey the call of Kel\'Thuzad!', 14, 0, 100, 0, 0, 14471, 0, 12999, 0, 'kelthuzad SAY_SUMMON_MINIONS');
INSERT INTO `creature_text` VALUES (15990, 15, 0, 'Your petty magics are no challenge to the might of the Scourge!', 14, 0, 100, 0, 0, 14481, 0, 13492, 0, 'kelthuzad SAY_SPECIAL1_MANA_DET');
INSERT INTO `creature_text` VALUES (15990, 15, 1, 'Enough! I grow tired of these distractions! ', 14, 0, 100, 0, 0, 14483, 0, 0, 0, 'kelthuzad SAY_SPECIAL3_MANA_DET');
INSERT INTO `creature_text` VALUES (15990, 15, 2, 'Fools, you have spread your powers too thin. Be free, my minions!', 14, 0, 100, 0, 0, 14482, 0, 0, 0, 'kelthuzad SAY_SPECIAL2_DISPELL');
INSERT INTO `creature_text` VALUES (15990, 16, 0, 'Who dares violate the sanctity of my domain? Be warned, all who trespass here are doomed.', 14, 0, 100, 0, 0, 14463, 0, 0, 3, 'kelthuzad SAY_TAUNT1');
INSERT INTO `creature_text` VALUES (15990, 17, 0, 'Fools! You think yourselves triumphant? You have only taken one step closer to the abyss!', 14, 0, 100, 0, 0, 14464, 0, 12985, 3, 'kelthuzad SAY_TAUNT2');
INSERT INTO `creature_text` VALUES (15990, 18, 0, 'I grow tired of these games. Proceed, and I will banish your souls to oblivion!', 14, 0, 100, 0, 0, 14465, 0, 12986, 3, 'kelthuzad SAY_TAUNT3');
INSERT INTO `creature_text` VALUES (15990, 19, 0, 'You have no idea what horrors lie ahead. You have seen nothing! The frozen heart of Naxxramas awaits you!', 14, 0, 100, 0, 0, 14466, 0, 27382, 3, 'kelthuzad SAY_TAUNT4');
INSERT INTO `creature_text` VALUES (15990, 20, 0, 'Come, heroes... By the will of the Lich King, you shall be destroyed.', 14, 0, 100, 0, 0, 0, 0, 12993, 3, 'Kel\'Thuzad SAY_DIALOGUE_SAPPHIRON_KELTHUZAD4');
INSERT INTO `creature_text` VALUES (16011, 0, 0, 'An aura of necrotic energy blocks all healing!', 41, 0, 100, 0, 0, 0, 0, 32334, 0, 'Loatheb');
INSERT INTO `creature_text` VALUES (16011, 1, 0, 'The aura fades away, allowing healing once more!', 41, 0, 100, 0, 0, 0, 0, 32335, 0, 'Loatheb');
INSERT INTO `creature_text` VALUES (16011, 2, 0, 'The aura\'s power begins to wane!', 41, 0, 100, 0, 0, 0, 0, 32805, 0, 'Loatheb');
INSERT INTO `creature_text` VALUES (16028, 0, 0, 'Patchwerk want to play!', 14, 0, 100, 0, 0, 8909, 0, 13068, 0, 'patchwerk SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (16028, 0, 1, 'Kel\'thuzad make Patchwerk his avatar of war!', 14, 0, 100, 0, 0, 8910, 0, 13069, 0, 'patchwerk SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (16028, 1, 0, 'No more play?', 14, 0, 100, 0, 0, 8912, 0, 28241, 0, 'patchwerk SAY_SLAY');
INSERT INTO `creature_text` VALUES (16028, 2, 0, 'What... happen to-', 14, 0, 100, 0, 0, 8911, 0, 13070, 0, 'patchwerk SAY_DEATH');
INSERT INTO `creature_text` VALUES (16028, 3, 0, '%s goes into a berserker rage!', 16, 0, 100, 0, 0, 0, 0, 4428, 0, 'patchwerk EMOTE_BERSERK');
INSERT INTO `creature_text` VALUES (16028, 4, 0, '%s goes into a frenzy!', 41, 0, 100, 0, 0, 0, 0, 38630, 0, 'patchwerk EMOTE_ENRAGE');
INSERT INTO `creature_text` VALUES (16042, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Lord Valthalak - Cast Frenzy');
INSERT INTO `creature_text` VALUES (16059, 0, 0, 'I\'m going to kick yer ugly head in!', 14, 0, 100, 0, 0, 0, 0, 11953, 0, '');
INSERT INTO `creature_text` VALUES (16059, 0, 1, 'You want a fight? A fight you\'ll get!', 14, 0, 100, 0, 0, 0, 0, 11952, 0, '');
INSERT INTO `creature_text` VALUES (16059, 0, 2, 'I crush weaklings like you every day! Bring it on!', 14, 0, 100, 0, 0, 0, 0, 11954, 0, '');
INSERT INTO `creature_text` VALUES (16060, 0, 0, 'Foolishly you have sought your own demise.', 14, 0, 100, 0, 0, 8807, 0, 13030, 3, 'Gothik SAY_INTRO_1');
INSERT INTO `creature_text` VALUES (16060, 1, 0, 'Brazenly you have disregarded powers beyond your understanding.', 14, 0, 100, 0, 0, 0, 0, 13031, 3, 'Gothik SAY_INTRO_2');
INSERT INTO `creature_text` VALUES (16060, 2, 0, 'You have fought hard to invade the realm of the harvester.', 14, 0, 100, 0, 0, 0, 0, 13032, 3, 'Gothik SAY_INTRO_3');
INSERT INTO `creature_text` VALUES (16060, 3, 0, 'Now there is only one way out- to walk the lonely path of the damned.', 14, 0, 100, 0, 0, 0, 0, 13033, 3, 'Gothik SAY_INTRO_4');
INSERT INTO `creature_text` VALUES (16060, 4, 0, 'I have waited long enough. Now you face the harvester of souls.', 14, 0, 100, 0, 0, 8808, 0, 13028, 3, 'Gothik SAY_PHASE_TWO');
INSERT INTO `creature_text` VALUES (16060, 5, 0, 'I... am... undone.', 14, 0, 100, 0, 0, 8805, 0, 13026, 3, 'Gothik SAY_DEATH');
INSERT INTO `creature_text` VALUES (16060, 6, 0, 'Death is the only escape!', 14, 0, 20, 0, 0, 8806, 0, 13027, 3, 'Gothik SAY_KILL');
INSERT INTO `creature_text` VALUES (16060, 7, 0, '%s teleports into the fray!', 41, 0, 100, 0, 0, 0, 0, 32306, 3, 'Gothik EMOTE_PHASE_TWO');
INSERT INTO `creature_text` VALUES (16060, 8, 0, 'The central gate opens!', 41, 0, 100, 0, 0, 0, 0, 32307, 3, 'Gothik EMOTE_GATE_OPENED');
INSERT INTO `creature_text` VALUES (16061, 0, 0, 'Do as I taught you!', 14, 0, 100, 0, 0, 8855, 0, 13075, 3, 'Razuvious SAY_AGGRO #1');
INSERT INTO `creature_text` VALUES (16061, 0, 1, 'The time for practice is over! Show me what you have learned!', 14, 0, 100, 0, 0, 8859, 0, 13078, 3, 'Razuvious SAY_AGGRO #2');
INSERT INTO `creature_text` VALUES (16061, 0, 2, 'Show them no mercy!', 14, 0, 100, 0, 0, 8856, 0, 13076, 3, 'Razuvious SAY_AGGRO #3');
INSERT INTO `creature_text` VALUES (16061, 1, 0, '%s lets loose a triumphant shout.', 41, 0, 30, 0, 0, 0, 0, 13082, 3, 'Razuvious SAY_SLAY');
INSERT INTO `creature_text` VALUES (16061, 2, 0, 'You should have stayed home.', 14, 0, 50, 0, 0, 8861, 0, 13081, 3, 'Razuvious SAY_TAUNTED #1');
INSERT INTO `creature_text` VALUES (16061, 2, 1, 'You disappoint me, students!', 14, 0, 50, 0, 0, 8858, 0, 13077, 3, 'Razuvious SAY_TAUNTED #2');
INSERT INTO `creature_text` VALUES (16061, 2, 2, 'I\'m just getting warmed up!', 14, 0, 50, 0, 0, 8852, 0, 13072, 3, 'Razuvious SAY_TAUNTED #3');
INSERT INTO `creature_text` VALUES (16061, 2, 3, 'Stand and fight!', 14, 0, 50, 0, 0, 8853, 0, 13073, 3, 'Razuvious SAY_TAUNTED #4');
INSERT INTO `creature_text` VALUES (16061, 2, 4, 'Sweep the leg... Do you have a problem with that?', 14, 0, 50, 0, 0, 8861, 0, 13080, 3, 'Razuvious SAY_TAUNTED #5');
INSERT INTO `creature_text` VALUES (16061, 3, 0, 'An honorable... death.', 14, 0, 100, 0, 0, 8860, 0, 13079, 3, 'Razuvious SAY_DEATH');
INSERT INTO `creature_text` VALUES (16063, 0, 0, 'Flee, before it\'s too late!', 14, 0, 100, 0, 0, 8913, 0, 13097, 0, 'zeliek SAY_ZELI_AGGRO');
INSERT INTO `creature_text` VALUES (16063, 1, 2, 'Do not continue! Turn back while there\'s still time!', 14, 0, 100, 0, 0, 8919, 0, 13103, 0, 'zeliek SAY_ZELI_TAUNT3');
INSERT INTO `creature_text` VALUES (16063, 2, 0, 'I have no choice but to obey!', 14, 0, 100, 0, 0, 8916, 0, 13100, 0, 'zeliek SAY_ZELI_SPECIAL');
INSERT INTO `creature_text` VALUES (16063, 3, 0, 'Forgive me!', 14, 0, 30, 0, 0, 8915, 0, 13099, 0, 'zeliek SAY_ZELI_SLAY');
INSERT INTO `creature_text` VALUES (16063, 4, 0, 'It is... as it should be. ', 14, 0, 100, 0, 0, 8914, 0, 13098, 0, 'zeliek SAY_ZELI_DEATH');
INSERT INTO `creature_text` VALUES (16063, 5, 0, 'Invaders, cease this foolish venture at once! Turn away while you still can!', 14, 0, 100, 0, 0, 8917, 0, 13101, 3, 'zeliek SAY_ZELI_TAUNT1');
INSERT INTO `creature_text` VALUES (16063, 6, 0, 'Perhaps they will come to their senses... and run away as fast as they can.', 14, 0, 100, 0, 0, 8918, 0, 13102, 3, 'zeliek SAY_ZELI_TAUNT2');
INSERT INTO `creature_text` VALUES (16063, 7, 0, '%s casts Condemnation on everyone!', 41, 0, 100, 0, 0, 0, 0, 33088, 3, 'zeliek EMOTE_RAGECAST');
INSERT INTO `creature_text` VALUES (16064, 0, 0, 'C\'mon an\' fight ye wee ninny!', 14, 0, 100, 0, 0, 8899, 0, 13034, 0, 'korthazz SAY_KORT_AGGRO');
INSERT INTO `creature_text` VALUES (16064, 1, 2, 'I\'m gonna\' enjoy killin\' these slack-jawed daffodils!', 14, 0, 100, 0, 0, 8905, 0, 13040, 0, 'korthazz SAY_KORT_TAUNT3');
INSERT INTO `creature_text` VALUES (16064, 2, 0, 'I like my meat extra crispy!', 14, 0, 100, 0, 0, 8902, 0, 13037, 0, 'korthazz SAY_KORT_SPECIAl');
INSERT INTO `creature_text` VALUES (16064, 3, 0, 'Next time, bring more friends!', 14, 0, 30, 0, 0, 8901, 0, 13036, 0, 'korthazz SAY_KORT_SLAY');
INSERT INTO `creature_text` VALUES (16064, 4, 0, 'What a bloody waste this is!', 14, 0, 100, 0, 0, 8900, 0, 13035, 0, 'korthazz SAY_KORT_DEATH');
INSERT INTO `creature_text` VALUES (16064, 5, 0, 'To arms, ye roustabouts! We\'ve got company!', 14, 0, 100, 0, 0, 8903, 0, 13038, 3, 'korthazz SAY_KORT_TAUNT1');
INSERT INTO `creature_text` VALUES (16064, 6, 0, 'I\'ve heard about enough a\' yer snivelin\'! Shut yer flytrap before I shut it for ye\'!', 14, 0, 100, 0, 0, 8904, 0, 13039, 3, 'korthazz SAY_KORT_TAUNT2');
INSERT INTO `creature_text` VALUES (16065, 0, 0, 'Defend yourself!', 14, 0, 100, 0, 0, 8892, 0, 13010, 0, 'blaumeux SAY_BLAU_AGGRO');
INSERT INTO `creature_text` VALUES (16065, 1, 2, 'The first kill goes to me! Anyone care to wager?', 14, 0, 100, 0, 0, 8898, 0, 13016, 0, 'blaumeux SAY_BLAU_TAUNT3');
INSERT INTO `creature_text` VALUES (16065, 2, 0, 'Your life is mine!', 14, 0, 100, 0, 0, 8895, 0, 13013, 0, 'blaumeux SAY_BLAU_SPECIAL');
INSERT INTO `creature_text` VALUES (16065, 3, 0, 'Who\'s next?', 14, 0, 30, 0, 0, 8894, 0, 21654, 0, 'blaumeux SAY_BLAU_SLAY');
INSERT INTO `creature_text` VALUES (16065, 4, 0, 'Touche...', 14, 0, 100, 0, 0, 8893, 0, 13011, 0, 'blaumeux SAY_BLAU_DEATH');
INSERT INTO `creature_text` VALUES (16065, 5, 0, 'Come, Zeliek, do not drive them out. Not before we\'ve had our fun!', 14, 0, 100, 0, 0, 8896, 0, 13014, 3, 'blaumeux SAY_BLAU_TAUNT1');
INSERT INTO `creature_text` VALUES (16065, 6, 0, 'I do hope they stay alive long enough for me to... introduce myself.', 14, 0, 100, 0, 0, 8897, 0, 13015, 3, 'blaumeux SAY_BLAU_TAUNT2');
INSERT INTO `creature_text` VALUES (16065, 7, 0, '%s casts Unyielding Pain on everyone!', 41, 0, 100, 0, 0, 0, 0, 33087, 3, 'blaumeux EMOTE_RAGECAST');
INSERT INTO `creature_text` VALUES (16098, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Empyrean');
INSERT INTO `creature_text` VALUES (16128, 0, 0, 'We received Brann\'s message, and we have begun preparations.', 12, 0, 100, 1, 0, 15649, 0, 33982, 0, 'Rhonin');
INSERT INTO `creature_text` VALUES (16128, 1, 0, 'Citizens of Dalaran! Raise your eyes to the skies and observe!', 14, 0, 100, 0, 0, 15650, 0, 33983, 2, 'Rhonin');
INSERT INTO `creature_text` VALUES (16128, 2, 0, 'Today our world\'s destruction has been averted in defiance of our very makers!', 14, 0, 100, 0, 0, 15651, 0, 33984, 2, 'Rhonin');
INSERT INTO `creature_text` VALUES (16128, 3, 0, 'Algalon the Observer, herald of the titans, has been defeated by our brave comrades in the depths of the titan city of Ulduar.', 14, 0, 100, 0, 0, 15652, 0, 33985, 2, 'Rhonin');
INSERT INTO `creature_text` VALUES (16128, 4, 0, 'Algalon was sent here to judge the fate of our world.', 14, 0, 100, 0, 0, 15653, 0, 33986, 2, 'Rhonin');
INSERT INTO `creature_text` VALUES (16128, 5, 0, 'He found a planet whose races had deviated from the titans\' blueprints. A planet where not everything had gone according to plan.', 14, 0, 100, 0, 0, 15654, 0, 33987, 2, 'Rhonin');
INSERT INTO `creature_text` VALUES (16128, 6, 0, 'Cold logic deemed our world not worth saving. Cold logic, however, does not account for the power of free will. It\'s up to each of us to prove this is a world worth saving.', 14, 0, 100, 0, 0, 15655, 0, 33988, 2, 'Rhonin');
INSERT INTO `creature_text` VALUES (16128, 7, 0, 'That our lives... our lives are worth living.', 14, 0, 100, 0, 0, 15656, 0, 33989, 2, 'Rhonin');
INSERT INTO `creature_text` VALUES (16144, 0, 0, 'As you say, my dear, as you say.', 12, 1, 100, 1, 0, 0, 0, 11996, 0, 'Lord Saltheril');
INSERT INTO `creature_text` VALUES (16144, 0, 1, 'Hmm, what was that? I\'m sorry my dear, I must have been daydreaming.', 12, 1, 100, 6, 0, 0, 0, 11997, 0, 'Lord Saltheril');
INSERT INTO `creature_text` VALUES (16144, 0, 2, 'Oh, most definitely, Elisara. But please, can you show the least bit of patience?', 12, 1, 100, 1, 0, 0, 0, 11998, 0, 'Lord Saltheril');
INSERT INTO `creature_text` VALUES (16144, 0, 3, 'Is that so? How very interesting. My dear, why don\'t you run along and powder your nose? I have much to think about.', 12, 1, 100, 1, 0, 0, 0, 11999, 0, 'Lord Saltheril');
INSERT INTO `creature_text` VALUES (16147, 0, 0, 'I was just thinking... we really ought to take a trip south one of these nights, maybe have an evening picnic overlooking the fighting at the border? What do you think, milord? Wouldn\'t that just be so exciting?', 12, 1, 100, 6, 0, 0, 0, 12070, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` VALUES (16147, 0, 1, 'Milord, with much of the sin\'dorei nobility regrettably crushed, does that mean that you are likely to receive an elevation soon?  I\'m so excited for you!', 12, 1, 100, 6, 0, 0, 0, 12071, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` VALUES (16147, 0, 2, 'Milord, do you think that we can join the party soon? I\'m so bored in here.', 12, 1, 100, 6, 0, 0, 0, 11992, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` VALUES (16147, 0, 3, 'Oh, my lordship, I do so hope that you\'ve arranged for more of that excellent wine from Silvermoon City. I dare say drinking it makes me feel almost as forward as a common woman!', 12, 1, 100, 1, 0, 0, 0, 11993, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` VALUES (16147, 0, 4, 'I\'m so tired. We really shouldn\'t have stayed up so late last night... well this morning. But it was fun! You do throw the best soirees milord.', 12, 1, 100, 1, 0, 0, 0, 0, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` VALUES (16147, 0, 5, 'Lord Saltheril, my dear, did you notice that supplies for the party are a little low? We really should send for one of those couriers from Fairbreeze Village to deliver more of whatever is needed. I especially like those little hors d\'oeuvres wrapped in the smoked bacon. What are those called?', 12, 1, 100, 6, 0, 0, 0, 11995, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` VALUES (16147, 0, 6, 'Salthy, do you think we\'re letting too many \'undesirables\' into the party?  I do worry about what our friends might think of all of this.', 12, 1, 100, 6, 0, 0, 0, 13355, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` VALUES (16147, 0, 7, 'You promised to take me out for a night on the town!  Are we just going to stand around here again tonight?', 12, 1, 100, 5, 0, 0, 0, 13356, 0, 'Elisara Sunstriker');
INSERT INTO `creature_text` VALUES (16151, 0, 0, '%s calls for her master!', 16, 0, 100, 0, 0, 0, 0, 13439, 0, 'midnight EMOTE_CALL_ATTUMEN');
INSERT INTO `creature_text` VALUES (16151, 1, 0, '%s rushes to her master\'s aid.', 16, 0, 100, 0, 0, 0, 0, 13455, 0, 'midnight EMOTE_MOUNT_UP');
INSERT INTO `creature_text` VALUES (16152, 0, 0, 'It was... inevitable.', 14, 0, 100, 0, 0, 9169, 0, 13460, 0, 'attumen SAY_KILL1');
INSERT INTO `creature_text` VALUES (16152, 0, 1, 'Another trophy to add to my collection!', 14, 0, 100, 0, 0, 9300, 0, 15333, 0, 'attumen SAY_KILL2');
INSERT INTO `creature_text` VALUES (16152, 1, 0, 'Such easy sport.', 14, 0, 100, 0, 0, 9170, 0, 0, 0, 'attumen SAY_RANDOM1');
INSERT INTO `creature_text` VALUES (16152, 1, 1, 'Amateurs! Do not think you can best me! I kill for a living.', 14, 0, 100, 0, 0, 9304, 0, 0, 0, 'attumen SAY_RANDOM2');
INSERT INTO `creature_text` VALUES (16152, 2, 0, 'Weapons are merely a convenience for a warrior of my skill!', 14, 0, 100, 0, 0, 9166, 0, 13490, 0, 'attumen SAY_DISARMED');
INSERT INTO `creature_text` VALUES (16152, 3, 0, 'Always knew... someday I would become... the hunted.', 14, 0, 100, 0, 0, 9165, 0, 13462, 0, 'attumen SAY_DEATH');
INSERT INTO `creature_text` VALUES (16183, 0, 0, 'Where... where am I?', 12, 0, 100, 0, 0, 0, 0, 12726, 0, 'Courier Dawnstrider');
INSERT INTO `creature_text` VALUES (16189, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Skymaster Sunwing');
INSERT INTO `creature_text` VALUES (16192, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Skymistress Gloaming');
INSERT INTO `creature_text` VALUES (16196, 0, 0, 'You\'re not going anywhere just yet, handsome.  Not until you regain your strength.', 12, 0, 100, 0, 0, 0, 0, 12728, 0, 'Apothecary Thedra');
INSERT INTO `creature_text` VALUES (16204, 0, 0, 'Why doesn\'t Kaendris move against Dar\'Khan yet?  What is it we\'re waiting for?', 12, 0, 100, 1, 0, 0, 0, 12594, 0, 'Magister Idonis');
INSERT INTO `creature_text` VALUES (16206, 0, 0, 'Th... thank you.  I thought I was going to die.', 12, 0, 100, 0, 0, 0, 0, 12222, 0, 'Apprentice Varnis');
INSERT INTO `creature_text` VALUES (16208, 0, 0, 'Thanks, I should\'ve never left Silverpine Forest.', 12, 0, 100, 6, 0, 0, 0, 12220, 0, 'Apothecary Enith');
INSERT INTO `creature_text` VALUES (16209, 0, 0, 'You have my thanks!', 12, 0, 100, 0, 0, 0, 0, 12221, 0, 'Ranger Vedoran');
INSERT INTO `creature_text` VALUES (16217, 0, 0, 'Take that!', 12, 0, 100, 0, 0, 0, 0, 12544, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` VALUES (16217, 0, 1, 'This isnt Silvermoon City! Where are we? Oh, I was confused there for a moment.', 12, 0, 100, 0, 0, 0, 0, 12542, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` VALUES (16217, 0, 2, 'I tell you now, when we get back to Farstrider Enclave there will be a full accounting of your actions, Ranger Valanna! You alone were responsible for our losses and Im certain that the captain will see it my way.', 12, 0, 100, 0, 0, 0, 0, 12543, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` VALUES (16217, 0, 3, 'Im sure to receive a commendation upon our return. We may have suffered a few losses, but overall I think we did quite nicely, dont you, Valanna?', 12, 0, 100, 0, 0, 0, 0, 12545, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` VALUES (16217, 0, 4, 'Did I not yell at them? Pull back! Pull back and regroup with suppressing fire!!', 12, 0, 100, 0, 0, 0, 0, 12541, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` VALUES (16217, 0, 5, 'They should have listened to me.  Then Id receive all of the accolades that are my due!', 12, 0, 100, 0, 0, 0, 0, 12540, 0, 'Lieutenant Tomathren - Out of combat');
INSERT INTO `creature_text` VALUES (16218, 0, 0, '%s loses its link!', 41, 0, 100, 0, 0, 0, 0, 12156, 3, 'Tesla Coil EMOTE_TESLA_LINK_BREAKS');
INSERT INTO `creature_text` VALUES (16218, 1, 0, '%s overloads!', 41, 0, 100, 0, 0, 0, 0, 12178, 3, 'Tesla Coil EMOTE_TESLA_OVERLOAD');
INSERT INTO `creature_text` VALUES (16219, 0, 0, 'You have got to be the most incompetent officer I have ever met!', 12, 0, 100, 0, 0, 0, 0, 22242, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` VALUES (16219, 0, 1, 'Remind me once again why it is that I dont just leave?', 12, 0, 100, 0, 0, 0, 0, 22241, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` VALUES (16219, 0, 2, 'Uh huh.  Whatever you say.', 12, 0, 100, 0, 0, 0, 0, 22238, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` VALUES (16219, 0, 3, 'Idiot.', 12, 0, 100, 0, 0, 0, 0, 22239, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` VALUES (16219, 0, 4, 'Would you please shut up?!', 12, 0, 100, 0, 0, 0, 0, 22240, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` VALUES (16219, 0, 5, 'With all due respect, sir, stick it!', 12, 0, 100, 0, 0, 0, 0, 22243, 0, 'Ranger Valanna - Out of combat');
INSERT INTO `creature_text` VALUES (16220, 0, 0, 'Lilatha, get someone to look at those injuries. Thank you for bringing her back safely.', 12, 1, 100, 0, 0, 0, 0, 12193, 0, 'lilatha CAPTAIN_ANSWER');
INSERT INTO `creature_text` VALUES (16222, 0, 0, 'Glory to the Sun K-- ah... Silvermoon!', 12, 1, 100, 0, 0, 0, 0, 14807, 0, 'Silvermoon City Guardian');
INSERT INTO `creature_text` VALUES (16222, 1, 0, 'Our lives are yours, Blood Knight.', 12, 1, 100, 0, 0, 0, 0, 14791, 0, 'Silvermoon City Guardian');
INSERT INTO `creature_text` VALUES (16222, 2, 0, 'Even the Light is no match for the indomitable will of our people.', 12, 1, 100, 0, 0, 0, 0, 14793, 0, 'Silvermoon City Guardian');
INSERT INTO `creature_text` VALUES (16222, 3, 0, 'Your power strengthens us all.', 12, 1, 100, 0, 0, 0, 0, 14792, 0, 'Silvermoon City Guardian');
INSERT INTO `creature_text` VALUES (16227, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Skymistress Gloaming');
INSERT INTO `creature_text` VALUES (16231, 0, 0, '%s nods her head yes at what her advisor has to say.', 16, 0, 100, 273, 0, 0, 0, 13362, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 0, 1, '%s disagrees with whatever it is that Valwyn has whispered to her.', 16, 0, 100, 274, 0, 0, 0, 13363, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 0, 2, '%s seems nonplussed by whatever it is that her advisor has whispered in her ear.', 16, 0, 100, 6, 0, 0, 0, 13365, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 1, 0, 'Executor, your direct assault approach will cost us more lives than I am comfortable with. A more nuanced strategy is called for; one that involves us reclaiming the surrounding territory, starting with the villages so as to cut off all points of reinforcement to Deatholme.', 12, 1, 100, 0, 0, 0, 0, 12552, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 2, 0, 'I hear what you are saying, Mavren. Nevertheless, the final decision is mine. I appreciate your assistance, but the majority of the lives on the line are blood elf. I will not have those lives carelessly thrown away!', 12, 1, 100, 0, 0, 0, 0, 12571, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 3, 0, 'How long until Forsaken reinforcements arrive? Our position here is tenuous. Your Lady promised us more soldiers. We must be able to concentrate solely on Deatholme and the Scourge!', 12, 1, 100, 0, 0, 0, 0, 12557, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 4, 0, 'Mavren, I don\'t want excuses, I want results! The Farstriders aren\'t available and we\'ve received all that we\'re going to get from Silvermoon for now. I cannot ignore the Shadowpine trolls and Zul\'Aman... they\'re arrayed across our eastern border!', 12, 1, 100, 0, 0, 0, 0, 12572, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 5, 0, 'No, I was right to begin with. Deatholme must come last. We must secure all of the Ghostlands first. I will not commit the forces here to a battle against Dar\'khan with enemies to our flank and rear!', 12, 1, 100, 0, 0, 0, 0, 12562, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16231, 6, 0, 'Disagree with me all you like, High Executor. I will weigh your counsel, and then we will take the steps to free all of Quel\'Thalas as I deem necessary. We shall continue this discussion anon.', 12, 1, 100, 0, 0, 0, 0, 12573, 0, 'Dame Auriferous');
INSERT INTO `creature_text` VALUES (16240, 0, 0, 'Calm yourself, Idonis.  Brash actions will get us nowhere!  Take a break, tap into some mana crystals.  You\'ll feel better.', 12, 0, 100, 1, 0, 0, 0, 12595, 0, 'Arcanist Janeda');
INSERT INTO `creature_text` VALUES (16240, 0, 1, 'He\'s got his reasons, Idonis.  He thinks the Farstriders have uncovered something.', 12, 0, 100, 1, 0, 0, 0, 12596, 0, 'Arcanist Janeda');
INSERT INTO `creature_text` VALUES (16240, 0, 2, 'We must remain focused on our work.  Once the sanctum starts producing at full capacity, Silvermoon will be forced to send help.  Functioning arcane sanctums are in short supply.', 12, 0, 100, 1, 0, 0, 0, 12597, 0, 'Arcanist Janeda');
INSERT INTO `creature_text` VALUES (16244, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Infectious Ghoul');
INSERT INTO `creature_text` VALUES (16251, 0, 0, '%s nods in agreement.', 16, 0, 100, 0, 0, 0, 0, 12582, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 1, '%s opens his mouth as if to add something, and then apparently thinks the better of it.', 16, 0, 100, 0, 0, 0, 0, 12583, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 2, '%s looks over at Advisor Valwyn with a smirk on his face.', 16, 0, 100, 0, 0, 0, 0, 12584, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 3, '%s grins at the High Executor\'s words.', 16, 0, 100, 0, 0, 0, 0, 12585, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 4, '%s appears bored with the whole discussion.', 16, 0, 100, 0, 0, 0, 0, 12586, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 5, '%s winks slyly at Advisor Valwyn.', 16, 0, 100, 0, 0, 0, 0, 12587, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 6, '%s fidgets with his armor, clearly uncomfortable at the tone of the discussion.', 16, 0, 100, 0, 0, 0, 0, 12588, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 0, 7, '%s looks like he\'d rather be anywhere else but here.', 16, 0, 100, 0, 0, 0, 0, 12589, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16251, 1, 0, '%s turns to Advisor Valwyn and winks lasciviously in her direction.', 16, 0, 100, 2, 0, 0, 0, 13366, 0, 'Deathstalker Maltendis');
INSERT INTO `creature_text` VALUES (16252, 0, 0, 'With all due respect, milady, you have not fought the Scourge as I have. The Lady appointed me to assist you in defeating Dar\'khan because of that, and you would do well to consider what I have to say.', 12, 1, 100, 0, 0, 0, 0, 12553, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 0, 1, 'I strongly disagree. Dar\'khan is not going to get reinforcements because he\'ll be bottlenecked. Deatholme has only one way in and out. Your traitor was a fool to corner himself there, and I intend to exploit that weakness!', 12, 1, 100, 0, 0, 0, 0, 12554, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 0, 2, 'This is war, Dame Auriferous, and in any war lives will be lost! The only areas that we need to secure are the two ziggurats. We will turtle in and take the Tower of the Damned and its master by brute force. Then you will have your Quel\'Thalas.', 12, 1, 100, 0, 0, 0, 0, 12555, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 0, 3, 'And if your Farstriders were here to reinforce us, I might agree. But, they are busying themselves instead with the Shadowpine trolls on your eastern border. The longer we wait for them, the stronger the Scourge will become.', 12, 1, 100, 0, 0, 0, 0, 12556, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 1, 0, 'I believe that you\'re making my argument for me, but yes it would be nice if we had more reinforcements. I would counter that it would be faster to pull the Farstriders from their senseless skirmishing with the trolls.', 12, 1, 100, 0, 0, 0, 0, 12558, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 1, 1, 'Dame Auriferous, we are the reinforcements. It may come to pass that The Lady will send more men in time. Better that you convince Silvermoon City to muster all of its forces and put them at our disposal here.', 12, 1, 100, 0, 0, 0, 0, 12559, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 1, 2, 'You are correct; we must concentrate on the Scourge. Ignore the trolls and the villages, and focus our attentions on Deatholme! With Dar\'khan defeated you will find that the rest of the Scourge \'body\' will fall quickly, lacking their \'head\'.', 12, 1, 100, 0, 0, 0, 0, 12560, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 1, 3, 'Forsaken reinforcements? Undercity is stretched thin with the Scourge on all sides. No, this will be a matter largely dealt with by the blood elves. We Forsaken are here as backup and advisors.', 12, 1, 100, 0, 0, 0, 0, 12561, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 2, 0, 'Again I strongly disagree with your view of the strategic situation. Your plan will spread our forces too thin. It will leave us with only a small force to assault Deatholme. I cannot in good conscience execute such a strategy.', 12, 1, 100, 0, 0, 0, 0, 12567, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 2, 1, 'Spread throughout the Ghostlands, our forces will be of little use in an assault on Deatholme. No, milady, I am here to kill Dar\'khan and that is what I intend to do!', 12, 1, 100, 0, 0, 0, 0, 12568, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 2, 2, 'We\'ve been over this before. Only a focused assault upon Deatholme will meet with success. These other targets are distractions that we can ill afford. I suggest that you inform your Captain Helios that he is to leave off his campaign against the Shadowpine trolls and focus solely on the Scourge.', 12, 1, 100, 0, 0, 0, 0, 12569, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16252, 2, 3, 'Perhaps we should let the matter rest for the time being until your chef has prepared your meal? You look a bit piqued if you don\'t mind my saying. We can resume our discussion when you are feeling more yourself.', 12, 1, 100, 0, 0, 0, 0, 12570, 0, 'High Executor Mavren');
INSERT INTO `creature_text` VALUES (16263, 0, 0, 'Why do we ally with filthy stinking trolls?  The only good troll is a dead troll.', 12, 0, 100, 1, 0, 0, 0, 12787, 0, 'Paelarin');
INSERT INTO `creature_text` VALUES (16263, 0, 1, 'Does anyone need supplies to hunt down the trolls?  Any troll will do.', 12, 0, 100, 1, 0, 0, 0, 12786, 0, 'Paelarin');
INSERT INTO `creature_text` VALUES (16287, 0, 0, 'That could have gone better. $n, come here, please. I have something I need you to take care of for me.', 15, 0, 100, 0, 0, 0, 0, 12157, 0, 'Ambassador Sunsorrow SAY_SUNSORROW_WHISPER');
INSERT INTO `creature_text` VALUES (16289, 0, 0, '%s whispers something in the Dame\'s ear.', 16, 0, 100, 0, 0, 0, 0, 13361, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 0, '%s looks appalled at the Executor\'s tone!', 16, 0, 100, 0, 0, 0, 0, 12574, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 1, '%s tries to busy herself with other matters.', 16, 0, 100, 0, 0, 0, 0, 12575, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 2, '%s glares daggers in the direction of High Executor Mavren and Deathstalker Maltendis.', 16, 0, 100, 0, 0, 0, 0, 12576, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 3, '%s lifts her nose and sniffs in response to the High Executor\'s reply.', 16, 0, 100, 0, 0, 0, 0, 12577, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 4, '%s shakes her head in disbelief at what she is hearing out of the Forsaken\'s mouth.', 16, 0, 100, 0, 0, 0, 0, 12578, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 5, '%s blanches as much of the blood drains from her face.', 16, 0, 100, 0, 0, 0, 0, 12579, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 6, '%s reddens furiously at what she is hearing in response to her lady\'s question.', 16, 0, 100, 0, 0, 0, 0, 12580, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 1, 7, '%s concentrates on the wall opposite her, clearly ignoring the looks of the Executor and his assistant.', 16, 0, 100, 0, 0, 0, 0, 12581, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16289, 2, 0, '%s looks disgusted at the deathstalker\'s flirtations.', 16, 0, 100, 274, 0, 0, 0, 13367, 0, 'Advisor Valwyn');
INSERT INTO `creature_text` VALUES (16295, 0, 0, 'Let\'s go.', 12, 0, 100, 0, 0, 0, 0, 15894, 0, 'lilatha SAY_START');
INSERT INTO `creature_text` VALUES (16295, 1, 0, '$n, let\'s use the antechamber to the right.', 12, 1, 100, 0, 0, 0, 0, 12187, 0, 'lilatha SAY_PROGRESS1');
INSERT INTO `creature_text` VALUES (16295, 2, 0, 'I can see the light at the end of the tunnel!', 12, 1, 100, 0, 0, 0, 0, 12188, 0, 'lilatha SAY_PROGRESS2');
INSERT INTO `creature_text` VALUES (16295, 3, 0, 'There\'s Farstrider Enclave now, $c. Not far to go... look out!  Troll ambush!!', 12, 1, 100, 0, 0, 0, 0, 12189, 0, 'lilatha SAY_PROGRESS3');
INSERT INTO `creature_text` VALUES (16295, 4, 0, 'Thank you for saving my life and bringing me back to safety, $n!', 12, 1, 100, 0, 0, 0, 0, 12191, 0, 'lilatha SAY_END1');
INSERT INTO `creature_text` VALUES (16295, 5, 0, 'Captain Helios, I\'ve been rescued from the Amani Catacombs. Reporting for duty, sir!', 12, 1, 100, 0, 0, 0, 0, 12190, 0, 'lilatha SAY_END2');
INSERT INTO `creature_text` VALUES (16325, 0, 0, '%s shimmers and becomes intangible!', 16, 0, 100, 0, 0, 0, 0, 12690, 0, 'Quel\'dorei Ghost');
INSERT INTO `creature_text` VALUES (16326, 0, 0, '%s shimmers and becomes intangible!', 16, 0, 100, 0, 0, 0, 0, 12690, 0, 'Quel\'dorei Wraith');
INSERT INTO `creature_text` VALUES (16329, 0, 0, 'Mortal fools!  The ghouls of Deatholme will feast on your remains!', 12, 0, 100, 0, 0, 0, 0, 12224, 0, 'Dar\'Khan Drathir');
INSERT INTO `creature_text` VALUES (16364, 0, 0, '%s releases the last of its energies into the nearby runestone, successfully reactivating it.', 16, 0, 100, 0, 0, 0, 0, 12272, 0, 'Infused Crystal');
INSERT INTO `creature_text` VALUES (16389, 0, 0, 'You are not welcome here!', 12, 0, 100, 0, 0, 0, 0, 13774, 0, 'Spectral Apprentice - On Aggro');
INSERT INTO `creature_text` VALUES (16389, 0, 1, 'You, hold there!', 12, 0, 100, 0, 0, 0, 0, 13798, 0, 'Spectral Apprentice - On Aggro');
INSERT INTO `creature_text` VALUES (16389, 1, 0, 'I am lost...', 12, 0, 100, 0, 0, 0, 0, 13801, 0, 'Spectral Apprentice - On Death');
INSERT INTO `creature_text` VALUES (16389, 1, 1, 'Why...?', 12, 0, 100, 0, 0, 0, 0, 13802, 0, 'Spectral Apprentice - On Death');
INSERT INTO `creature_text` VALUES (16406, 0, 0, 'What is happening?', 12, 0, 100, 0, 0, 0, 0, 13848, 0, 'Phantom Attendant');
INSERT INTO `creature_text` VALUES (16406, 0, 1, 'Where am I? What--', 12, 0, 100, 0, 0, 0, 0, 13847, 0, 'Phantom Attendant');
INSERT INTO `creature_text` VALUES (16406, 0, 2, 'I mustn\'t be afraid....', 12, 0, 100, 0, 0, 0, 0, 13849, 0, 'Phantom Attendant - On Death');
INSERT INTO `creature_text` VALUES (16406, 1, 0, 'You\'ll ruin everything!', 12, 0, 100, 0, 0, 0, 0, 13844, 0, 'Phantom Attendant - On Aggro');
INSERT INTO `creature_text` VALUES (16406, 1, 1, 'Off with you!', 12, 0, 100, 0, 0, 0, 0, 13845, 0, 'Phantom Attendant - On Aggro');
INSERT INTO `creature_text` VALUES (16406, 1, 2, 'We do not wish to be disturbed!', 12, 0, 100, 0, 0, 0, 0, 13846, 0, 'Phantom Attendant - On Aggro');
INSERT INTO `creature_text` VALUES (16407, 0, 0, 'No!', 12, 0, 100, 0, 0, 0, 0, 35155, 0, 'Spectral Servant - On Death Say');
INSERT INTO `creature_text` VALUES (16407, 0, 1, 'I am finished!', 12, 0, 100, 0, 0, 0, 0, 13823, 0, 'Spectral Servant - On Death Say');
INSERT INTO `creature_text` VALUES (16407, 0, 2, 'The master... will be angry....', 12, 0, 100, 0, 0, 0, 0, 13825, 0, 'Spectral Servant - On Death Say');
INSERT INTO `creature_text` VALUES (16407, 1, 0, 'What is this?', 12, 0, 100, 0, 0, 0, 0, 13820, 0, 'Spectral Servant - On Aggro');
INSERT INTO `creature_text` VALUES (16407, 1, 1, 'Invaders in the tower!', 12, 0, 100, 0, 0, 0, 0, 13821, 0, 'Spectral Servant - On Aggro');
INSERT INTO `creature_text` VALUES (16407, 1, 2, 'Stop them!', 12, 0, 100, 0, 0, 0, 0, 13822, 0, 'Spectral Servant - On Aggro');
INSERT INTO `creature_text` VALUES (16408, 0, 0, 'The fates have turned against me!', 12, 0, 100, 0, 0, 0, 0, 13856, 0, 'Phantom Valet');
INSERT INTO `creature_text` VALUES (16408, 0, 1, 'My time is done.', 12, 0, 100, 0, 0, 0, 0, 13857, 0, 'Phantom Valet - On Death');
INSERT INTO `creature_text` VALUES (16408, 0, 2, 'No, not now! Not like this!', 12, 0, 100, 0, 0, 0, 0, 13858, 0, 'Phantom Valet - On Death');
INSERT INTO `creature_text` VALUES (16408, 1, 0, 'Hey! Where do you think you are going, sneaking around like that?', 12, 0, 100, 0, 0, 0, 0, 13398, 0, 'Phantom Valet - On Aggro');
INSERT INTO `creature_text` VALUES (16408, 1, 1, 'Excuse me, are you trying to sneak by without an invitation?', 12, 0, 100, 0, 0, 0, 0, 13399, 0, 'Phantom Valet - On Aggro');
INSERT INTO `creature_text` VALUES (16408, 1, 2, 'Begone, outsider!', 12, 0, 100, 0, 0, 0, 0, 13851, 0, 'Phantom Valet - On Aggro');
INSERT INTO `creature_text` VALUES (16408, 1, 3, 'You\'ll not ruin the celebration!', 12, 0, 100, 0, 0, 0, 0, 13852, 0, 'Phantom Valet - On Aggro');
INSERT INTO `creature_text` VALUES (16408, 1, 4, 'We are besieged!', 12, 0, 100, 0, 0, 0, 0, 13853, 0, 'Phantom Valet - On Aggro');
INSERT INTO `creature_text` VALUES (16409, 0, 0, 'Not... fair...', 12, 0, 100, 0, 0, 0, 0, 13841, 0, 'Phantom Guest');
INSERT INTO `creature_text` VALUES (16409, 0, 1, 'It\'s not my time, not yet!', 12, 0, 100, 0, 0, 0, 0, 13842, 0, 'Phantom Guest - On Death');
INSERT INTO `creature_text` VALUES (16409, 0, 2, 'All good things must come to an end.', 12, 0, 100, 0, 0, 0, 0, 13843, 0, 'Phantom Guest - On Death');
INSERT INTO `creature_text` VALUES (16409, 1, 0, 'How dare you interfere?!', 12, 0, 100, 0, 0, 0, 0, 13838, 0, 'Phantom Guest - On Aggro');
INSERT INTO `creature_text` VALUES (16409, 1, 1, 'You do not belong here!', 12, 0, 100, 0, 0, 0, 0, 13839, 0, 'Phantom Guest - On Aggro');
INSERT INTO `creature_text` VALUES (16409, 1, 2, 'Leave us alone! Let us be!', 12, 0, 100, 0, 0, 0, 0, 13840, 0, 'Phantom Guest - On Aggro');
