INSERT INTO `creature_text` VALUES (16485, 0, 0, 'The same fate awaits all who would steal from the master.', 12, 0, 100, 0, 0, 0, 0, 13954, 0, 'Arcane Watchman');
INSERT INTO `creature_text` VALUES (16485, 0, 1, 'Death is far too merciful for your kind.', 12, 0, 100, 0, 0, 0, 0, 13955, 0, 'Arcane Watchman');
INSERT INTO `creature_text` VALUES (16485, 1, 0, 'This... changes nothing. Eternal damnation awaits you!\n', 12, 0, 100, 0, 0, 0, 0, 13958, 0, 'Arcane Watchman');
INSERT INTO `creature_text` VALUES (16485, 1, 1, 'Others will take my place!', 12, 0, 100, 0, 0, 0, 0, 13957, 0, 'Arcane Watchman');
INSERT INTO `creature_text` VALUES (16502, 0, 0, 'Oh no!  I\'m losing this one!', 12, 7, 100, 0, 0, 0, 0, 13113, 0, 'Zalduun');
INSERT INTO `creature_text` VALUES (16514, 0, 0, 'Tedon, go ahead and try the neutralizing agent on the mutated creature.', 12, 0, 100, 1, 0, 0, 0, 17938, 0, 'Botanist Taerix');
INSERT INTO `creature_text` VALUES (16518, 0, 0, 'The %s seems overjoyed.', 16, 0, 100, 0, 0, 0, 0, 12516, 0, 'Inoculated Nestlewood Owlkin');
INSERT INTO `creature_text` VALUES (16518, 0, 1, 'The %s seems disoriented.', 16, 0, 100, 0, 0, 0, 0, 12518, 0, 'Inoculated Nestlewood Owlkin');
INSERT INTO `creature_text` VALUES (16518, 0, 2, 'The %s doesn\'t look like it minds the crystal\'s effects.', 16, 0, 100, 0, 0, 0, 0, 12520, 0, 'Inoculated Nestlewood Owlkin');
INSERT INTO `creature_text` VALUES (16518, 0, 3, 'The %s didn\'t like what just happened.', 16, 0, 100, 0, 0, 0, 0, 12521, 0, 'Inoculated Nestlewood Owlkin');
INSERT INTO `creature_text` VALUES (16518, 0, 4, 'The %s looks confused.', 16, 0, 100, 0, 0, 0, 0, 12517, 0, 'Inoculated Nestlewood Owlkin');
INSERT INTO `creature_text` VALUES (16518, 0, 5, 'The %s nods appreciatively.', 16, 0, 100, 0, 0, 0, 0, 12514, 0, 'Inoculated Nestlewood Owlkin');
INSERT INTO `creature_text` VALUES (16521, 0, 0, 'I hope you\'ve made your peace.', 12, 0, 100, 0, 0, 0, 0, 13428, 0, 'Blood Elf Scout');
INSERT INTO `creature_text` VALUES (16521, 0, 1, 'We won\'t allow you to leave this valley!', 12, 0, 100, 0, 0, 0, 0, 13426, 0, 'Blood Elf Scout');
INSERT INTO `creature_text` VALUES (16521, 0, 2, 'What a pity you survived the crash just to die here.', 12, 0, 100, 0, 0, 0, 0, 13427, 0, 'Blood Elf Scout');
INSERT INTO `creature_text` VALUES (16522, 0, 0, 'You\'ll not take me alive, scum!', 12, 0, 100, 0, 0, 0, 0, 12767, 0, 'Surveyor Candress');
INSERT INTO `creature_text` VALUES (16523, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Shattered Hand Savage');
INSERT INTO `creature_text` VALUES (16524, 0, 0, 'Please, no more! My son... he\'s gone mad!', 14, 0, 100, 0, 0, 9241, 0, 13529, 0, 'aran SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (16524, 0, 1, 'I\'ll not be tortured again!', 14, 0, 100, 0, 0, 9323, 0, 15353, 0, 'aran SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (16524, 0, 2, 'Who are you? What do you want? Stay away from me!', 14, 0, 100, 0, 0, 9324, 0, 13530, 0, 'aran SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (16524, 1, 0, 'I\'ll show you: this beaten dog still has some teeth!', 14, 0, 100, 0, 0, 9245, 0, 13519, 0, 'aran SAY_FLAMEWREATH1');
INSERT INTO `creature_text` VALUES (16524, 1, 1, 'Burn, you hellish fiends!', 14, 0, 100, 0, 0, 9326, 0, 13520, 0, 'aran SAY_FLAMEWREATH2');
INSERT INTO `creature_text` VALUES (16524, 2, 0, 'I\'ll freeze you all!', 14, 0, 100, 0, 0, 9246, 0, 13522, 0, 'aran SAY_BLIZZARD1');
INSERT INTO `creature_text` VALUES (16524, 2, 1, 'Back to the cold dark with you!', 14, 7, 100, 0, 0, 9327, 0, 22914, 0, 'aran SAY_BLIZZARD2');
INSERT INTO `creature_text` VALUES (16524, 3, 0, 'Yes, yes my son is quite powerful... but I have powers of my own!', 14, 0, 100, 0, 0, 9242, 0, 13517, 0, 'aran SAY_EXPLOSION1');
INSERT INTO `creature_text` VALUES (16524, 3, 1, 'I am not some simple jester! I am Nielas Aran!', 14, 0, 100, 0, 0, 9325, 0, 13518, 0, 'aran SAY_EXPLOSION2');
INSERT INTO `creature_text` VALUES (16524, 4, 0, 'Surely you wouldn\'t deny an old man a replenishing drink? No, no, I thought not.', 14, 0, 100, 0, 0, 9248, 0, 13514, 0, 'aran SAY_DRINK');
INSERT INTO `creature_text` VALUES (16524, 5, 0, 'I\'m not finished yet! No, I have a few more tricks up my sleeve...', 12, 0, 100, 0, 0, 9251, 0, 13513, 0, 'aran SAY_ELEMENTALS');
INSERT INTO `creature_text` VALUES (16524, 6, 0, 'I want this nightmare to be over!', 14, 0, 100, 0, 0, 9250, 0, 13526, 0, 'aran SAY_KILL1');
INSERT INTO `creature_text` VALUES (16524, 6, 1, 'Torment me no more!', 14, 0, 100, 0, 0, 9328, 0, 13527, 0, 'aran SAY_KILL2');
INSERT INTO `creature_text` VALUES (16524, 7, 0, 'You\'ve wasted enough of my time... let these games be finished!', 14, 0, 100, 0, 0, 9247, 0, 13569, 0, 'aran SAY_TIMEOVER');
INSERT INTO `creature_text` VALUES (16524, 8, 0, 'At last the nightmare is over...', 14, 0, 100, 0, 0, 9244, 0, 15345, 0, 'aran SAY_DEATH');
INSERT INTO `creature_text` VALUES (16524, 9, 0, 'Where did you get that? Did HE send you?', 14, 0, 100, 0, 0, 9249, 0, 13532, 0, 'aran SAY_ATIESH');
INSERT INTO `creature_text` VALUES (16534, 0, 0, 'The %s nods appreciatively.', 16, 0, 100, 2, 0, 0, 0, 12514, 0, 'Inoculated Nestlewood Owlkin');
INSERT INTO `creature_text` VALUES (16534, 0, 1, 'The %s looks confused.', 16, 0, 100, 6, 0, 0, 0, 12517, 0, 'Inoculated Nestlewood Owlkin');
INSERT INTO `creature_text` VALUES (16534, 0, 2, 'The %s didn\'t like what just happened.', 16, 0, 100, 18, 0, 0, 0, 12521, 0, 'Inoculated Nestlewood Owlkin');
INSERT INTO `creature_text` VALUES (16534, 0, 3, 'The %s seems overjoyed.', 16, 0, 100, 4, 0, 0, 0, 12516, 0, 'Inoculated Nestlewood Owlkin');
INSERT INTO `creature_text` VALUES (16534, 0, 4, 'The %s doesn\'t look like it minds the crystal\'s effects.', 16, 0, 100, 22, 0, 0, 0, 12520, 0, 'Inoculated Nestlewood Owlkin');
INSERT INTO `creature_text` VALUES (16544, 0, 0, 'Turn back while you can.', 12, 0, 100, 0, 0, 0, 0, 13965, 0, 'Ethereal Thief');
INSERT INTO `creature_text` VALUES (16544, 1, 0, 'You will succeed only in damning yourselves!', 12, 0, 100, 0, 0, 0, 0, 13972, 0, 'Ethereal Thief');
INSERT INTO `creature_text` VALUES (16550, 0, 0, 'By the seven Ata\'mal crystals!  Others have survived the crash!  Zhanaa, is that you?  It is so good to see you again!', 12, 7, 100, 0, 0, 0, 0, 12845, 0, 'Image of Technician Dyvuun');
INSERT INTO `creature_text` VALUES (16550, 1, 0, 'And who do we have here?  No doubt th... crzzzk ...ade this communication possible?  I give you greetings fro... psshzzzk... Azure Watch.', 12, 7, 100, 0, 0, 0, 0, 12752, 0, 'Image of Technician Dyvuun');
INSERT INTO `creature_text` VALUES (16550, 2, 0, 'Judging by your signal, we put you... ust southeast of the larger part of the island we crashed onto.  Cross the Crystalbrook River to ge... kshhhhk zzzt. Sorry, there\'s a lot of stat...', 12, 7, 100, 0, 0, 0, 0, 12753, 0, 'Image of Technician Dyvuun');
INSERT INTO `creature_text` VALUES (16551, 0, 0, 'Yes, Zhanna, it\'s me! Your signal is weak.', 12, 0, 100, 0, 0, 0, 0, 13687, 0, 'Technician Dyvuun');
INSERT INTO `creature_text` VALUES (16551, 1, 0, 'Zhanna! Gah, I have to get this thing fixed!', 12, 0, 100, 0, 0, 0, 0, 13692, 0, 'Technician Dyvuun');
INSERT INTO `creature_text` VALUES (16573, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 1191, 3, 'Crypt Guard EMOTE_FRENZY');
INSERT INTO `creature_text` VALUES (16573, 1, 0, 'A Crypt Guard joins the fight!', 41, 0, 100, 0, 0, 0, 0, 29887, 3, 'Crypt Guard EMOTE_SPAWN');
INSERT INTO `creature_text` VALUES (16573, 2, 0, 'Corpse Scarabs appear from a Crypt Guard\'s corpse!', 41, 0, 100, 0, 0, 0, 0, 32796, 3, 'Crypt Guard EMOTE_SCARAB');
INSERT INTO `creature_text` VALUES (16577, 0, 0, 'Brothers and sisters, I have been to the promised land. I have tasted in the sublime energy. I have felt bliss - bliss so engrossing and all encompassing that I was left wondering if I had stumbled upon the dreams of gods.', 12, 1, 100, 5, 0, 0, 0, 12698, 0, 'Martik Tor\'seldoi');
INSERT INTO `creature_text` VALUES (16577, 1, 0, 'In paradise, you merely reach out and take from the Nether.', 12, 1, 100, 1, 0, 0, 0, 12703, 0, 'Martik Tor\'seldoi');
INSERT INTO `creature_text` VALUES (16577, 2, 0, 'From the very air! Tendrils of arcane energy light up the obsidian sky as plumes of raw magic rise up from fissures in the land. Kneel and drink from the fissure as you do from a stream or well. Pluck a tendril from the heavens as if it were an apple hanging from a branch.', 12, 1, 100, 5, 0, 0, 0, 12706, 0, 'Martik Tor\'seldoi');
INSERT INTO `creature_text` VALUES (16578, 0, 0, 'Tell us more, Martik. What is it? What does it feel like?', 12, 1, 100, 1, 0, 0, 0, 12700, 0, 'Blood Elf Pilgrim');
INSERT INTO `creature_text` VALUES (16578, 1, 0, 'But how?', 12, 1, 100, 6, 0, 0, 0, 12704, 0, 'Blood Elf Pilgrim');
INSERT INTO `creature_text` VALUES (16578, 2, 0, '%s gasps.', 16, 1, 100, 0, 0, 0, 0, 12707, 0, 'Blood Elf Pilgrim');
INSERT INTO `creature_text` VALUES (16580, 0, 0, 'I say we fire it. Cut the rope.', 12, 1, 100, 1, 0, 0, 0, 16981, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 1, 0, 'You cut the rope. You have the knife.', 12, 1, 100, 1, 0, 0, 0, 16983, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 2, 0, 'Harumph! Coward.', 12, 1, 100, 1, 0, 0, 0, 16982, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 3, 0, 'You know, I think we can hit Honor Hold from here.', 12, 1, 100, 1, 0, 0, 0, 16986, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 4, 0, 'If General Krakork found out, he\'d eat us alive. Don\'t.', 12, 1, 100, 1, 0, 0, 0, 16988, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 5, 0, 'Ah! We can tell him Hagash stumbled into it and fired it accidentally!', 12, 1, 100, 1, 0, 0, 0, 16987, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 6, 0, 'I\'m blind, you stupid sow-spawn, not deaf. Don\'t even think about it.', 12, 1, 100, 1, 0, 0, 0, 16990, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16580, 7, 0, '%s sighs.', 16, 0, 100, 0, 0, 0, 0, 16989, 0, 'Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16584, 0, 0, 'I\'ve seen gnomes work harder than you, peon! Little girl gnomes - with pig tails! MOVE IT, MOVE IT, MOVE IT!', 12, 1, 100, 5, 0, 0, 0, 12551, 0, 'Watch Commander Krunk');
INSERT INTO `creature_text` VALUES (16585, 0, 0, 'Yer slop is ready. Come n\' get it...', 12, 1, 100, 0, 0, 0, 0, 12531, 0, 'Cookie One-Eye');
INSERT INTO `creature_text` VALUES (16585, 1, 0, 'And if I hear that you palookas complained to Nazgrel again, it\'ll be one of ya\'s that I serve up next time...', 12, 1, 100, 0, 0, 0, 0, 12532, 0, 'Cookie One-Eye');
INSERT INTO `creature_text` VALUES (16587, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Barley');
INSERT INTO `creature_text` VALUES (16589, 0, 0, 'So there we were - me and the battalion, doing a routine recon down the bone road. We\'d sent the scout up ahead to look around in case anything dangerous come up.', 12, 1, 100, 0, 0, 0, 0, 12617, 0, 'Guard Captain Cragtar - Speech Text 0');
INSERT INTO `creature_text` VALUES (16589, 1, 0, 'Sure enough, scout came runnin\' back, talkin\' bout some orcs down the path. They hadn\'t seen us yet so we figured we\'d go get acquainted.', 12, 1, 100, 0, 0, 0, 0, 12618, 0, 'Guard Captain Cragtar - Speech Text 1');
INSERT INTO `creature_text` VALUES (16589, 2, 0, 'Big red suckers! Bigger than any orc I\'d ever seen. Muscles out to here, tusks out to here... ', 12, 1, 100, 0, 0, 0, 0, 12625, 0, 'Guard Captain Cragtar - Speech Text 2');
INSERT INTO `creature_text` VALUES (16589, 3, 0, '%s nods.', 16, 0, 100, 0, 0, 0, 0, 37791, 0, 'Guard Captain Cragtar - Speech Text 3');
INSERT INTO `creature_text` VALUES (16589, 4, 0, 'Fel orcs...', 12, 1, 100, 0, 0, 0, 0, 12628, 0, 'Guard Captain Cragtar - Speech Text 4');
INSERT INTO `creature_text` VALUES (16589, 5, 0, 'I dunno, grunt. I just - don\'t - know...', 12, 1, 100, 0, 0, 0, 0, 12636, 0, 'Guard Captain Cragtar - Speech Text 5');
INSERT INTO `creature_text` VALUES (16589, 6, 0, 'But that\'s not the worst of it. Before I could pull us back, a group of \'em spotted us and attacked.', 12, 1, 100, 0, 0, 0, 0, 12637, 0, 'Guard Captain Cragtar - Speech Text 6');
INSERT INTO `creature_text` VALUES (16589, 7, 0, 'Lost some of my best soldiers that day...', 12, 1, 100, 0, 0, 0, 0, 12638, 0, 'Guard Captain Cragtar - Speech Text 7');
INSERT INTO `creature_text` VALUES (16590, 0, 0, 'If my stomach wasn\'t threatening to dislodge itself from my body and leave me for the refuse that we give to the prisoners, I might be insulted by this... this food.', 12, 1, 100, 0, 0, 0, 0, 12550, 0, 'Injured Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16590, 0, 1, 'Do I detect a hint of cinnamon? Or is that nutmeg? Whatever it is, one thing\'s for sure - nobody cooks a donkey like you, Cookie.', 12, 1, 100, 0, 0, 0, 0, 12547, 0, 'Injured Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16590, 1, 0, 'What kinda orcs, chief?', 12, 1, 100, 0, 0, 0, 0, 12619, 0, 'Injured Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16590, 2, 0, 'You mean...', 12, 1, 100, 0, 0, 0, 0, 12626, 0, 'Injured Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16590, 3, 0, 'But how, chief? I thought the curse was lifted...\n', 12, 1, 100, 0, 0, 0, 0, 12635, 0, 'Injured Thrallmar Grunt');
INSERT INTO `creature_text` VALUES (16699, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Shattered Hand Reaver');
INSERT INTO `creature_text` VALUES (16769, 0, 0, 'Knowing there isn\'t enough time, the %s doesn\'t even try to run.', 16, 0, 100, 0, 0, 0, 0, 19051, 0, 'Firewing Warlock');
INSERT INTO `creature_text` VALUES (16769, 1, 0, 'For Kael\'thas!', 12, 0, 100, 0, 0, 0, 0, 16252, 0, 'Firewing Warlock');
INSERT INTO `creature_text` VALUES (16769, 1, 1, 'You won\'t escape alive!', 12, 0, 100, 0, 0, 0, 0, 16249, 0, 'Firewing Warlock');
INSERT INTO `creature_text` VALUES (16769, 1, 2, 'Kill them before they get away!', 12, 0, 100, 0, 0, 0, 0, 16250, 0, 'Firewing Warlock');
INSERT INTO `creature_text` VALUES (16769, 1, 3, 'I will have your energy and then your life!', 12, 0, 100, 0, 0, 0, 0, 16254, 0, 'Firewing Warlock');
INSERT INTO `creature_text` VALUES (16781, 0, 0, 'The Horde fires will never be extinguished!', 12, 1, 100, 1, 0, 0, 0, 24534, 0, 'Celebrant for the Horde');
INSERT INTO `creature_text` VALUES (16781, 1, 0, 'I\'d like to see the Alliance try to steal our flame.', 12, 1, 100, 1, 0, 0, 0, 24533, 0, 'Celebrant - Horde speech');
INSERT INTO `creature_text` VALUES (16781, 2, 0, 'The Horde have tried to steal our flames.  I hope we return the favor.', 12, 7, 100, 1, 0, 0, 0, 24531, 0, 'Celebrant - Alliance speech');
INSERT INTO `creature_text` VALUES (16781, 3, 0, 'The Alliance fires burn strong and true!', 12, 7, 100, 1, 0, 0, 0, 24532, 0, 'Celebrant - Alliance speech');
INSERT INTO `creature_text` VALUES (16781, 4, 0, 'Midsummer Fire Festival is a great time to celebrate the summer!', 12, 0, 100, 1, 0, 0, 0, 24535, 0, 'Celebrant -  speech');
INSERT INTO `creature_text` VALUES (16781, 5, 0, 'I love the firework show at the end of Midsummer Firefestival.', 12, 0, 100, 1, 0, 0, 0, 24536, 0, 'Celebrant -  speech');
INSERT INTO `creature_text` VALUES (16790, 0, 0, 'Thank you, $N. It looks like they\'re going to get along just fine.', 12, 0, 100, 0, 0, 0, 0, 13666, 0, 'Falconer Drenna Riverwind');
INSERT INTO `creature_text` VALUES (16793, 0, 0, 'You\'re such a good friend, Twinkle.  You\'d never leave me, would you?', 12, 1, 100, 16, 0, 0, 0, 13586, 0, 'Magistrix Carinda');
INSERT INTO `creature_text` VALUES (16793, 0, 1, 'It\'s just you and me, Twinkle.  Daddy\'s not going to be coming home.', 12, 1, 100, 16, 0, 0, 0, 13587, 0, 'Magistrix Carinda');
INSERT INTO `creature_text` VALUES (16793, 0, 2, 'Did you find something fun to chase around, Twinkle?  I bet you did!', 12, 1, 100, 16, 0, 0, 0, 13588, 0, 'Magistrix Carinda');
INSERT INTO `creature_text` VALUES (16794, 0, 0, '...a potent concoction!', 12, 0, 100, 0, 0, 0, 0, 13678, 0, 'Apothecary Azethen');
INSERT INTO `creature_text` VALUES (16794, 1, 0, 'But where could I ever find a suitable test subject?', 12, 0, 100, 6, 0, 0, 0, 13682, 0, 'Apothecary Azethen');
INSERT INTO `creature_text` VALUES (16794, 2, 0, 'Drink this, you blue brute!  Disobey me and I promise you a slow, painful death!', 12, 0, 100, 25, 0, 0, 0, 13683, 0, 'Apothecary Azethen');
INSERT INTO `creature_text` VALUES (16794, 3, 0, 'Hmmm... looks like I\'m going to need a new test subject.', 12, 0, 100, 274, 0, 0, 0, 13684, 0, 'Apothecary Azethen');
INSERT INTO `creature_text` VALUES (16795, 0, 0, 'I pity you, Forsaken.  Your soul is devoid of light.', 12, 35, 100, 0, 0, 0, 0, 13685, 0, 'Draenei Prisoner');
INSERT INTO `creature_text` VALUES (16807, 0, 0, 'You wish to fight us all at once? This should be amusing!\n', 14, 0, 100, 0, 0, 10262, 0, 15594, 0, 'nethekurse SAY_INTRO');
INSERT INTO `creature_text` VALUES (16807, 1, 0, 'You can have that one, I no longer need him!', 14, 0, 100, 0, 0, 10263, 0, 15569, 0, 'nethekurse PEON_ATTACK_1');
INSERT INTO `creature_text` VALUES (16807, 1, 1, 'Yes, beat him mercilessly! His skull is as thick as an ogre\'s!\n', 14, 0, 100, 0, 0, 10264, 0, 15575, 0, 'nethekurse PEON_ATTACK_2');
INSERT INTO `creature_text` VALUES (16807, 1, 2, 'Don\'t waste your time on that one, he\'s weak!', 14, 0, 100, 0, 0, 10265, 0, 15573, 0, 'nethekurse PEON_ATTACK_3');
INSERT INTO `creature_text` VALUES (16807, 1, 3, 'You want him? Very well, take him!', 14, 0, 100, 0, 0, 10266, 0, 15572, 0, 'nethekurse PEON_ATTACK_4');
INSERT INTO `creature_text` VALUES (16807, 2, 0, 'One pitiful wretch down. Go on, take another one! ', 14, 0, 100, 0, 0, 10267, 0, 15579, 0, 'nethekurse PEON_DIE_1');
INSERT INTO `creature_text` VALUES (16807, 2, 1, 'Ah, what a waste... next!', 14, 0, 100, 0, 0, 10268, 0, 15584, 0, 'nethekurse PEON_DIE_2');
INSERT INTO `creature_text` VALUES (16807, 2, 2, 'I was going to kill him anyway!', 14, 0, 100, 0, 0, 10269, 0, 15582, 0, 'nethekurse PEON_DIE_3');
INSERT INTO `creature_text` VALUES (16807, 2, 3, 'Thank you for saving me the trouble. Now it\'s my turn to have some fun!\n', 14, 0, 100, 0, 0, 10270, 0, 15589, 0, 'nethekurse PEON_DIE_4');
INSERT INTO `creature_text` VALUES (16807, 3, 0, 'Beg for your pitiful life!', 14, 0, 100, 0, 0, 10259, 0, 14130, 0, 'nethekurse SAY_TAUNT_1');
INSERT INTO `creature_text` VALUES (16807, 3, 1, 'Run, coward, run!   ', 14, 0, 100, 0, 0, 10260, 0, 14132, 0, 'nethekurse SAY_TAUNT_2');
INSERT INTO `creature_text` VALUES (16807, 3, 2, 'Your pain amuses me!', 14, 0, 100, 0, 0, 10261, 0, 14148, 0, 'nethekurse SAY_TAUNT_3');
INSERT INTO `creature_text` VALUES (16807, 4, 0, 'I\'m already bored!', 14, 0, 100, 0, 0, 10271, 0, 16864, 0, 'nethekurse SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (16807, 4, 1, 'Come on, show me a real fight!', 14, 0, 100, 0, 0, 10272, 0, 15595, 0, 'nethekurse SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (16807, 4, 2, 'I had more fun torturing the peons!', 14, 0, 100, 0, 0, 10273, 0, 16863, 0, 'nethekurse SAY_AGGRO_3');
INSERT INTO `creature_text` VALUES (16807, 5, 0, 'You lose.', 14, 0, 100, 0, 0, 10274, 0, 16865, 0, 'nethekurse SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (16807, 5, 1, 'Oh, just die!', 14, 0, 100, 0, 0, 10275, 0, 16866, 0, 'nethekurse SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (16807, 6, 0, 'What... a shame.', 14, 0, 100, 0, 0, 10276, 0, 16862, 0, 'nethekurse SAY_DIE');
INSERT INTO `creature_text` VALUES (16808, 0, 0, 'Ours is the TRUE Horde! The only Horde!', 14, 0, 100, 0, 0, 10323, 0, 17614, 0, 'kargath SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (16808, 0, 1, 'I\'ll carve the meat from your bones!', 14, 0, 100, 0, 0, 10324, 0, 17616, 0, 'kargath SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (16808, 0, 2, 'I am called Bladefist for a reason. As you will see.', 14, 0, 100, 0, 0, 10325, 0, 17617, 0, 'kargath SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (16808, 1, 0, 'For the real Horde!', 14, 0, 100, 0, 0, 10326, 0, 17619, 0, 'kargath SAY_SLAY1');
INSERT INTO `creature_text` VALUES (16808, 1, 1, 'I am the ONLY warchief!', 14, 0, 100, 0, 0, 10327, 0, 17620, 0, 'kargath SAY_SLAY2');
INSERT INTO `creature_text` VALUES (16808, 2, 0, 'The true Horde... will prevail.', 14, 0, 100, 0, 0, 10328, 0, 17621, 0, 'kargath SAY_DEATH');
INSERT INTO `creature_text` VALUES (16808, 3, 0, 'The Alliance dares to intrude this far into my fortress?  Bring out the Honor Hold prisoners and call for the executioner!  They\'ll pay with their lives for this trespass!', 14, 0, 100, 0, 0, 0, 0, 13721, 3, 'kargath SAY_CALL_EXECUTIONER_A');
INSERT INTO `creature_text` VALUES (16808, 4, 0, 'Thrall\'s false Horde dares to intrude this far into my fortress?  Bring out the Thrallmar prisoners and call for the executioner!  They\'ll pay with their lives for this trespass!', 14, 0, 100, 0, 0, 0, 0, 13722, 3, 'kargath SAY_CALL_EXECUTIONER_H');
INSERT INTO `creature_text` VALUES (16809, 0, 0, 'This all... your fault!', 14, 0, 100, 0, 0, 10311, 0, 16924, 0, 'omrogg YELL_DIE_L');
INSERT INTO `creature_text` VALUES (16809, 1, 0, 'I... hate... you.', 14, 0, 100, 0, 0, 10322, 0, 16925, 0, 'omrogg YELL_DIE_R');
INSERT INTO `creature_text` VALUES (16809, 2, 0, '%s enrages', 16, 0, 100, 0, 0, 0, 0, 0, 0, 'omrogg EMOTE_ENRAGE');
INSERT INTO `creature_text` VALUES (16812, 0, 0, 'Welcome ladies and gentlemen, to this evening\'s presentation!', 14, 0, 100, 0, 0, 9174, 0, 14122, 0, 'barnes OZ1');
INSERT INTO `creature_text` VALUES (16812, 1, 0, 'Tonight we plumb the depths of the human soul as we join a lost, lonely girl trying desperately--with the help of her loyal companions--to find her way home!', 14, 0, 100, 0, 0, 9338, 0, 15362, 0, 'barnes OZ2');
INSERT INTO `creature_text` VALUES (16812, 2, 0, 'But she is pursued... by a wicked, malevolent crone!', 14, 0, 100, 0, 0, 9339, 0, 15363, 0, 'barnes OZ3');
INSERT INTO `creature_text` VALUES (16812, 3, 0, 'Will she survive? Will she prevail? Only time will tell. And now... on with the show!', 14, 0, 100, 0, 0, 9340, 0, 15364, 0, 'barnes OZ4');
INSERT INTO `creature_text` VALUES (16812, 4, 0, 'Good evening ladies and gentlemen, welcome to this evening\'s presentation!', 14, 0, 100, 0, 0, 9175, 0, 14057, 0, 'barnes HOOD1');
INSERT INTO `creature_text` VALUES (16812, 5, 0, 'Tonight, things are not what they seem... for tonight your eyes may not be trusted!', 14, 0, 100, 0, 0, 9335, 0, 15359, 0, 'barnes HOOD2');
INSERT INTO `creature_text` VALUES (16812, 6, 0, 'Take for instance this quiet elderly woman waiting for a visit from her granddaughter... surely there is nothing to fear from this sweet, gray-haired old lady!', 14, 0, 100, 0, 0, 9336, 0, 15360, 0, 'barnes HOOD3');
INSERT INTO `creature_text` VALUES (16812, 7, 0, 'But don\'t let me pull the wool over your eyes! See for yourself what lies beneath those covers! And now... on with the show!', 14, 0, 100, 0, 0, 9337, 0, 15361, 0, 'barnes HOOD4');
INSERT INTO `creature_text` VALUES (16812, 8, 0, 'Welcome ladies and gentlemen, to this evening\'s presentation!', 14, 0, 100, 0, 0, 9176, 0, 14122, 0, 'barnes RAJ1');
INSERT INTO `creature_text` VALUES (16812, 9, 0, 'Tonight... we explore a tale of forbidden love!', 14, 0, 100, 0, 0, 9341, 0, 15365, 0, 'barnes RAJ2');
INSERT INTO `creature_text` VALUES (16812, 10, 0, 'But beware, for not all love stories end happily, as you may find out. Sometimes, love pricks like a thorn!', 14, 0, 100, 0, 0, 9342, 0, 15366, 0, 'barnes RAJ3');
INSERT INTO `creature_text` VALUES (16812, 11, 0, 'But don\'t take it from me; see for yourself what tragedy lies ahead when the paths of star crossed lovers meet! And now... on with the show!', 14, 0, 100, 0, 0, 9343, 0, 15367, 0, 'barnes RAJ4');
INSERT INTO `creature_text` VALUES (16819, 0, 0, 'Welcome to Honor Hold, $n.  It\'s good to have you.', 12, 0, 100, 66, 0, 0, 0, 17514, 0, 'Commander Danath Trollbane');
INSERT INTO `creature_text` VALUES (16819, 1, 0, 'The feast of corruption is no more! Magtheridon has fallen to the battle prowess of $n and $g his:her; allies! Witness the might of the Alliance! A pit lord commander is destroyed!', 14, 7, 100, 22, 0, 0, 0, 20754, 2, 'Force Commander Danath Trollbane to Player');
INSERT INTO `creature_text` VALUES (16819, 2, 0, 'Hear me brothers and sisters-in-arms! The time of our enemies is at an end! We must strike at the fel orcs that remain! Exterminate them like the vermin that they are! Carry with you the favor of the Sons of Lothar!', 14, 7, 100, 22, 0, 0, 0, 20755, 2, 'Force Commander Danath Trollbane to Player');
INSERT INTO `creature_text` VALUES (16822, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Flightmaster Krill Bitterhue');
INSERT INTO `creature_text` VALUES (16833, 1, 0, 'The orcs hate us, Obadei!  They\'ve killed many of us before!  They deserve death and worse.', 12, 0, 100, 0, 0, 0, 0, 13996, 0, 'Makuru');
INSERT INTO `creature_text` VALUES (16834, 0, 0, 'No!  Not... Sedai!  The orcs must pay!', 12, 0, 100, 1, 0, 0, 0, 13997, 0, 'Anchorite Obadei');
INSERT INTO `creature_text` VALUES (16834, 1, 0, 'What have you done, Makuru?!  These are not our ways!', 12, 0, 100, 0, 0, 0, 0, 13991, 0, 'Anchorite Obadei');
INSERT INTO `creature_text` VALUES (16834, 2, 0, 'I understand how you feel Makuru.  Sedai was my brother after all.  Yet we can\'t disgrace his memory by going against his very ideals.', 12, 0, 100, 0, 0, 0, 0, 13992, 0, 'Makuru');
