INSERT INTO `creature_text` VALUES (16915, 0, 1, 'What?!  You don\'t think I can do it?  I\'ll show you...once I get my parts, that is.', 12, 0, 100, 1, 0, 0, 0, 17802, 0, 'Foreman Razelcraz');
INSERT INTO `creature_text` VALUES (16915, 0, 2, 'It moved!  I swear, I saw it move!', 12, 0, 100, 0, 0, 0, 0, 17804, 0, 'Foreman Razelcraz');
INSERT INTO `creature_text` VALUES (16915, 0, 3, 'Ouch!  That\'s not right.', 12, 0, 100, 0, 0, 0, 0, 17805, 0, 'Foreman Razelcraz');
INSERT INTO `creature_text` VALUES (16915, 0, 4, 'Oh yeah, it\'s real funny isn\'t it?', 12, 0, 100, 1, 0, 0, 0, 17803, 0, 'Foreman Razelcraz');
INSERT INTO `creature_text` VALUES (16916, 0, 0, 'Please!  I must feed on something soon... A mana crystal.... a shard... anything!  The pain is unbearable!', 12, 1, 100, 0, 0, 0, 0, 12835, 0, 'Wretched Captive');
INSERT INTO `creature_text` VALUES (16923, 0, 0, 'I might just have to go hunting for that Wretched beast now that there\'s a bounty on his head.', 12, 1, 100, 0, 0, 0, 0, 12832, 0, 'Novice Ranger');
