INSERT INTO `creature_text` VALUES (16929, 0, 0, '%s defends himself in desperation.', 16, 0, 100, 0, 0, 0, 0, 16744, 0, 'Stonescythe Alpha');
INSERT INTO `creature_text` VALUES (16938, 0, 0, 'Put me out of this misery!', 12, 0, 100, 0, 0, 0, 0, 16471, 0, 'Dreghood Brute');
INSERT INTO `creature_text` VALUES (16938, 0, 1, 'Forgive me... I have no choice.', 12, 0, 100, 0, 0, 0, 0, 16469, 0, 'Dreghood Brute');
INSERT INTO `creature_text` VALUES (16938, 0, 2, 'I will do as you ask, demon.  At least for now.', 12, 0, 100, 0, 0, 0, 0, 16470, 0, 'Dreghood Brute');
