INSERT INTO `creature_text` VALUES (16973, 0, 0, '%s smells a fresh corpse nearby.', 16, 0, 100, 0, 0, 0, 0, 16746, 0, 'Bonestripper Vulture');
INSERT INTO `creature_text` VALUES (16980, 0, 0, 'Intruders have breached the inner sanctum. Hasten the destruction of the green dragon! Leave only bones and sinew for the reanimation!', 14, 0, 0, 0, 0, 17251, 0, 38682, 0, 'The Lich King - Valithria Dreamwalker - SAY_LICH_KING_INTRO');
INSERT INTO `creature_text` VALUES (16980, 1, 0, 'Soon we will eradicate the Alliance and Horde. Then the rest of Azeroth will fall before the might of my army.', 14, 0, 100, 0, 0, 14768, 0, 12988, 3, 'The Lich King SAY_DIALOGUE_SAPPHIRON_LICHKING');
INSERT INTO `creature_text` VALUES (16980, 2, 0, 'Invaders... here?! DESTROY them, Kel\'Thuzad! Naxxramas must not fall!', 14, 0, 100, 0, 0, 14769, 0, 12989, 3, 'The Lich King SAY_DIALOGUE_SAPPHIRON_LICHKING2');
INSERT INTO `creature_text` VALUES (16980, 3, 0, 'Very well. Warriors of the frozen wastes, rise up! I command you to fight, kill and die for your master! Let none survive!', 14, 0, 100, 0, 0, 14770, 0, 12994, 3, 'The Lich King SAY_ANSWER_REQUEST');
INSERT INTO `creature_text` VALUES (16992, 0, 0, '%s begins to grow stronger.', 16, 0, 100, 0, 0, 0, 0, 16646, 0, 'Dreadtusk');
INSERT INTO `creature_text` VALUES (16993, 0, 0, 'Thank you for agreeing to help. Now, let\'s get out of here, $n!', 12, 0, 100, 0, 0, 0, 0, 13289, 0, 'wounded elf SAY_ELF_START');
INSERT INTO `creature_text` VALUES (16993, 1, 0, 'Over there! They\'re following us!', 12, 0, 100, 0, 0, 0, 0, 13255, 0, 'wounded elf SAY_ELF_SUMMON1');
INSERT INTO `creature_text` VALUES (16993, 2, 0, 'Allow me a moment to rest. The journey taxes what little strength I have.', 12, 0, 100, 0, 0, 0, 0, 13290, 0, 'wounded elf SAY_ELF_RESTING');
INSERT INTO `creature_text` VALUES (16993, 3, 0, 'Did you hear something?', 12, 0, 100, 0, 0, 0, 0, 27926, 0, 'wounded elf SAY_ELF_SUMMON2');
INSERT INTO `creature_text` VALUES (16993, 4, 0, 'Falcon Watch, at last! Now, where\'s my... Oh no! My pack, it\'s missing! Where has - ', 12, 0, 100, 0, 0, 0, 0, 13292, 0, 'wounded elf SAY_ELF_COMPLETE');
INSERT INTO `creature_text` VALUES (16993, 5, 0, 'You won\'t keep me from getting to Falcon Watch!', 12, 0, 100, 0, 0, 0, 0, 13659, 0, 'wounded elf SAY_ELF_AGGRO');
INSERT INTO `creature_text` VALUES (17008, 0, 0, 'Be silent! The shattering is soon to come!', 12, 0, 100, 15, 0, 0, 0, 18701, 0, 'Gul\'dan');
INSERT INTO `creature_text` VALUES (17008, 1, 0, 'Bear witness to the undeniable power of our dark master!', 12, 0, 100, 0, 0, 0, 0, 18702, 0, 'Gul\'dan');
INSERT INTO `creature_text` VALUES (17008, 2, 0, 'With his gift, I shall raze this land and reform it!', 12, 0, 100, 0, 0, 0, 0, 18703, 0, 'Gul\'dan');
INSERT INTO `creature_text` VALUES (17008, 3, 0, 'Watch! See the ground shatter before us! Watch as the energy flows! It will feed our armies...', 12, 0, 100, 0, 0, 0, 0, 18704, 0, 'Gul\'dan');
INSERT INTO `creature_text` VALUES (17008, 4, 0, 'We will never be without power! I have secured our future! Bask in my glory!', 12, 0, 100, 0, 0, 0, 0, 18705, 0, 'Gul\'dan');
INSERT INTO `creature_text` VALUES (17008, 5, 0, 'It... is... done...', 12, 0, 100, 0, 0, 0, 0, 18706, 0, 'Gul\'dan');
INSERT INTO `creature_text` VALUES (17015, 0, 0, 'Wake up, Gal\'ressa.', 12, 1, 100, 1, 0, 0, 0, 13325, 0, 'Taleris Dawngazer');
INSERT INTO `creature_text` VALUES (17015, 1, 0, 'You\'ve had enough time to recover from your wounds, Gal\'ressa. The time has come for you to return to the road.', 12, 1, 100, 1, 0, 0, 0, 13328, 0, 'Taleris Dawngazer');
INSERT INTO `creature_text` VALUES (17015, 2, 0, 'You\'ll do no such thing. We simply haven\'t the room to keep you here. Now, kindly be on your way.', 12, 1, 100, 1, 0, 0, 0, 13330, 0, 'Taleris Dawngazer');
INSERT INTO `creature_text` VALUES (17015, 3, 0, 'Very well then, have it your way. When we turn others away, you will be responsible for their suffering. Think about that.', 12, 1, 100, 1, 0, 0, 0, 13332, 0, 'Taleris Dawngazer');
INSERT INTO `creature_text` VALUES (17023, 0, 0, 'Gul\'dan speaks the truth! We should return at once to tell our brothers of the news! Retreat back through the portal!', 14, 0, 100, 0, 0, 0, 0, 19911, 0, 'ancestral wolf SAY_WOLF_WELCOME');
INSERT INTO `creature_text` VALUES (17046, 0, 0, '%s rubs the sleep from her eyes.', 16, 0, 100, 0, 0, 0, 0, 13326, 0, 'Pilgrim Gal\'ressa');
INSERT INTO `creature_text` VALUES (17046, 1, 0, 'What do you want, Taleris?', 12, 1, 100, 1, 0, 0, 0, 13327, 0, 'Pilgrim Gal\'ressa');
INSERT INTO `creature_text` VALUES (17046, 2, 0, 'I\'m not going on alone, Taleris, and that\'s final. I\'m going to wait here for the next group of pilgrims and set out with them when they depart.', 12, 1, 100, 1, 0, 0, 0, 13329, 0, 'Pilgrim Gal\'ressa');
INSERT INTO `creature_text` VALUES (17046, 3, 0, 'By the Sunwell, you\'ve a thick skull! You\'ll not bully me into leaving, Taleris. Now, begone from my sight!', 12, 1, 100, 1, 0, 0, 0, 13331, 0, 'Pilgrim Gal\'ressa');
INSERT INTO `creature_text` VALUES (17058, 0, 0, 'Do not stop!  I promise you a thousand deaths if you even think about putting down that pick!', 12, 0, 100, 0, 0, 0, 0, 16425, 0, 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES (17058, 0, 1, 'Dig faster, wretch! Do not force me to show you the true meaning of pain!', 12, 0, 100, 0, 0, 0, 0, 16424, 0, 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES (17058, 0, 2, 'Faster, scum!  The Master won\'t be pleased if we don\'t find the Ata\'mal crystal he\'s after.', 12, 0, 100, 0, 0, 0, 0, 16421, 0, 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES (17058, 1, 0, 'You dare interfere with the Master\'s endeavors?  You will pay, foolish mortal!', 12, 0, 100, 0, 0, 0, 0, 16438, 0, 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES (17058, 1, 1, 'Slay these intruders, filthy Dreghood!  I promise you neverending pain if you disobey me!', 12, 0, 100, 0, 0, 0, 0, 16439, 0, 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES (17058, 1, 2, 'Go, you little wretches! Show these fools that our Master is not to be trifled with!', 12, 0, 100, 0, 0, 0, 0, 16440, 0, 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES (17058, 1, 3, 'Do not even think of fleeing, Broken wretches!  I promise you a fate worse than death if you don\'t slay these intruders!', 12, 0, 100, 0, 0, 0, 0, 16441, 0, 'Illidari Taskmaster');
INSERT INTO `creature_text` VALUES (17071, 0, 0, 'This is great news!  Your efforts have indeed saved us, $n!', 12, 7, 100, 0, 0, 0, 0, 12846, 0, 'Technician Zhanaa');
INSERT INTO `creature_text` VALUES (17073, 0, 0, '<Sobbing> I hate graveyards.  So many dead.  And my sweet Luhanaa....', 12, 0, 100, 1, 0, 0, 0, 13413, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 1, 'It should have been me!', 12, 7, 100, 1, 0, 0, 0, 13419, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 2, 'Be at peace, my sweet, sweet, Luhanaa.', 12, 7, 100, 1, 0, 0, 0, 13425, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 3, 'I\'ll never forget you, my love.', 12, 7, 100, 1, 0, 0, 0, 13420, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 4, 'If I ever find the ones responsible for the crash...!', 12, 7, 100, 1, 0, 0, 0, 13423, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 5, 'We should never have come along.', 12, 7, 100, 1, 0, 0, 0, 13421, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 6, 'Why, Luhanaa?  Why?!', 12, 7, 100, 6, 0, 0, 0, 13418, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 7, 'I miss you so much!', 12, 7, 100, 1, 0, 0, 0, 13422, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 8, 'You\'ll always be here, with me.', 12, 7, 100, 1, 0, 0, 0, 13424, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 9, '%s weeps softly.', 16, 0, 100, 18, 0, 0, 0, 13414, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 10, '%s bows his head and sighs, clearly exhausted.', 16, 0, 100, 2, 0, 0, 0, 13417, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 11, '%s breaks down into huge, wracking sobs.', 16, 0, 100, 18, 0, 0, 0, 13416, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17073, 0, 12, '%s stares in silence at the grave marker before him.', 16, 0, 100, 0, 0, 0, 0, 13415, 0, 'Draenei Mourner - Random Say');
INSERT INTO `creature_text` VALUES (17077, 0, 0, '%s lifts its head into the air, as if listening for something.', 16, 0, 100, 0, 0, 0, 0, 13430, 0, 'ancestral wolf EMOTE_WOLF_LIFT_HEAD');
INSERT INTO `creature_text` VALUES (17077, 1, 0, '%s lets out a howl that rings across the mountains to the north and motions for you to follow.', 16, 0, 100, 0, 0, 0, 0, 13446, 0, 'ancestral wolf EMOTE_WOLF_HOWL');
INSERT INTO `creature_text` VALUES (17085, 0, 0, 'Avruu\'s magic... it still controls me.  You must fight me, mortal.  It\'s the only way to break the spell!', 12, 0, 100, 0, 0, 0, 0, 13448, 0, 'aeranas SAY_SUMMON');
INSERT INTO `creature_text` VALUES (17085, 1, 0, 'Avruu\'s magic is broken!  I\'m free once again!', 12, 0, 100, 0, 0, 0, 0, 13447, 0, 'aeranas SAY_FREE');
INSERT INTO `creature_text` VALUES (17086, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Enraged Wraith');
INSERT INTO `creature_text` VALUES (17117, 0, 0, '%s groans.', 16, 7, 100, 0, 0, 0, 0, 13601, 0, 'Injured Night Elf Priestess');
INSERT INTO `creature_text` VALUES (17117, 1, 0, '%s sits up.', 16, 7, 100, 0, 0, 0, 0, 13602, 0, 'Injured Night Elf Priestess');
INSERT INTO `creature_text` VALUES (17117, 2, 0, 'Whe... Where am I? What... What is this place?', 12, 7, 100, 6, 0, 0, 0, 13603, 0, 'Injured Night Elf Priestess');
INSERT INTO `creature_text` VALUES (17117, 3, 0, 'BY THE LIGHT OF ELUNE! Eredar! Cursed demons, what have you done to me? I have not come alone, we will fight you as we have in the past! I...', 12, 7, 100, 5, 0, 0, 0, 13604, 0, 'Injured Night Elf Priestess');
INSERT INTO `creature_text` VALUES (17117, 4, 0, '%s falls back into a coma.', 16, 0, 100, 0, 0, 0, 0, 13605, 0, 'Injured Night Elf Priestess');
INSERT INTO `creature_text` VALUES (17119, 0, 0, 'I think I can make it to Brackenwall on my own. They\'ve got the cargo at the top of the tower. Be careful!', 12, 0, 0, 0, 0, 0, 0, 13500, 0, 'Ithania - Escape');
INSERT INTO `creature_text` VALUES (17123, 0, 0, 'Welcome, kind spirit.  What has brought you to us?', 12, 1, 100, 0, 0, 0, 0, 13512, 0, '');
INSERT INTO `creature_text` VALUES (17156, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Tortured Earth Spirit');
INSERT INTO `creature_text` VALUES (17188, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Raving Owlbeast');
INSERT INTO `creature_text` VALUES (17207, 0, 0, 'Who challenges Naias? Puny $r, you are little better than those mindless trolls I have played against each other like so many pieces on a game board!', 12, 0, 100, 0, 0, 0, 0, 13560, 0, 'Naias');
INSERT INTO `creature_text` VALUES (17214, 0, 0, 'Now to apply the ointment to her wounds.', 12, 7, 100, 1, 0, 0, 0, 13596, 0, 'Anchorite Fateema');
INSERT INTO `creature_text` VALUES (17214, 1, 0, 'It appears that the ointment is ineffective. Such a pity...', 12, 7, 100, 1, 0, 0, 0, 13597, 0, 'Anchorite Fateema');
INSERT INTO `creature_text` VALUES (17214, 2, 0, 'Man\'ari... But how... We are not eredar! She must know! If there are others like her, we must find them and explain...', 12, 7, 100, 1, 0, 0, 0, 13608, 0, 'Anchorite Fateema');
INSERT INTO `creature_text` VALUES (17215, 0, 0, 'There may still be a solution. Come here, $n. I will explain what I need.', 12, 7, 100, 1, 0, 0, 0, 13598, 0, 'Daedal0');
INSERT INTO `creature_text` VALUES (17215, 1, 0, 'This should do it...', 12, 7, 100, 0, 0, 0, 0, 13599, 0, 'Daedal');
INSERT INTO `creature_text` VALUES (17215, 2, 0, 'Wake up little creature. Wake up from your slumber... You are safe now.', 12, 7, 100, 0, 0, 0, 0, 13600, 0, 'Daedal');
INSERT INTO `creature_text` VALUES (17225, 0, 0, 'An ancient being awakens in the distance...', 16, 0, 100, 0, 0, 0, 0, 13647, 3, 'Nightbane - EMOTE_SUMMON');
INSERT INTO `creature_text` VALUES (17225, 1, 0, 'What fools! I shall bring a quick end to your suffering!', 14, 0, 100, 0, 0, 0, 0, 13649, 3, 'Nightbane - YELL_AGGRO');
INSERT INTO `creature_text` VALUES (17225, 2, 0, 'Miserable vermin. I shall exterminate you from the air!', 14, 0, 100, 0, 0, 0, 0, 20023, 3, 'Nightbane - YELL_FLY_PHASE');
INSERT INTO `creature_text` VALUES (17225, 3, 0, 'Enough! I shall land and crush you myself!', 14, 0, 100, 0, 0, 0, 0, 13676, 3, 'Nightbane - YELL_LAND_PHASE');
INSERT INTO `creature_text` VALUES (17225, 3, 1, 'Insects! Let me show you my strength up close!', 14, 0, 100, 0, 0, 0, 0, 13677, 3, 'Nightbane - YELL_LAND_PHASE');
INSERT INTO `creature_text` VALUES (17225, 4, 0, '%s takes a deep breath.', 41, 7, 100, 0, 0, 0, 0, 24548, 3, 'Nightbane - EMOTE_BREATH');
INSERT INTO `creature_text` VALUES (17226, 0, 0, 'Ok, let\'s go.  I know just the perfect spot!', 12, 0, 100, 0, 0, 0, 0, 13589, 0, 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES (17226, 1, 0, 'I can\'t wait to try this wine!', 12, 0, 100, 0, 0, 0, 0, 13590, 0, 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES (17226, 2, 0, 'What... is happening... to me?  Get this cat away from me!', 12, 0, 100, 0, 0, 0, 0, 13591, 0, 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES (17226, 3, 0, 'Viera Sunwhisper scurries away, attempting to flee from Twinkle.', 16, 0, 100, 0, 0, 0, 0, 13610, 0, 'Viera Sunwhisper');
INSERT INTO `creature_text` VALUES (17228, 0, 0, 'How can I be expected to get any work done with that thing spreading its heresy?', 12, 0, 100, 0, 0, 0, 0, 14383, 0, 'Draenei Artificer');
INSERT INTO `creature_text` VALUES (17228, 1, 0, '%s sighs disgustedly as he turns back to his work.', 16, 0, 100, 0, 0, 0, 0, 14384, 0, 'Draenei Artificer');
INSERT INTO `creature_text` VALUES (17228, 2, 0, 'Turning to face Tuluun, the %s sneers contemptuously.', 16, 0, 100, 0, 0, 0, 0, 14381, 0, 'Draenei Artificer');
INSERT INTO `creature_text` VALUES (17228, 3, 0, 'We do not need your kind here.', 12, 0, 100, 0, 0, 0, 0, 14382, 0, 'Draenei Artificer');
INSERT INTO `creature_text` VALUES (17230, 0, 0, 'Twinkle looks at Viera Sunwhisper with very hungry eyes.', 16, 0, 100, 0, 0, 0, 0, 13595, 0, 'Twinkle');
INSERT INTO `creature_text` VALUES (17233, 0, 0, 'Thank you for coming here in remembrance of me.  Your efforts in recovering that symbol, while unnecessary, are certainly touching to an old man\'s heart.', 12, 0, 100, 0, 0, 0, 0, 13622, 0, 'anchorite truuen SAY_WP_4');
INSERT INTO `creature_text` VALUES (17233, 1, 0, 'Please, rise my friend.  Keep the Blessing as a symbol of the strength of the Light and how heroes long gone might once again rise in each of us to inspire.', 12, 0, 100, 0, 0, 0, 0, 13623, 0, 'anchorite truuen SAY_WP_5');
INSERT INTO `creature_text` VALUES (17233, 2, 0, 'Thank you my friend for making this possible.  This is a day that I shall never forget!  I think I will stay a while.  Please return to High Priestess MacDonnell at the camp.  I know that she\'ll be keenly interested to know of what has transpired here.', 12, 7, 100, 0, 0, 0, 0, 13624, 0, 'anchorite truuen SAY_WP_6');
INSERT INTO `creature_text` VALUES (17233, 4, 0, 'Why do you do this?  Did I somehow wrong you in life?', 12, 0, 100, 0, 0, 0, 0, 13625, 0, 'Ghost of Uther Lightbringer ');
INSERT INTO `creature_text` VALUES (17233, 5, 0, 'Ah, I see it now in your mind.  This is the work of one of my former students... Mehlar Dawnblade.  It is sad to know that his heart has turned so dark.', 12, 0, 100, 0, 0, 0, 0, 13626, 0, 'Ghost of Uther Lightbringer');
INSERT INTO `creature_text` VALUES (17233, 6, 0, 'Return to him.  Return to Mehlar and tell him that I forgive him and that I understand why he believes what he does.', 12, 0, 100, 0, 0, 0, 0, 13627, 0, 'Ghost of Uther Lightbringer ');
INSERT INTO `creature_text` VALUES (17233, 7, 0, 'I can only hope that he will see the Light and instead turn his energies to restoring once-beautiful Quel\'Thalas.', 12, 0, 100, 0, 0, 0, 0, 13640, 0, 'Ghost of Uther Lightbringer ');
INSERT INTO `creature_text` VALUES (17238, 0, 0, 'Beware!  We are attacked!', 12, 7, 100, 0, 0, 0, 0, 13616, 0, 'anchorite truuen SAY_WP_0');
INSERT INTO `creature_text` VALUES (17238, 1, 0, 'It must be the purity of the Mark of the Lightbringer that is drawing forth the Scourge to us.  We must proceed with caution lest we be overwhelmed!', 12, 7, 100, 0, 0, 0, 0, 13617, 0, 'anchorite truuen SAY_WP_1');
INSERT INTO `creature_text` VALUES (17238, 2, 0, 'This land truly needs to be cleansed by the Light!  Let us continue on to the tomb.  It isn\'t far now.', 12, 7, 100, 0, 0, 0, 0, 13619, 0, 'anchorite truuen SAY_WP_2');
INSERT INTO `creature_text` VALUES (17240, 0, 0, 'Hey, Cookie! What\'s for dinner tonight? And don\'t say chicken!', 12, 7, 100, 22, 0, 0, 0, 13698, 0, 'Admiral Odesyus');
INSERT INTO `creature_text` VALUES (17240, 1, 0, 'Wow! Really?', 12, 7, 100, 5, 0, 0, 0, 13702, 0, 'Admiral Odesyus');
INSERT INTO `creature_text` VALUES (17240, 2, 0, 'I hate you so much, Cookie.', 12, 7, 100, 1, 0, 0, 0, 13705, 0, 'Admiral Odesyus');
INSERT INTO `creature_text` VALUES (17241, 0, 0, 'Adamant, could you please take a look at this tablet and try to decipher the runes?', 12, 7, 100, 1, 0, 0, 0, 13711, 0, 'Priestess Kyleen Il\'dinare');
INSERT INTO `creature_text` VALUES (17241, 1, 0, 'Thank you, Adamant. I think I know just the person for the job.', 12, 7, 100, 2, 0, 0, 0, 13719, 0, 'Priestess Kyleen Il\'dinare');
INSERT INTO `creature_text` VALUES (17242, 0, 0, 'Of course! Anything for you, darling!', 12, 7, 100, 5, 0, 0, 0, 13712, 0, 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES (17242, 1, 0, 'Alright, let me take a look at that tablet.', 12, 7, 100, 1, 0, 0, 0, 13713, 0, 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES (17242, 2, 0, '%s slowly reads the tablet, mouthing the words in their native tongue.', 16, 7, 100, 0, 0, 0, 0, 13714, 0, 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES (17242, 3, 0, 'Naga warlord! Oh dear...', 12, 7, 100, 5, 0, 0, 0, 13715, 0, 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES (17242, 4, 0, 'Well, you aren\'t going to like what I have to tell you, young missy. This tablet is a naga communication. Their leader, Warlord Sriss\'tiz, has ordered his naga to take the rest of the island by force.', 12, 7, 100, 1, 0, 0, 0, 13716, 0, 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES (17242, 5, 0, 'And it looks like he\'s got reinforcements comin\' from Nazjatar!', 12, 7, 100, 5, 0, 0, 0, 13717, 0, 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES (17242, 6, 0, 'We\'ve got to do something about this!', 12, 0, 100, 5, 0, 0, 0, 13718, 0, 'Archaeologist Adamant Ironheart');
INSERT INTO `creature_text` VALUES (17243, 0, 0, 'Yes Master, all goes along as planned.', 12, 7, 100, 0, 0, 0, 0, 14082, 0, 'engineer spark SAY_TEXT');
INSERT INTO `creature_text` VALUES (17243, 1, 0, '%s holds the shell up to his ear.', 16, 7, 100, 0, 0, 0, 0, 14084, 0, 'engineer spark EMOTE_SHELL');
INSERT INTO `creature_text` VALUES (17243, 2, 0, 'And now, I cut you!', 14, 7, 100, 0, 0, 0, 0, 14090, 0, 'engineer spark SAY_ATTACK');
INSERT INTO `creature_text` VALUES (17243, 3, 0, 'What\'s the big idea? You nearly blew my cover, idiot! I told you to put the compass and navigation maps somewhere safe - not out in the open for any fool to discover.', 12, 0, 100, 0, 0, 0, 0, 13777, 0, 'geezle SPARK_SAY_2');
INSERT INTO `creature_text` VALUES (17243, 4, 0, 'The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside.', 12, 0, 100, 0, 0, 0, 0, 13778, 0, 'geezle SPARK_SAY_3');
INSERT INTO `creature_text` VALUES (17243, 5, 0, 'Relax? Do you know what Kael\'thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool.', 12, 0, 100, 0, 0, 0, 0, 13780, 0, 'geezle SPARK_SAY_5');
INSERT INTO `creature_text` VALUES (17243, 6, 0, 'Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you...', 12, 0, 100, 0, 0, 0, 0, 13781, 0, 'geezle SPARK_SAY_6');
INSERT INTO `creature_text` VALUES (17243, 7, 0, '%s picks up the naga flag.', 16, 0, 100, 0, 0, 0, 0, 13775, 0, 'geezle EMOTE_SPARK');
INSERT INTO `creature_text` VALUES (17246, 0, 0, 'Yarr, Admiral. Let me think about it... Arrr... It be comin\' into focus.', 12, 7, 100, 1, 0, 0, 0, 13699, 0, '\"Cookie\" McWeaksauce');
INSERT INTO `creature_text` VALUES (17246, 1, 0, 'It looks like it be succulent pork ribs basted with its own delectable juices and a side o\' Captain Sander\'s Secret Sauce - fer dippin\'.', 12, 7, 100, 1, 0, 0, 0, 13700, 0, '\"Cookie\" McWeaksauce');
INSERT INTO `creature_text` VALUES (17246, 2, 0, 'And fer dessert we be havin\' cheesecake, flown in fresh from Stormwind on the wings o\' angels...', 12, 7, 100, 1, 0, 0, 0, 13701, 0, '\"Cookie\" McWeaksauce');
INSERT INTO `creature_text` VALUES (17246, 3, 0, 'No. Not really...', 12, 7, 100, 274, 0, 0, 0, 13703, 0, '\"Cookie\" McWeaksauce');
INSERT INTO `creature_text` VALUES (17246, 4, 0, 'It be chicken...', 12, 7, 100, 1, 0, 0, 0, 13704, 0, '\"Cookie\" McWeaksauce');
INSERT INTO `creature_text` VALUES (17257, 0, 0, 'Wretched, meddling insects! Release me, and perhaps I will grant you a merciful death!', 14, 0, 100, 0, 0, 10247, 0, 17339, 0, 'Magtheridon - Taunt01');
INSERT INTO `creature_text` VALUES (17257, 0, 1, 'Vermin! Leeches! Take my blood and choke on it!', 14, 0, 100, 0, 0, 10248, 0, 17340, 0, 'Magtheridon - Taunt02');
INSERT INTO `creature_text` VALUES (17257, 0, 2, 'Illidan is an arrogant fool! I will crush him and reclaim Outland as my own!', 14, 0, 100, 0, 0, 10249, 0, 17341, 0, 'Magtheridon - Taunt03');
INSERT INTO `creature_text` VALUES (17257, 0, 3, 'Away, you mindless parasites! My blood is my own!', 14, 0, 100, 0, 0, 10250, 0, 17342, 0, 'Magtheridon - Taunt04');
INSERT INTO `creature_text` VALUES (17257, 0, 4, 'How long do you believe your pathetic sorcery can hold me?', 14, 0, 100, 0, 0, 10251, 0, 17343, 0, 'Magtheridon - Taunt05');
INSERT INTO `creature_text` VALUES (17257, 0, 5, 'My blood will be the end of you!', 14, 0, 100, 0, 0, 10252, 0, 17344, 0, 'Magtheridon - Taunt06');
INSERT INTO `creature_text` VALUES (17257, 1, 0, 'I... am... unleashed!', 14, 0, 100, 15, 0, 10253, 0, 17346, 0, 'Magtheridon - Free');
INSERT INTO `creature_text` VALUES (17257, 2, 0, 'Did you think me weak? Soft? Who is the weak one now?', 14, 0, 100, 0, 0, 10255, 0, 17349, 0, 'Magtheridon - Slay');
INSERT INTO `creature_text` VALUES (17257, 3, 0, 'Not again! Not again...', 14, 0, 100, 0, 0, 10256, 0, 17348, 0, 'Magtheridon - Banished');
INSERT INTO `creature_text` VALUES (17257, 4, 0, 'I will not be taken so easily! Let the walls of this prison tremble... and fall!', 14, 0, 100, 0, 0, 10257, 0, 17336, 0, 'Magtheridon - Collapse');
INSERT INTO `creature_text` VALUES (17257, 5, 0, 'The Legion will consume you all!', 14, 0, 100, 0, 0, 10258, 0, 17347, 0, 'Magtheridon - Death');
INSERT INTO `creature_text` VALUES (17257, 6, 0, '%s\'s bonds begin to weaken!', 16, 0, 100, 0, 0, 0, 0, 13689, 0, 'Magtheridon - Weaken');
INSERT INTO `creature_text` VALUES (17257, 7, 0, '%s is nearly free of his bonds!', 16, 0, 100, 0, 0, 0, 0, 13690, 0, 'Magtheridon - Nearly free');
INSERT INTO `creature_text` VALUES (17257, 8, 0, '%s breaks free!', 16, 0, 100, 0, 0, 0, 0, 13691, 0, 'Magtheridon - Breaks free');
INSERT INTO `creature_text` VALUES (17257, 9, 0, '%s begins to cast Blast Nova!', 41, 0, 100, 0, 0, 0, 0, 18739, 0, 'Magtheridon - Blast Nova');
INSERT INTO `creature_text` VALUES (17259, 0, 0, 'This world is OURS!', 12, 0, 100, 0, 0, 0, 0, 16702, 0, 'Bonechewer Hungerer');
INSERT INTO `creature_text` VALUES (17259, 0, 1, 'We are the true Horde!', 12, 0, 100, 0, 0, 0, 0, 16697, 0, 'Bonechewer Hungerer');
INSERT INTO `creature_text` VALUES (17259, 0, 2, 'The blood is our power! ', 12, 0, 100, 0, 0, 0, 0, 16700, 0, 'Bonechewer Hungerer');
