INSERT INTO `creature_text` VALUES (17271, 0, 6, 'Lok narash!', 12, 0, 100, 0, 0, 0, 0, 16703, 0, 'Bonechewer Destroyer - On Aggro');
INSERT INTO `creature_text` VALUES (17281, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Bonechewer Ripper');
INSERT INTO `creature_text` VALUES (17300, 0, 0, '%s calls for help!', 16, 0, 100, 0, 0, 0, 0, 2541, 0, 'emote Text');
INSERT INTO `creature_text` VALUES (17306, 0, 0, 'Do you smell that?  Fresh meat has somehow breached our citadel.  Be wary of any intruders.', 14, 0, 100, 0, 0, 0, 0, 13999, 0, 'gargolmar SAY_TAUNT');
INSERT INTO `creature_text` VALUES (17306, 1, 0, 'Heal me, quickly!', 14, 0, 100, 0, 0, 10329, 0, 17498, 0, 'gargolmar SAY_HEAL');
INSERT INTO `creature_text` VALUES (17306, 2, 0, 'Back off, pup!', 14, 0, 100, 0, 0, 10330, 0, 17499, 0, 'gargolmar SAY_SURGE');
INSERT INTO `creature_text` VALUES (17306, 3, 0, 'What have we here?', 14, 0, 100, 0, 0, 10331, 0, 17502, 0, 'gargolmar SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (17306, 3, 1, 'This may hurt a little....', 14, 0, 100, 0, 0, 10332, 0, 17503, 0, 'gargolmar SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (17306, 3, 2, 'I\'m going to enjoy this...', 14, 0, 100, 0, 0, 10333, 0, 17504, 0, 'gargolmar SAY_AGGRO_3');
INSERT INTO `creature_text` VALUES (17306, 4, 0, 'Say farewell!', 14, 0, 100, 0, 0, 10334, 0, 17500, 0, 'gargolmar SAY_KILL_1');
INSERT INTO `creature_text` VALUES (17306, 4, 1, 'Much too easy.', 14, 0, 100, 0, 0, 10335, 0, 17501, 0, 'gargolmar SAY_KILL_2');
INSERT INTO `creature_text` VALUES (17306, 5, 0, 'Hahah.. <cough> ..argh!', 14, 0, 100, 0, 0, 10336, 0, 0, 0, 'gargolmar SAY_DIE');
INSERT INTO `creature_text` VALUES (17307, 0, 0, 'You have faced many challenges.... Pity they were all in vain! Soon your people will kneel to MY lord!', 14, 0, 100, 0, 0, 10292, 0, 13981, 0, 'vazruden the herald SAY_INTRO');
INSERT INTO `creature_text` VALUES (17308, 0, 0, 'You dare stand against ME?', 14, 0, 100, 0, 0, 10280, 0, 17634, 0, 'omor SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (17308, 0, 1, 'I will not be defeated!', 14, 0, 100, 0, 0, 10279, 0, 17633, 0, 'omor SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (17308, 0, 2, 'Your insolence will be your death!', 14, 0, 100, 0, 0, 10281, 0, 17635, 0, 'omor SAY_AGGRO_3');
INSERT INTO `creature_text` VALUES (17308, 1, 0, 'Achor she-ki!  Feast, my pet!  Eat your fill!', 14, 0, 100, 0, 0, 10277, 0, 17637, 0, 'omor SAY_SUMMON');
INSERT INTO `creature_text` VALUES (17308, 2, 0, 'A-kreesh!', 14, 0, 100, 0, 0, 10278, 0, 18260, 0, 'omor SAY_CURSE');
INSERT INTO `creature_text` VALUES (17308, 3, 0, 'Die, weakling!', 14, 0, 100, 0, 0, 10282, 0, 17631, 0, 'omor SAY_KILL_1');
INSERT INTO `creature_text` VALUES (17308, 4, 0, 'It is... not over.', 14, 0, 100, 0, 0, 10284, 0, 17630, 0, 'omor SAY_DIE');
INSERT INTO `creature_text` VALUES (17308, 5, 0, 'I am victorious!', 14, 0, 100, 0, 0, 10283, 0, 17632, 0, 'omor SAY_WIPE');
INSERT INTO `creature_text` VALUES (17311, 0, 0, 'Daughter!', 12, 7, 100, 0, 0, 0, 0, 13749, 0, 'Cowlen');
INSERT INTO `creature_text` VALUES (17312, 0, 0, 'Our house is this way, through the thicket.', 12, 7, 100, 0, 0, 0, 0, 13744, 0, 'magwin SAY_START');
INSERT INTO `creature_text` VALUES (17312, 1, 0, 'Help me!', 12, 7, 100, 0, 0, 0, 0, 13770, 0, 'magwin SAY_AGGRO');
INSERT INTO `creature_text` VALUES (17312, 2, 0, 'My poor family. Everything has been destroyed.', 12, 7, 100, 5, 0, 0, 0, 13745, 0, 'magwin SAY_PROGRESS');
INSERT INTO `creature_text` VALUES (17312, 3, 0, 'Father! Father! You\'re alive!', 12, 7, 100, 22, 0, 0, 0, 13746, 0, 'magwin SAY_END1');
INSERT INTO `creature_text` VALUES (17312, 4, 0, 'You can thank $n for getting me back here safely, father.', 12, 7, 100, 5, 0, 0, 0, 13748, 0, 'magwin SAY_END2');
INSERT INTO `creature_text` VALUES (17312, 5, 0, '%s hugs her father.', 16, 7, 100, 0, 0, 0, 0, 13747, 0, 'magwin EMOTE_HUG');
INSERT INTO `creature_text` VALUES (17318, 0, 0, 'What\'s the big idea, Spark? Why\'d you call for this meeting?', 12, 0, 100, 0, 0, 0, 0, 13776, 0, 'geezle GEEZLE_SAY_1');
INSERT INTO `creature_text` VALUES (17318, 1, 0, 'Relax, Spark! I have it all under control. We\'ll strip mine the Exodar right out from under \'em - making both you and I very, very rich in the process.', 12, 0, 100, 0, 0, 0, 0, 13779, 0, 'geezle GEEZLE_SAY_4');
