INSERT INTO `creature_text` VALUES (17381, 0, 1, 'Perhaps I can find a use for you...', 14, 0, 100, 0, 0, 10287, 0, 17680, 0, 'the maker SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (17381, 0, 2, 'Anger...hate... These are tools I can use.', 14, 0, 100, 0, 0, 10288, 0, 17681, 0, 'the maker SAY_AGGRO_3');
