INSERT INTO `creature_text` VALUES (17410, 0, 0, '[Furbolg] Bear witness to the savage acts of our adversaries, $n. They steal our young... our weak... Imprisoned here, they are left to the devices of the Bristlelimb. Set them free, Promised One!', 12, 0, 100, 0, 0, 0, 0, 13950, 0, 'Stillpine Ancestor Vark');
