INSERT INTO `creature_text` VALUES (17417, 0, 0, 'Do not return, draenei scum.  Next time we won\'t spare your life, unarmed or not!', 12, 0, 100, 0, 0, 0, 0, 13986, 0, 'Mag har Escort');
INSERT INTO `creature_text` VALUES (17420, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Shattered Hand Heathen');
INSERT INTO `creature_text` VALUES (17424, 0, 0, 'Now, let\'s see....', 12, 0, 100, 69, 0, 0, 0, 14281, 0, 'Anchorite Paetheus');
INSERT INTO `creature_text` VALUES (17424, 1, 0, 'In the process of translating this, I believe I\'ve learned more about the drunken exploits of a certain \'Andrew Clementine\' than about the journal\'s author.', 12, 0, 100, 1, 0, 0, 0, 14282, 0, 'Anchorite Paetheus');
INSERT INTO `creature_text` VALUES (17424, 2, 0, 'But, no matter. Mister Clementine\'s... err, \'contribution\' notwithstanding, this seems to be the journal of a man named Nolkai. The entries toward the end describe a brutal battle.', 12, 0, 100, 11, 0, 0, 0, 14283, 0, 'Anchorite Paetheus');
INSERT INTO `creature_text` VALUES (17424, 3, 0, 'Take a look at this passage...', 12, 0, 100, 0, 0, 0, 0, 14285, 0, 'Anchorite Paetheus');
INSERT INTO `creature_text` VALUES (17426, 0, 0, 'Morae... Is that you?', 12, 0, 100, 6, 0, 0, 0, 14262, 0, 'Galaen');
INSERT INTO `creature_text` VALUES (17426, 1, 0, 'No... you\'re not Morae, but I sense that you have met. When you see her, tell her that I survived the crash, only to be done in by the infiltrators.', 12, 0, 100, 274, 0, 0, 0, 14263, 0, 'Galaen');
INSERT INTO `creature_text` VALUES (17426, 2, 0, 'Let her know... my last thought was of her...', 12, 0, 100, 1, 0, 0, 0, 14264, 0, 'Galaen');
INSERT INTO `creature_text` VALUES (17433, 0, 0, 'These pictographs tell a story. This figure at the beginning, almost looks like one of the creatures we encountered at Azure Watch...', 12, 0, 100, 0, 0, 0, 0, 14030, 0, 'Vindicator Aalesia');
INSERT INTO `creature_text` VALUES (17433, 1, 0, 'No, this can\'t be... It says this creature willingly became a servant of the Legion. He transforms into a satyr and receives the Legion\'s \"blessing.', 12, 0, 100, 274, 0, 0, 0, 14031, 0, 'Vindicator Aalesia');
INSERT INTO `creature_text` VALUES (17433, 2, 0, 'We must defeat these satyr, $n. We will not suffer the Legion\'s presence in these lands.', 12, 0, 100, 53, 0, 0, 0, 14032, 0, 'Vindicator Aalesia');
INSERT INTO `creature_text` VALUES (17445, 0, 0, 'Stillpine raiders, the time to strike is at hand! Charge!', 14, 0, 100, 0, 0, 0, 0, 14038, 0, 'Stillpine the Younger');
INSERT INTO `creature_text` VALUES (17448, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 10677, 0, 'Chieftain Oomooroo');
INSERT INTO `creature_text` VALUES (17455, 0, 0, 'Intruders!  Hold them off until I can release the warhounds!', 14, 0, 100, 0, 0, 10160, 0, 14000, 0, 'Bonechewer Beastmaster');
INSERT INTO `creature_text` VALUES (17455, 1, 0, 'You\'re too late, now feel the wrath of my warhounds!', 14, 0, 100, 0, 0, 10161, 0, 14001, 0, 'Bonechewer Beastmaster');
