INSERT INTO `creature_text` VALUES (17521, 0, 0, 'The better to own you with!', 14, 0, 100, 0, 0, 9276, 0, 14212, 0, 'wolf SAY_WOLF_AGGRO');
INSERT INTO `creature_text` VALUES (17521, 1, 0, 'Mmmm... delicious.', 14, 0, 100, 0, 0, 9277, 0, 15153, 0, 'SAY_WOLF_SLAY');
INSERT INTO `creature_text` VALUES (17521, 2, 0, 'Run away little girl, run away!', 14, 0, 100, 0, 0, 9278, 0, 14213, 0, 'SAY_WOLF_HOOD');
INSERT INTO `creature_text` VALUES (17528, 0, 0, 'Fools! Weaklings! Tzerak has no need of your assistance!', 14, 0, 100, 0, 0, 0, 0, 14070, 1, 'Tzerak');
INSERT INTO `creature_text` VALUES (17533, 0, 0, 'Wilt thou provoke me? Then have at thee, boy!', 14, 0, 100, 0, 0, 9233, 0, 15075, 0, 'romulo SAY_ROMULO_AGGRO');
INSERT INTO `creature_text` VALUES (17533, 1, 0, 'Thou smilest... upon the stroke that... murders me.', 14, 0, 100, 0, 0, 9235, 0, 15078, 0, 'romulo SAY_ROMULO_DEATH');
INSERT INTO `creature_text` VALUES (17533, 2, 0, 'This day\'s black fate on more days doth depend. This but begins the woe. Others must end.', 14, 0, 100, 0, 0, 9236, 0, 0, 0, 'romulo SAY_ROMULO_ENTER');
INSERT INTO `creature_text` VALUES (17533, 3, 0, 'Thou detestable maw, thou womb of death; I enforce thy rotten jaws to open!', 14, 0, 100, 0, 0, 9237, 0, 15077, 0, 'romulo SAY_ROMULO_RESURRECT');
INSERT INTO `creature_text` VALUES (17533, 4, 0, 'How well my comfort is revived by this!', 14, 0, 100, 0, 0, 9238, 0, 15076, 0, 'romulo SAY_ROMULO_SLAY');
INSERT INTO `creature_text` VALUES (17534, 0, 0, 'What devil art thou, that dost torment me thus?', 14, 0, 100, 0, 0, 9196, 0, 15070, 0, 'julianne SAY_JULIANNE_AGGRO');
INSERT INTO `creature_text` VALUES (17534, 1, 0, 'Where is my lord? Where is my Romulo?', 14, 0, 100, 0, 0, 9199, 0, 0, 0, 'julianne SAY_JULIANNE_ENTER');
INSERT INTO `creature_text` VALUES (17534, 2, 0, 'Romulo, I come! Oh... this do I drink to thee!', 14, 0, 100, 0, 0, 9199, 0, 15072, 0, 'julianne SAY_JULIANNE_DEATH01');
INSERT INTO `creature_text` VALUES (17534, 3, 0, 'O happy dagger! This is thy sheath; there rust, and let me die!', 14, 0, 100, 0, 0, 9310, 0, 15074, 0, 'julianne SAY_JULIANNE_DEATH02');
INSERT INTO `creature_text` VALUES (17534, 4, 0, 'Come, gentle night; and give me back my Romulo!', 14, 0, 100, 0, 0, 9200, 0, 15073, 0, 'julianne SAY_JULIANNE_RESURRECT');
INSERT INTO `creature_text` VALUES (17534, 5, 0, 'Parting is such sweet sorrow.', 14, 0, 100, 0, 0, 9201, 0, 15071, 0, 'julianne SAY_JULIANNE_SLAY');
INSERT INTO `creature_text` VALUES (17535, 0, 0, 'Oh at last, at last I can go home!', 14, 0, 100, 0, 0, 9190, 0, 15068, 0, 'dorothee SAY_DOROTHEE_DEATH');
INSERT INTO `creature_text` VALUES (17535, 1, 0, 'Don\'t let them hurt us Tito! Oh, you won\'t, will you?', 14, 0, 100, 0, 0, 9191, 0, 15066, 0, 'dorothee SAY_DOROTHEE_SUMMON');
INSERT INTO `creature_text` VALUES (17535, 2, 0, 'Tito! Oh Tito, no!', 14, 0, 100, 0, 0, 9192, 0, 15069, 0, 'dorothee SAY_DOROTHEE_TITO_DEATH');
INSERT INTO `creature_text` VALUES (17535, 3, 0, 'Oh Tito, we simply must find a way home! The old wizard could be our only hope! Strawman, Roar, Tinhead, will you - wait... oh golly, look we have visitors!', 14, 0, 100, 0, 0, 9195, 0, 15067, 0, 'dorothee SAY_DOROTHEE_AGGRO');
INSERT INTO `creature_text` VALUES (17536, 0, 0, 'Nazan descends from the sky.', 41, 0, 100, 0, 0, 0, 0, 20472, 0, 'nazan EMOTE');
INSERT INTO `creature_text` VALUES (17537, 0, 0, 'Is there no one left to test me? ', 14, 0, 100, 0, 0, 10293, 0, 17491, 0, 'vazruden SAY_WIPE');
INSERT INTO `creature_text` VALUES (17537, 1, 0, 'Your time is running out!', 14, 0, 100, 0, 0, 10294, 0, 17492, 0, 'vazruden SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (17537, 1, 1, 'You are nothing! I answer a higher call....', 14, 0, 100, 0, 0, 10295, 0, 17493, 0, 'vazruden SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (17537, 1, 2, 'The dark lord laughs at you!', 14, 0, 100, 0, 0, 10296, 0, 17494, 0, 'vazruden SAY_AGGRO_3');
INSERT INTO `creature_text` VALUES (17537, 2, 0, 'It is over! Finished!', 14, 0, 100, 0, 0, 10297, 0, 17495, 0, 'vazruden SAY_KILL_1');
INSERT INTO `creature_text` VALUES (17537, 2, 1, 'Your days are done!', 14, 0, 100, 0, 0, 10298, 0, 17496, 0, 'vazruden SAY_KILL_2');
INSERT INTO `creature_text` VALUES (17537, 3, 0, 'My lord will be... the end of you all!', 14, 0, 100, 0, 0, 10299, 0, 17497, 0, 'vazruden SAY_DIE');
INSERT INTO `creature_text` VALUES (17543, 0, 0, 'Now what should I do with you? I simply can\'t make up my mind.', 14, 0, 100, 0, 0, 9254, 0, 19277, 0, 'strawman SAY_STRAWMAN_AGGRO');
INSERT INTO `creature_text` VALUES (17543, 1, 0, 'Don\'t let them make... a mattress outta\' me.', 14, 0, 100, 0, 0, 9256, 0, 15135, 0, 'strawman SAY_STRAWMAN_DEATH');
INSERT INTO `creature_text` VALUES (17543, 2, 0, 'I guess I\'m not a failure after all!', 14, 0, 100, 0, 0, 9257, 0, 15136, 0, 'strawman SAY_STRAWMAN_SLAY');
INSERT INTO `creature_text` VALUES (17546, 0, 0, 'I\'m not afraid a\' you! Do you wanna\' fight? Huh, do ya\'? C\'mon! I\'ll fight ya\' with both paws behind my back!', 14, 0, 100, 0, 0, 9227, 0, 19276, 0, 'roar SAY_ROAR_AGGRO');
INSERT INTO `creature_text` VALUES (17546, 1, 0, 'You didn\'t have to go and do that!', 14, 0, 100, 0, 0, 9229, 0, 15133, 0, 'roar SAY_ROAR_DEATH');
INSERT INTO `creature_text` VALUES (17546, 2, 0, 'I think I\'m going to go take fourty winks.', 14, 0, 100, 0, 0, 9230, 0, 0, 0, 'roar SAY_ROAR_SLAY');
INSERT INTO `creature_text` VALUES (17547, 0, 0, 'I could really use a heart. Say, can I have yours?', 14, 0, 100, 0, 0, 9268, 0, 19278, 0, 'tinhead SAY_TINHEAD_AGGRO');
INSERT INTO `creature_text` VALUES (17547, 1, 0, 'Back to being an old rust bucket.', 14, 0, 100, 0, 0, 9270, 0, 15155, 0, 'tinhead SAY_TINHEAD_DEATH');
