INSERT INTO `creature_text` VALUES (17831, 3, 0, 'Oh Buddy, you big baby!  Look, it\'s working.  It\'s really working!!  Now all I need to do is grow more of these mushrooms here and the bog lords will likely leave the sporelings alone!', 12, 0, 100, 0, 0, 0, 0, 14610, 0, 'Watcher Leesa\'oh');
INSERT INTO `creature_text` VALUES (17831, 4, 0, 'Oh, thank you, $n!  You\'ve made it possible to save both the sporelings from the bog lords, and the bog lords from our wrath.', 12, 0, 100, 0, 0, 0, 0, 14611, 0, 'Watcher Leesa\'oh');
INSERT INTO `creature_text` VALUES (17835, 0, 0, 'The wizard will fall!', 12, 0, 100, 0, 0, 0, 0, 15164, 0, 'Infinite Assassin');
INSERT INTO `creature_text` VALUES (17835, 0, 1, 'We will not be stopped!', 12, 0, 100, 0, 0, 0, 0, 15166, 0, 'Infinite Assassin');
INSERT INTO `creature_text` VALUES (17835, 0, 2, 'Your efforts... are in vain.', 12, 0, 100, 0, 0, 0, 0, 28034, 0, 'Infinite Assassin');
INSERT INTO `creature_text` VALUES (17839, 0, 0, 'Victory or Death!', 12, 0, 100, 0, 0, 0, 0, 18732, 0, 'Rift Lord');
INSERT INTO `creature_text` VALUES (17839, 0, 1, 'You are running out of time!', 12, 0, 100, 0, 0, 0, 0, 15183, 0, 'Rift Lord');
INSERT INTO `creature_text` VALUES (17839, 0, 2, 'The rift must be protected!', 12, 0, 100, 0, 0, 0, 0, 15182, 0, 'Rift Lord');
INSERT INTO `creature_text` VALUES (17840, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Durnholde Tracking Hound');
INSERT INTO `creature_text` VALUES (17842, 0, 0, 'Your time is almost... up.', 14, 0, 100, 0, 0, 11002, 0, 18248, 0, 'Azgalor - SAY_ONDEATH');
INSERT INTO `creature_text` VALUES (17842, 1, 0, 'Reesh, hokta!', 14, 0, 100, 0, 0, 11001, 0, 18253, 0, 'Azgalor - SAY_ONSLAY1');
INSERT INTO `creature_text` VALUES (17842, 1, 1, 'Don\'t fight it.', 14, 0, 100, 0, 0, 11048, 0, 18255, 0, 'Azgalor - SAY_ONSLAY2');
INSERT INTO `creature_text` VALUES (17842, 1, 2, 'No one is going to save you.', 14, 0, 100, 0, 0, 11047, 0, 18254, 0, 'Azgalor - SAY_ONSLAY3');
INSERT INTO `creature_text` VALUES (17842, 2, 0, 'Just a taste... of what awaits you.', 14, 0, 100, 0, 0, 11046, 0, 18252, 0, 'Azgalor - SAY_DOOM1');
INSERT INTO `creature_text` VALUES (17842, 2, 1, 'Suffer, you despicable insect!', 14, 0, 100, 0, 0, 11000, 0, 18251, 0, 'Azgalor - SAY_DOOM2');
INSERT INTO `creature_text` VALUES (17842, 3, 0, 'Abandon all hope! The Legion has returned to finish what was begun so many years ago. This time, there will be no escape!', 14, 0, 100, 0, 0, 10999, 0, 18247, 0, 'Azgalor - SAY_ONAGGRO');
INSERT INTO `creature_text` VALUES (17843, 0, 0, 'I want all of your trackers out in search of the criminal, Matis the Cruel. I have given trusted agents of the Hand flare guns. Should you or one of your trackers see the flare, it means that an agent has found Matis and needs assistance. And remember, tracker, Matis is to be brought back to me alive.', 12, 7, 100, 1, 0, 0, 0, 14454, 0, 'Vindicator Kuros');
INSERT INTO `creature_text` VALUES (17848, 0, 0, 'You there, fetch water quickly! Get these flames out before they spread to the rest of the keep! Hurry, damn you!', 14, 0, 100, 0, 0, 10428, 0, 15737, 0, 'lieutenant drake SAY_ENTER');
INSERT INTO `creature_text` VALUES (17848, 1, 0, 'I know what you\'re up to, and I mean to put an end to it, permanently!', 14, 0, 100, 0, 0, 10429, 0, 16650, 0, 'lieutenant drake SAY_AGGRO');
INSERT INTO `creature_text` VALUES (17848, 2, 0, 'No more middling for you.', 14, 0, 100, 0, 0, 10432, 0, 0, 0, 'lieutenant drake SAY_SLAY1');
INSERT INTO `creature_text` VALUES (17848, 2, 1, 'You will not interfere!', 14, 0, 100, 0, 0, 10433, 0, 16663, 0, 'lieutenant drake SAY_SLAY2');
INSERT INTO `creature_text` VALUES (17848, 3, 0, 'Time to bleed!', 14, 0, 100, 0, 0, 10430, 0, 16659, 0, 'lieutenant drake SAY_MORTAL');
INSERT INTO `creature_text` VALUES (17848, 4, 0, 'Run, you blasted cowards!', 14, 0, 100, 0, 0, 10431, 0, 16660, 0, 'lieutenant drake SAY_SHOUT');
INSERT INTO `creature_text` VALUES (17848, 5, 0, 'Thrall... must not... go free.', 14, 0, 100, 0, 0, 10434, 0, 16664, 0, 'lieutenant drake SAY_DEATH');
INSERT INTO `creature_text` VALUES (17852, 0, 0, 'I will lay down for no one!', 14, 0, 100, 0, 0, 11031, 0, 18544, 0, 'thrall hyjal ATTACKED 1');
INSERT INTO `creature_text` VALUES (17852, 0, 1, 'Bring the fight to me and pay with your lives!', 14, 0, 100, 0, 0, 11061, 0, 18545, 0, 'thrall hyjal ATTACKED 2');
INSERT INTO `creature_text` VALUES (17852, 1, 0, 'Hold them back! Do not falter!', 14, 0, 100, 0, 0, 11030, 0, 18731, 0, 'thrall hyjal BEGIN');
INSERT INTO `creature_text` VALUES (17852, 2, 0, 'Make ready for another wave! LOK-TAR OGAR!', 14, 0, 100, 0, 0, 11032, 0, 0, 0, 'thrall hyjal INCOMING');
INSERT INTO `creature_text` VALUES (17852, 3, 0, 'Victory or Death!', 14, 0, 100, 0, 0, 11059, 0, 18732, 0, 'thrall hyjal RALLY 1');
INSERT INTO `creature_text` VALUES (17852, 3, 1, 'Do not give an inch of ground!', 14, 0, 100, 0, 0, 11060, 0, 18733, 0, 'thrall hyjal RALLY 2');
INSERT INTO `creature_text` VALUES (17852, 4, 0, 'It is over! Withdraw! We have failed....', 14, 0, 100, 0, 0, 11033, 0, 18734, 0, 'thrall hyjal FAILURE');
INSERT INTO `creature_text` VALUES (17852, 5, 0, 'We have played our part, and done well at that. It is up to the others now.', 14, 0, 100, 0, 0, 11035, 0, 15312, 0, 'thrall hyjal SUCCESS');
INSERT INTO `creature_text` VALUES (17852, 6, 0, 'Uraaa...', 14, 0, 100, 0, 0, 11034, 0, 0, 0, 'thrall hyjal DEATH');
INSERT INTO `creature_text` VALUES (17853, 0, 0, 'We\'ve got you now, Matis the Cruel!', 12, 0, 100, 0, 0, 0, 0, 0, 0, 'Tracker of the Hand');
INSERT INTO `creature_text` VALUES (17853, 1, 0, 'Return to Kuros. I will bring him to Blood Watch.', 12, 7, 100, 0, 0, 0, 0, 14445, 0, 'Tracker of the Hand');
INSERT INTO `creature_text` VALUES (17855, 0, 0, 'Taste blade, mongrel!', 12, 0, 100, 0, 0, 0, 0, 10950, 0, 'Expedition Warden');
INSERT INTO `creature_text` VALUES (17855, 0, 1, 'Please tell me that you didn\'t just do what I think you just did. Please tell me that I\'m not going to have to hurt you...', 12, 0, 100, 0, 0, 0, 0, 10951, 0, 'Expedition Warden');
INSERT INTO `creature_text` VALUES (17855, 0, 2, 'As if we don\'t have enough problems, you go and create more!', 12, 0, 100, 0, 0, 0, 0, 10953, 0, 'Expedition Warden');
INSERT INTO `creature_text` VALUES (17855, 0, 3, 'You dare spill blood on neutral ground? OUT! OUT, I SAY!', 12, 0, 100, 0, 0, 0, 0, 10948, 0, 'Expedition Warden');
INSERT INTO `creature_text` VALUES (17855, 0, 4, 'We don\'t take kindly to miscreants, $r.', 12, 0, 100, 0, 0, 0, 0, 10949, 0, 'Expedition Warden');
INSERT INTO `creature_text` VALUES (17855, 0, 5, 'Get a rope!', 12, 0, 100, 0, 0, 0, 0, 10952, 0, 'Expedition Warden');
INSERT INTO `creature_text` VALUES (17855, 0, 6, 'Believe me when I tell you this: You\'re gonna wish you weren\'t born, sissy!', 12, 0, 100, 0, 0, 0, 0, 10954, 0, 'Expedition Warden');
INSERT INTO `creature_text` VALUES (17855, 0, 7, 'Your actions shame us all, $c. I hurt inside as I beat you senseless.', 12, 0, 100, 0, 0, 0, 0, 10955, 0, 'Expedition Warden');
INSERT INTO `creature_text` VALUES (17862, 0, 0, 'Thrall! You didn\'t really think you would escape, did you?  You and your allies shall answer to Blackmoore... after I\'ve had my fun.', 14, 0, 100, 0, 0, 10406, 0, 15909, 0, 'skarloc SAY_ENTER');
INSERT INTO `creature_text` VALUES (17862, 1, 0, 'You\'re a slave! That\'s all you\'ll ever be!', 14, 0, 100, 0, 0, 10407, 0, 16673, 0, 'skarloc SAY_TAUNT1');
INSERT INTO `creature_text` VALUES (17862, 2, 0, 'I don\'t know what Blackmoore sees in you. For my money you\'re just another ignorant savage!', 14, 0, 100, 0, 0, 10408, 0, 16674, 0, 'skarloc SAY_TAUNT2');
INSERT INTO `creature_text` VALUES (17862, 3, 0, 'Thrall will never be free!', 14, 0, 100, 0, 0, 10409, 0, 16675, 0, 'skarloc SAY_SLAY1');
INSERT INTO `creature_text` VALUES (17862, 3, 1, 'Did you really think you would leave here alive?', 14, 0, 100, 0, 0, 10410, 0, 16676, 0, 'skarloc SAY_SLAY2');
INSERT INTO `creature_text` VALUES (17862, 4, 0, 'Guards... guards!', 14, 0, 100, 0, 0, 10411, 0, 16677, 0, 'skarloc SAY_DEATH');
INSERT INTO `creature_text` VALUES (17875, 0, 0, 'I am at your service, my lord.', 12, 7, 100, 0, 0, 0, 0, 14453, 0, 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES (17875, 1, 0, 'It will be done, my lord.', 12, 7, 100, 1, 0, 0, 0, 14455, 0, 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES (17875, 2, 0, '%s calls forth their bretheren.', 16, 7, 100, 22, 0, 0, 0, 14456, 0, 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES (17875, 3, 0, 'Brothers and sisters, tonight we hunt! A blood elf known as Matis must be brought to justice! The first to find him gets one week\'s vacation aboard the Exodar!', 12, 7, 100, 1, 0, 0, 0, 14457, 0, 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES (17875, 4, 0, 'Onward! The wilds of Bloodmyst await!', 12, 7, 100, 1, 0, 0, 0, 14458, 0, 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES (17876, 0, 0, 'Very well then. Let\'s go!', 14, 0, 100, 0, 0, 10465, 0, 17622, 0, 'thrall hillsbrad SAY_TH_START_EVENT_PART1');
INSERT INTO `creature_text` VALUES (17876, 1, 0, 'As long as we\'re going with a new plan, I may as well pick up a weapon and some armor.', 12, 0, 100, 0, 0, 0, 0, 15893, 0, 'thrall hillsbrad SAY_TH_ARMORY');
INSERT INTO `creature_text` VALUES (17876, 2, 0, 'A rider approaches.', 12, 0, 100, 0, 0, 10466, 0, 15908, 0, 'thrall hillsbrad SAY_TH_SKARLOC_MEET');
INSERT INTO `creature_text` VALUES (17876, 3, 0, 'I\'ll never be chained again!', 14, 0, 100, 0, 0, 10467, 0, 17623, 0, 'thrall hillsbrad SAY_TH_SKARLOC_TAUNT');
INSERT INTO `creature_text` VALUES (17876, 4, 0, 'Very well. Tarren Mill lies just west of here. Since time is of the essence...', 14, 0, 100, 0, 0, 10468, 0, 15916, 0, 'thrall hillsbrad SAY_TH_START_EVENT_PART2');
INSERT INTO `creature_text` VALUES (17876, 5, 0, 'Let\'s ride!', 12, 0, 100, 0, 0, 10469, 0, 15948, 0, 'thrall hillsbrad SAY_TH_MOUNTS_UP');
INSERT INTO `creature_text` VALUES (17876, 6, 0, 'Taretha must be in the inn. Let\'s go.', 12, 0, 100, 0, 0, 0, 0, 15977, 0, 'thrall hillsbrad SAY_TH_CHURCH_END');
INSERT INTO `creature_text` VALUES (17876, 7, 0, 'Taretha! What foul magic is this?', 12, 0, 100, 0, 0, 0, 0, 15978, 0, 'thrall hillsbrad SAY_TH_MEET_TARETHA');
INSERT INTO `creature_text` VALUES (17876, 8, 0, 'Who or what was that?', 14, 0, 100, 0, 0, 10470, 0, 16017, 0, 'thrall hillsbrad SAY_TH_EPOCH_WONDER');
INSERT INTO `creature_text` VALUES (17876, 9, 0, 'No!', 14, 0, 100, 0, 0, 10471, 0, 35155, 0, 'thrall hillsbrad SAY_TH_EPOCH_KILL_TARETHA');
INSERT INTO `creature_text` VALUES (17876, 10, 0, 'Goodbye, Taretha. I will never forget your kindness.', 14, 0, 100, 0, 0, 10472, 0, 16089, 0, 'thrall hillsbrad SAY_TH_EVENT_COMPLETE');
INSERT INTO `creature_text` VALUES (17876, 11, 0, 'Things are looking grim....', 14, 0, 100, 0, 0, 10458, 0, 17610, 0, 'thrall hillsbrad SAY_TH_RANDOM_LOW_HP1');
INSERT INTO `creature_text` VALUES (17876, 11, 1, 'I\'ll fight... to the last!', 14, 0, 100, 0, 0, 10459, 0, 17611, 0, 'thrall hillsbrad SAY_TH_RANDOM_LOW_HP2');
INSERT INTO `creature_text` VALUES (17876, 12, 0, 'Taretha....', 14, 0, 100, 0, 0, 10460, 0, 17612, 0, 'thrall hillsbrad SAY_TH_RANDOM_DIE1');
INSERT INTO `creature_text` VALUES (17876, 12, 1, 'A good day... to die.', 14, 0, 100, 0, 0, 10461, 0, 17613, 0, 'thrall hillsbrad SAY_TH_RANDOM_DIE2');
INSERT INTO `creature_text` VALUES (17876, 13, 0, 'I have earned my freedom!', 14, 0, 100, 0, 0, 10448, 0, 17600, 0, 'thrall hillsbrad SAY_TH_RANDOM_AGGRO1');
INSERT INTO `creature_text` VALUES (17876, 13, 1, 'This day is long overdue! Out of my way!', 14, 0, 100, 0, 0, 10449, 0, 17601, 0, 'thrall hillsbrad SAY_TH_RANDOM_AGGRO2');
INSERT INTO `creature_text` VALUES (17876, 13, 2, 'I am a slave no longer!', 14, 0, 100, 0, 0, 10450, 0, 17602, 0, 'thrall hillsbrad SAY_TH_RANDOM_AGGRO3');
INSERT INTO `creature_text` VALUES (17876, 13, 3, 'Blackmoore has much to answer for!', 14, 0, 100, 0, 0, 10451, 0, 17603, 0, 'thrall hillsbrad SAY_TH_RANDOM_AGGRO4');
INSERT INTO `creature_text` VALUES (17876, 14, 0, 'You have forced my hand!', 14, 0, 100, 0, 0, 10452, 0, 17604, 0, 'thrall hillsbrad SAY_TH_RANDOM_KILL1');
INSERT INTO `creature_text` VALUES (17876, 14, 1, 'It should not have come to this!', 14, 0, 100, 0, 0, 10453, 0, 17605, 0, 'thrall hillsbrad SAY_TH_RANDOM_KILL2');
INSERT INTO `creature_text` VALUES (17876, 14, 2, 'I did not ask for this!', 14, 0, 100, 0, 0, 10454, 0, 17606, 0, 'thrall hillsbrad SAY_TH_RANDOM_KILL3');
INSERT INTO `creature_text` VALUES (17876, 15, 0, 'I am truly in your debt, strangers.', 14, 0, 100, 0, 0, 10455, 0, 17607, 0, 'thrall hillsbrad SAY_TH_LEAVE_COMBAT1');
INSERT INTO `creature_text` VALUES (17876, 15, 1, 'Thank you, strangers. You have given me hope.', 14, 0, 100, 0, 0, 10456, 0, 17608, 0, 'thrall hillsbrad SAY_TH_LEAVE_COMBAT2');
INSERT INTO `creature_text` VALUES (17876, 15, 2, 'I will not waste this chance. I will seek out my destiny.', 14, 0, 100, 0, 0, 10457, 0, 17609, 0, 'thrall hillsbrad SAY_TH_LEAVE_COMBAT3');
INSERT INTO `creature_text` VALUES (17877, 0, 0, 'Fhwoor go now, $n.  Get ark, come back.', 12, 0, 100, 0, 0, 0, 0, 16439, 0, 'Fhwoor');
INSERT INTO `creature_text` VALUES (17877, 1, 0, 'Take moment... get ready.', 12, 0, 100, 0, 0, 0, 0, 16440, 0, 'Fhwoor');
INSERT INTO `creature_text` VALUES (17877, 2, 0, 'We go!', 12, 0, 100, 0, 0, 0, 0, 16441, 0, 'Fhwoor');
INSERT INTO `creature_text` VALUES (17877, 3, 0, '%s lifts the Ark of Ssslith with ease.', 16, 0, 100, 0, 0, 0, 0, 16442, 0, 'Fhwoor');
INSERT INTO `creature_text` VALUES (17877, 4, 0, 'Uh oh...', 12, 0, 100, 0, 0, 0, 0, 16443, 0, 'Fhwoor');
INSERT INTO `creature_text` VALUES (17877, 5, 0, 'Ha ha, squishy naga!', 12, 0, 100, 0, 0, 0, 0, 16444, 0, 'Fhwoor');
INSERT INTO `creature_text` VALUES (17877, 6, 0, '%s places the Ark of Ssslith on the ground.', 16, 0, 100, 0, 0, 0, 0, 16445, 0, 'Fhwoor');
INSERT INTO `creature_text` VALUES (17877, 7, 0, 'Fhwoor do good!', 16, 0, 100, 0, 0, 0, 0, 16446, 0, 'Fhwoor');
INSERT INTO `creature_text` VALUES (17879, 0, 0, 'Why do you aid the Magus? Just think of how many lives could be saved if the portal is never opened, if the resulting wars could be erased....', 14, 0, 100, 0, 0, 10412, 0, 16814, 0, 'chrono lord deja SAY_ENTER');
INSERT INTO `creature_text` VALUES (17879, 1, 0, 'If you will not cease this foolish quest, then you will die!', 14, 0, 100, 0, 0, 10414, 0, 16815, 0, 'chrono lord deja SAY_AGGRO');
INSERT INTO `creature_text` VALUES (17879, 2, 0, 'You have outstayed your welcome, Keeper. Be gone!', 14, 0, 100, 0, 0, 10413, 0, 16817, 0, 'chrono lord deja SAY_BANISH');
INSERT INTO `creature_text` VALUES (17879, 3, 0, 'I told you it was a fool\'s quest!', 14, 0, 100, 0, 0, 10415, 0, 16818, 0, 'chrono lord deja SAY_SLAY1');
INSERT INTO `creature_text` VALUES (17879, 3, 1, 'Leaving so soon?', 14, 0, 100, 0, 0, 10416, 0, 16819, 0, 'chrono lord deja SAY_SLAY2');
INSERT INTO `creature_text` VALUES (17879, 4, 0, 'Time... is on our side.', 14, 0, 100, 0, 0, 10417, 0, 16816, 0, 'chrono lord deja SAY_DEATH');
INSERT INTO `creature_text` VALUES (17880, 0, 0, 'Why do you persist? Surely you can see the futility of it all. It is not too late! You may still leave with your lives...', 14, 0, 100, 0, 0, 10442, 0, 16821, 0, 'temporus SAY_ENTER');
INSERT INTO `creature_text` VALUES (17880, 1, 0, 'So be it... you have been warned.', 14, 0, 100, 0, 0, 10444, 0, 16822, 0, 'temporus SAY_AGGRO');
INSERT INTO `creature_text` VALUES (17880, 2, 0, 'Keeper! The sands of time have run out for you.', 14, 0, 100, 0, 0, 10443, 0, 16824, 0, 'temporus SAY_BANISH');
INSERT INTO `creature_text` VALUES (17880, 3, 0, 'You should have left when you had the chance.', 14, 0, 100, 0, 0, 10445, 0, 16825, 0, 'temporus SAY_SLAY1');
INSERT INTO `creature_text` VALUES (17880, 3, 1, 'Your days are done.', 14, 0, 100, 0, 0, 10446, 0, 16826, 0, 'temporus SAY_SLAY2');
INSERT INTO `creature_text` VALUES (17880, 4, 0, 'My death means... little.', 14, 0, 100, 0, 0, 10447, 0, 16823, 0, 'temporus SAY_DEATH');
INSERT INTO `creature_text` VALUES (17881, 0, 0, 'The time has come to shatter this clockwork universe forever! Let us no longer be slaves of the hourglass! I warn you: those who do not embrace this greater path shall become victims of its passing!', 14, 0, 100, 0, 0, 10400, 0, 16827, 0, 'aeonus SAY_ENTER');
INSERT INTO `creature_text` VALUES (17881, 1, 0, 'Let us see what fate has in store....', 14, 0, 100, 0, 0, 10402, 0, 16828, 0, 'aeonus SAY_AGGRO');
INSERT INTO `creature_text` VALUES (17881, 2, 0, 'Your time is up, slave of the past.', 14, 0, 100, 0, 0, 10401, 0, 16829, 0, 'aeonus SAY_BANISH');
INSERT INTO `creature_text` VALUES (17881, 3, 0, 'One less obstacle in our way!', 14, 0, 100, 0, 0, 10403, 0, 16831, 0, 'aeonus SAY_SLAY1');
INSERT INTO `creature_text` VALUES (17881, 3, 1, 'No one will stop us!', 14, 0, 100, 0, 0, 10404, 0, 16832, 0, 'aeonus SAY_SLAY2');
INSERT INTO `creature_text` VALUES (17881, 4, 0, 'It is only a matter...of time.', 14, 0, 100, 0, 0, 10405, 0, 0, 0, 'aeonus SAY_DEATH');
INSERT INTO `creature_text` VALUES (17881, 5, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 10645, 0, 'aeonus EMOTE_FRENZY');
INSERT INTO `creature_text` VALUES (17888, 0, 0, 'Shaza-kiel!', 14, 0, 100, 0, 0, 11017, 0, 18243, 0, 'Kazrogal - SAY_ONSLAY1');
INSERT INTO `creature_text` VALUES (17888, 0, 1, 'You are nothing!', 14, 0, 100, 0, 0, 11053, 0, 18244, 0, 'Kazrogal - SAY_ONSLAY2');
INSERT INTO `creature_text` VALUES (17888, 0, 2, 'Miserable nuisance!', 14, 0, 100, 0, 0, 11054, 0, 18245, 0, 'Kazrogal - SAY_ONSLAY3');
INSERT INTO `creature_text` VALUES (17888, 1, 0, 'Your death will be a painful one.', 14, 0, 100, 0, 0, 11016, 0, 18241, 0, 'Kazrogal - SAY_MARK1');
INSERT INTO `creature_text` VALUES (17888, 1, 1, 'You... are marked.', 14, 0, 100, 0, 0, 11052, 0, 18242, 0, 'Kazrogal - SAY_MARK2');
INSERT INTO `creature_text` VALUES (17888, 2, 0, 'Cry for mercy! Your meaningless lives will soon be forfeit!', 14, 0, 100, 0, 0, 11015, 0, 18240, 0, 'Kazrogal - SAY_ONAGGRO');
INSERT INTO `creature_text` VALUES (17892, 0, 0, 'We are not finished!', 12, 0, 100, 0, 0, 0, 0, 15174, 0, 'Infinite Chronomancer');
INSERT INTO `creature_text` VALUES (17892, 0, 1, 'Death to the Last Guardian!', 12, 0, 100, 0, 0, 0, 0, 15171, 0, 'Infinite Chronomancer');
INSERT INTO `creature_text` VALUES (17892, 0, 2, 'We will not fail!', 12, 0, 100, 0, 0, 0, 0, 23332, 0, 'Infinite Chronomancer');
INSERT INTO `creature_text` VALUES (17893, 0, 0, 'Hey!  Over here!  Yeah, over here... I\'m in this cage!!!', 14, 0, 100, 0, 0, 0, 0, 14668, 0, 'Naturalist Bite');
INSERT INTO `creature_text` VALUES (17893, 1, 0, 'Uh oh!  It would appear that all of the noise you\'ve been making has attracted some unwanted attention!', 12, 0, 100, 0, 0, 0, 0, 14575, 0, 'Naturalist Bite');
INSERT INTO `creature_text` VALUES (17918, 0, 0, 'Victory will be ours!', 14, 0, 100, 0, 0, 0, 0, 15194, 0, 'Time Keeper');
INSERT INTO `creature_text` VALUES (17918, 0, 1, 'Carry on! Victory at all costs!', 14, 0, 100, 0, 0, 0, 0, 15195, 0, 'Time Keeper');
INSERT INTO `creature_text` VALUES (17918, 0, 2, 'Continue the fight! Do not falter!', 14, 0, 100, 0, 0, 0, 0, 15196, 0, 'Time Keeper');
INSERT INTO `creature_text` VALUES (17941, 0, 0, 'The work must continue!', 14, 0, 100, 0, 0, 10376, 0, 17888, 0, 'Mennu the Betrayer - Aggro');
INSERT INTO `creature_text` VALUES (17941, 0, 1, 'You brought this on yourselves!', 14, 0, 100, 0, 0, 10378, 0, 17889, 0, 'Mennu the Betrayer - Aggro');
INSERT INTO `creature_text` VALUES (17941, 0, 2, 'Don\'t make me kill you!', 14, 0, 100, 0, 0, 10379, 0, 17890, 0, 'Mennu the Betrayer - Aggro');
INSERT INTO `creature_text` VALUES (17941, 1, 0, 'It had to be done....', 14, 0, 100, 0, 0, 10380, 0, 17892, 0, 'Mennu the Betrayer - Kill');
