INSERT INTO `creature_text` VALUES (18174, 0, 2, 'Still, the Blood Knights will endure through this loss. We have no other choice... we must find another source.', 12, 1, 100, 1, 0, 0, 0, 14840, 0, 'Initiate Colin');
INSERT INTO `creature_text` VALUES (18174, 1, 0, 'Who do they think they are?', 12, 1, 100, 1, 0, 0, 0, 14820, 0, 'Initiate Colin');
INSERT INTO `creature_text` VALUES (18174, 1, 1, 'I know what you mean... they whisper about us when they think we can\'t hear them. They\'ll get theirs.', 12, 1, 100, 1, 0, 0, 0, 14821, 0, 'Initiate Colin');
INSERT INTO `creature_text` VALUES (18174, 1, 2, 'Jealous, that\'s what they are. They\'re afraid to dedicate themselves to true power.', 12, 1, 100, 1, 0, 0, 0, 14822, 0, 'Initiate Colin');
INSERT INTO `creature_text` VALUES (18174, 1, 3, 'They simply fear what they cannot begin to understand.', 12, 1, 100, 1, 0, 0, 0, 14823, 0, 'Initiate Colin');
INSERT INTO `creature_text` VALUES (18175, 0, 0, 'Matters are so uncertain now that the chamber below is empty...', 12, 1, 100, 1, 0, 0, 0, 14831, 0, 'Initiate Emeline');
INSERT INTO `creature_text` VALUES (18175, 0, 1, 'It\'s gone. I can\'t believe it\'s gone. Who would have thought we\'d be betrayed like this?', 12, 1, 100, 1, 0, 0, 0, 14833, 0, 'Initiate Emeline');
INSERT INTO `creature_text` VALUES (18175, 1, 0, 'Our neighbors are positively laughable. Have you seen the way they turn up their noses at us?', 12, 1, 100, 1, 0, 0, 0, 14815, 0, 'Initiate Emeline');
INSERT INTO `creature_text` VALUES (18175, 1, 1, 'The rangers certainly have a high opinion of themselves.', 12, 1, 100, 1, 0, 0, 0, 14818, 0, 'Initiate Emeline');
INSERT INTO `creature_text` VALUES (18175, 1, 2, 'I\'m quickly tiring of the haughty attitude I receive from those grubby bowslingers outside.', 12, 1, 100, 1, 0, 0, 0, 14819, 0, 'Initiate Emeline');
INSERT INTO `creature_text` VALUES (18202, 0, 0, 'Where do you think you\'re going? Kill them all!', 12, 0, 100, 0, 0, 0, 0, 15003, 0, 'maghar captive SAY_MAG_MORE_REPLY');
INSERT INTO `creature_text` VALUES (18206, 0, 0, 'Will the pain ever end?', 12, 0, 100, 0, 0, 0, 0, 15108, 0, 'Wastewalker Captive');
INSERT INTO `creature_text` VALUES (18206, 0, 1, 'Help me!  Please help me!', 12, 0, 100, 0, 0, 0, 0, 15103, 0, 'Wastewalker Captive');
INSERT INTO `creature_text` VALUES (18206, 0, 2, 'How can you just leave me here?', 12, 0, 100, 0, 0, 0, 0, 15106, 0, 'Wastewalker Captive');
INSERT INTO `creature_text` VALUES (18209, 0, 0, 'It\'s an ambush! Defend yourself!', 12, 0, 100, 0, 0, 0, 0, 15044, 0, 'Kurenai Captive');
INSERT INTO `creature_text` VALUES (18209, 1, 0, 'More of them coming! Watch out!', 12, 0, 100, 0, 0, 0, 0, 15000, 0, 'Kurenai Captive');
INSERT INTO `creature_text` VALUES (18209, 2, 0, 'Ride the lightning, filth!', 12, 1, 100, 0, 0, 0, 0, 15006, 0, 'Kurenai Captive');
INSERT INTO `creature_text` VALUES (18209, 3, 0, 'We are surrounded!', 14, 0, 100, 0, 0, 0, 0, 15047, 0, 'Kurenai Captive');
INSERT INTO `creature_text` VALUES (18209, 4, 0, 'Up ahead is the road to Telaar. We will split up when we reach the fork as they will surely send more Murkblood after us. Hopefully one of us makes it back to Telaar alive.', 14, 7, 100, 0, 0, 0, 0, 15048, 0, 'Kurenai Captive');
