INSERT INTO `creature_text` VALUES (18210, 4, 0, 'It is best that we split up now, in case they send more after us. Hopefully one of us will make it back to Garrosh. Farewell, stranger.', 12, 1, 100, 0, 0, 0, 0, 15001, 0, 'maghar captive SAY_MAG_COMPLETE');
INSERT INTO `creature_text` VALUES (18211, 0, 0, 'Don\'t let them escape! Kill the strong one first!', 12, 0, 100, 0, 0, 0, 0, 15002, 0, 'maghar captive SAY_MAG_NO_ESCAPE');
INSERT INTO `creature_text` VALUES (18230, 0, 0, 'The magister\'s going to kill me...', 12, 1, 100, 0, 0, 0, 0, 15011, 0, 'Trayanise');
INSERT INTO `creature_text` VALUES (18230, 0, 1, 'No, no, no! Come back here!', 12, 1, 100, 0, 0, 0, 0, 15010, 0, 'Trayanise');
INSERT INTO `creature_text` VALUES (18230, 0, 2, 'Argh. They told me those crystals would work properly!', 12, 1, 100, 0, 0, 0, 0, 15013, 0, 'Trayanise');
