INSERT INTO `creature_text` VALUES (18303, 0, 0, 'My brothers and sisters. Listen to me. We must speak of the survival of our people. We have narrowly survived one calamity, but I fear the next shall destroy us.', 12, 0, 100, 1, 0, 0, 0, 15673, 0, 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES (18303, 1, 0, 'Can you not see what is happening? Open your eyes. It is laid bare for all to see. What is this new alliance? Why have we betrayed our allies of old, only to take up with the enemies of all that live?', 12, 0, 100, 1, 0, 0, 0, 15674, 0, 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES (18303, 2, 0, 'You speak of her as friend? You are a fool. This \"Dark Lady\" is not Sylvanas.', 12, 0, 100, 1, 0, 0, 0, 15680, 0, 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES (18303, 3, 0, 'Blood? And these orcs, these fair weather friends? They will aid us?', 12, 0, 100, 11, 0, 0, 0, 15743, 0, 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES (18303, 4, 0, 'The magisters are the traitors! They have sealed our doom.', 12, 0, 100, 25, 0, 0, 0, 15750, 0, 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES (18303, 5, 0, 'We have not struggled so hard to survive, only to bring about the true destruction.', 12, 0, 100, 1, 0, 0, 0, 15754, 0, 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES (18303, 6, 0, 'If we speak as one, we cannot be ignored!', 12, 0, 100, 1, 0, 0, 0, 15917, 0, 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES (18303, 7, 0, 'We will repay the betrayal of the Alliance! We shall never forget, and never forgive!', 12, 1, 100, 21, 0, 0, 0, 15930, 0, 'Lyria Skystrider');
INSERT INTO `creature_text` VALUES (18311, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Ethereal Crypt Raider');
INSERT INTO `creature_text` VALUES (18322, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Sethekk Ravenguard');
INSERT INTO `creature_text` VALUES (18337, 0, 0, 'That should take care of that. Summon me if you have further need of my services.', 12, 0, 100, 11, 0, 0, 0, 15922, 0, 'Priest Kath\'mar');
INSERT INTO `creature_text` VALUES (18340, 0, 0, 'I will show you not to cross us again!', 12, 0, 100, 0, 0, 0, 0, 15202, 0, 'Steam Pump Overseer');
INSERT INTO `creature_text` VALUES (18340, 0, 1, 'Warlord Kalithresh will hear of this insolence!', 12, 0, 100, 0, 0, 0, 0, 15201, 0, 'Steam Pump Overseer');
INSERT INTO `creature_text` VALUES (18340, 0, 2, 'I\'ll make you pay for that!', 12, 0, 100, 0, 0, 0, 0, 15199, 0, 'Steam Pump Overseer');
INSERT INTO `creature_text` VALUES (18341, 0, 0, 'I will feed... on your soul.', 14, 0, 100, 0, 0, 10561, 0, 17771, 0, 'pandemonius SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (18341, 0, 1, 'So... full of life.', 14, 0, 100, 0, 0, 10562, 0, 17772, 0, 'pandemonius SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (18341, 0, 2, 'Do not... resist.', 14, 0, 100, 0, 0, 10563, 0, 17773, 0, 'pandemonius SAY_AGGRO_3');
