INSERT INTO `creature_text` VALUES (18341, 1, 1, 'More... I must have more.', 14, 0, 100, 0, 0, 10565, 0, 17775, 0, 'pandemonius SAY_KILL_2');
INSERT INTO `creature_text` VALUES (18341, 2, 0, 'To the Void... once more.', 14, 0, 100, 0, 0, 10566, 0, 17776, 0, 'pandemonius SAY_DEATH');
INSERT INTO `creature_text` VALUES (18341, 3, 0, '%s shifts into the void...', 41, 0, 100, 0, 0, 0, 0, 21067, 0, 'pandemonius EMOTE_DARK_SHELL');
INSERT INTO `creature_text` VALUES (18344, 0, 0, 'What is this? You must forgive me, but I was not expecting company. As you can see, we are somewhat preoccupied right now. But no matter. As I am a gracious host, I will tend to you... personally.', 14, 0, 100, 0, 0, 10539, 0, 17784, 0, 'shaffar SAY_INTRO');
