INSERT INTO `creature_text` VALUES (18344, 1, 1, 'An epic battle, how exciting!', 14, 0, 100, 0, 0, 10542, 0, 17781, 0, 'shaffar SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (18344, 1, 2, 'I\'ve longed for a good adventure!', 14, 0, 100, 0, 0, 10543, 0, 17782, 0, 'shaffar SAY_AGGRO_3');
INSERT INTO `creature_text` VALUES (18344, 2, 0, 'It has been... entertaining.', 14, 0, 100, 0, 0, 10544, 0, 17779, 0, 'shaffar SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (18344, 2, 1, 'And now we part company.', 14, 0, 100, 0, 0, 10545, 0, 17778, 0, 'shaffar SAY_SLAY_2');
