INSERT INTO `creature_text` VALUES (18351, 0, 1, 'You taste good with maybe a little salt and pepper.', 12, 0, 100, 0, 0, 0, 0, 15279, 0, 'Lump');
INSERT INTO `creature_text` VALUES (18351, 1, 2, 'OK, OK! Lump give up!', 12, 0, 100, 0, 0, 0, 0, 15282, 0, 'Lump');
INSERT INTO `creature_text` VALUES (18369, 0, 0, 'Thanks, $r! I\'m sure my dad will reward you greatly! Bye!', 12, 0, 100, 0, 0, 0, 0, 15297, 0, 'corki NPC_CORKI_3');
INSERT INTO `creature_text` VALUES (18371, 0, 0, '%s focuses on $n!', 41, 0, 100, 0, 0, 0, 0, 19321, 0, 'Shirrak the Dead Watcher - Emote Focus');
INSERT INTO `creature_text` VALUES (18373, 0, 0, 'You have defiled the resting place of our ancestors. For this offense, there can be but one punishment. It is fitting that you have come to a place of the dead... for you will soon be joining them.', 14, 0, 100, 0, 0, 10509, 0, 0, 0, 'maladaar SAY_INTRO');
