INSERT INTO `creature_text` VALUES (18373, 2, 0, 'You will pay with your life!', 14, 0, 100, 0, 0, 10513, 0, 17647, 0, 'maladaar SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (18373, 2, 1, 'There is no turning back now!', 14, 0, 100, 0, 0, 10514, 0, 17648, 0, 'maladaar SAY_AGGRO_2');
INSERT INTO `creature_text` VALUES (18373, 2, 2, 'Serve your penitence!', 14, 0, 100, 0, 0, 10515, 0, 17649, 0, 'maladaar SAY_AGGRO_3');
