INSERT INTO `creature_text` VALUES (18373, 4, 1, 'Now you\'ll stay... for eternity.', 14, 0, 100, 0, 0, 10517, 0, 17651, 0, 'maladaar SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (18373, 5, 0, 'This is... where I belong.', 14, 0, 100, 0, 0, 10518, 0, 17646, 0, 'maladaar SAY_DEATH');
INSERT INTO `creature_text` VALUES (18384, 0, 0, '%s dons his wolf-fur vestments.', 16, 0, 100, 0, 0, 0, 0, 17039, 0, 'Malukaz');
INSERT INTO `creature_text` VALUES (18384, 1, 0, 'Wolf spirit of the forest, hear me!', 12, 0, 100, 0, 0, 0, 0, 17044, 0, 'Malukaz');
