INSERT INTO `creature_text` VALUES (18385, 0, 0, 'I never thought it would turn out to be mana oil! There\'s going to be plenty of demand for that, minus Grek, of course!', 12, 0, 100, 0, 0, 0, 0, 17073, 0, 'Rakoria');
