INSERT INTO `creature_text` VALUES (18417, 0, 0, 'The information you seek... I cannot give you that freely.  You will have to prove that your enemies are the same as mine.', 12, 0, 100, 25, 0, 0, 0, 19890, 0, 'Altruis the Sufferer to Player');
INSERT INTO `creature_text` VALUES (18417, 1, 0, 'This book would give me unlimited power over my enemies... I would become... unstoppable!', 12, 0, 100, 1, 0, 0, 0, 19614, 0, 'Altruis the Sufferer to Player');
INSERT INTO `creature_text` VALUES (18417, 2, 0, 'No... you must take this from me, $n!  I feel its dark power swaying my will already!  Use it to destroy Varedis.', 12, 0, 100, 274, 0, 0, 0, 19615, 0, 'Altruis the Sufferer to Player');
INSERT INTO `creature_text` VALUES (18428, 0, 0, 'Death to the ogres!', 12, 0, 100, 0, 0, 0, 0, 15388, 0, 'Mag\'har Prisoner');
