INSERT INTO `creature_text` VALUES (18445, 0, 0, 'Thanks, $n! How many times have you saved my life now? Three? That makes us blood in my book!', 12, 0, 100, 0, 0, 0, 0, 15424, 0, 'corki NPC_CORKI');
