INSERT INTO `creature_text` VALUES (18471, 9, 0, 'This is the moment we\'ve all been waiting for! The Warmaul champion is about to make his first showing at the Ring of Blood in weeks! Will $n go down in defeat as easily as the champion\'s other opponents? We shall see...', 14, 0, 100, 0, 0, 0, 0, 15475, 0, 'Gurgthock');
INSERT INTO `creature_text` VALUES (18471, 10, 0, '$n is victorious once more!', 14, 0, 100, 0, 0, 0, 0, 15469, 0, 'Gurgthock');
