INSERT INTO `creature_text` VALUES (18533, 0, 0, 'Skywing, I will free you from your curse over my dead body!', 14, 0, 100, 0, 0, 0, 0, 20283, 0, 'Luanga the Imprisoner');
INSERT INTO `creature_text` VALUES (18548, 0, 0, 'This belongs to Firewing Point.  You\'ll not be putting your grubby hands on it!', 12, 0, 100, 0, 0, 0, 0, 15528, 0, 'Firewing Courier');
INSERT INTO `creature_text` VALUES (18554, 0, 0, 'You come into my house and threaten ME?  I think not!\'', 12, 0, 100, 1, 0, 0, 0, 15542, 0, 'Sharth Voldoun');
INSERT INTO `creature_text` VALUES (18554, 1, 0, 'My master, we are honored by your presence.  How may I serve thee?', 12, 0, 100, 0, 0, 0, 0, 19156, 0, 'Sharth Voldoun');
INSERT INTO `creature_text` VALUES (18554, 2, 0, 'All goes exceedingly well, my lord.  Testing of the smaller prototype at the Cenarion Thicket was a complete success.  The second bomb is being ritually fueled in the courtyard below even as we speak.  And, I\'\'ve sent a courier to Tuurem to bring the rest of the parts to us here.', 12, 0, 100, 1, 0, 0, 0, 19159, 0, 'Sharth Voldoun');
INSERT INTO `creature_text` VALUES (18554, 3, 0, 'You are satisfied?', 12, 0, 100, 6, 0, 0, 0, 19160, 0, 'Sharth Voldoun');
INSERT INTO `creature_text` VALUES (18554, 4, 0, 'I can assure you that we will not fail, my master.  I am personally overseeing every aspect of the construction, and I hold the final codes, myself.  Within a day\'s time, I will have the bomb detonated on those nearby pests.', 12, 0, 100, 2, 0, 0, 0, 19164, 0, 'Sharth Voldoun');
