INSERT INTO `creature_text` VALUES (19534, 0, 0, 'The creatures of this place are strong, wayfarer. You\'ll need ample ammunition if you hope to survive. You\'re in luck - I am well-stocked with fine arrows, bullets, and throwing weapons. The price is reasonable, considering circumstances.', 12, 0, 100, 1, 0, 0, 0, 17002, 0, 'Dealer Digriz');
INSERT INTO `creature_text` VALUES (19535, 0, 0, 'Ahh, another powerful traveler. The nether can be dangerous; you\'ll want the most potent spells at your disposal. I carry the finest reagents, common and exotic. Come, buy - you won\'t be sorry!', 12, 0, 100, 0, 0, 0, 0, 17001, 0, 'Dealer Zijaad');
