INSERT INTO `creature_text` VALUES (19610, 2, 0, 'Show us a trick, Ravandwyr!', 12, 0, 100, 0, 0, 0, 0, 17852, 0, 'Irradiated Worker');
INSERT INTO `creature_text` VALUES (19610, 3, 0, 'Let me outta here! Guys... guys?', 12, 0, 100, 0, 0, 0, 0, 17874, 0, 'Irradiated Worker');
INSERT INTO `creature_text` VALUES (19610, 4, 0, 'Oh man, he got you good!', 12, 0, 100, 0, 0, 0, 0, 17875, 0, 'Irradiated Worker');
INSERT INTO `creature_text` VALUES (19610, 5, 0, 'Remember how you\'re always telling us to chill?', 12, 0, 100, 11, 0, 0, 0, 17876, 0, 'Irradiated Worker');
INSERT INTO `creature_text` VALUES (19610, 6, 0, 'That was the best trick ever! Ok, boys, let\'s go get a drink.', 12, 0, 100, 4, 0, 0, 0, 17853, 0, 'Irradiated Worker');
INSERT INTO `creature_text` VALUES (19610, 7, 0, 'My eyebrows! You singed my eyebrows!', 12, 0, 100, 22, 0, 0, 0, 20285, 0, 'Irradiated Worker');
INSERT INTO `creature_text` VALUES (19610, 8, 0, 'Don\'t worry. It\'s an improvement!', 12, 0, 100, 11, 0, 0, 0, 20286, 0, 'Irradiated Worker');
INSERT INTO `creature_text` VALUES (19616, 0, 0, '%s howls in response to Malukaz\'s call.', 16, 0, 100, 0, 0, 0, 0, 17051, 0, 'Terokkar Wolf Spirit');
INSERT INTO `creature_text` VALUES (19616, 1, 0, '%s expresses its approval.', 16, 0, 100, 0, 0, 0, 0, 17086, 0, 'Terokkar Wolf Spirit');
INSERT INTO `creature_text` VALUES (19622, 0, 0, 'Energy. Power. My people are addicted to it... a dependence made manifest after the Sunwell was destroyed. Welcome to the future. A pity you are too late to stop it. No one can stop me now! Selama ashal\'anore!', 14, 0, 100, 0, 0, 11256, 0, 20195, 0, 'kaelthas SAY_INTRO');
INSERT INTO `creature_text` VALUES (19622, 1, 0, 'Capernian will see to it that your stay here is a short one.', 14, 0, 100, 0, 0, 11257, 0, 19004, 0, 'kaelthas SAY_INTRO_CAPERNIAN');
INSERT INTO `creature_text` VALUES (19622, 2, 0, 'Well done, you have proven worthy to test your skills against my master engineer, Telonicus.', 14, 0, 100, 0, 0, 11258, 0, 19005, 0, 'kaelthas SAY_INTRO_TELONICUS');
INSERT INTO `creature_text` VALUES (19622, 3, 0, 'Let us see how your nerves hold up against the Darkener, Thaladred! ', 14, 0, 100, 0, 0, 11259, 0, 19006, 0, 'kaelthas SAY_INTRO_THALADRED');
INSERT INTO `creature_text` VALUES (19622, 4, 0, 'You have persevered against some of my best advisors... but none can withstand the might of the Blood Hammer. Behold, Lord Sanguinar!', 14, 0, 100, 0, 0, 11260, 0, 19003, 0, 'kaelthas SAY_INTRO_SANGUINAR');
INSERT INTO `creature_text` VALUES (19622, 5, 0, 'As you see, I have many weapons in my arsenal....', 14, 0, 100, 0, 0, 11261, 0, 19053, 0, 'kaelthas SAY_PHASE2_WEAPON');
INSERT INTO `creature_text` VALUES (19622, 6, 0, 'Perhaps I underestimated you. It would be unfair to make you fight all four advisors at once, but... fair treatment was never shown to my people. I\'m just returning the favor.', 14, 0, 100, 0, 0, 11262, 0, 19060, 0, 'kaelthas SAY_PHASE3_ADVANCE');
INSERT INTO `creature_text` VALUES (19622, 7, 0, 'Alas, sometimes one must take matters into one\'s own hands. Balamore shanal!', 14, 0, 100, 0, 0, 11263, 0, 19195, 0, 'kaelthas SAY_PHASE4_INTRO2');
INSERT INTO `creature_text` VALUES (19622, 8, 0, 'I have not come this far to be stopped! The future I have planned will not be jeopardized! Now you will taste true power!!', 14, 0, 100, 0, 0, 11273, 0, 20208, 0, 'kaelthas SAY_PHASE5_NUTS');
INSERT INTO `creature_text` VALUES (19622, 9, 0, 'You will not prevail!', 14, 0, 100, 0, 0, 11270, 0, 20204, 0, 'kaelthas SAY_SLAY1');
INSERT INTO `creature_text` VALUES (19622, 9, 1, 'You gambled. And lost.', 14, 0, 100, 0, 0, 11271, 0, 20205, 0, 'kaelthas SAY_SLAY2');
INSERT INTO `creature_text` VALUES (19622, 9, 2, 'This was child\'s play.', 14, 0, 100, 0, 0, 11272, 0, 20206, 0, 'kaelthas SAY_SLAY3');
INSERT INTO `creature_text` VALUES (19622, 10, 0, 'Obey me!', 14, 0, 100, 0, 0, 11268, 0, 20202, 0, 'kaelthas SAY_MINDCONTROL1');
INSERT INTO `creature_text` VALUES (19622, 10, 1, 'Bow to my will.', 14, 0, 100, 0, 0, 11269, 0, 18259, 0, 'kaelthas SAY_MINDCONTROL2');
INSERT INTO `creature_text` VALUES (19622, 11, 0, 'Let us see how you fare when your world is turned upside down.', 14, 0, 100, 0, 0, 11264, 0, 20198, 0, 'kaelthas SAY_GRAVITYLAPSE1');
INSERT INTO `creature_text` VALUES (19622, 11, 1, 'Having trouble staying grounded?', 14, 0, 100, 0, 0, 11265, 0, 20199, 0, 'kaelthas SAY_GRAVITYLAPSE2');
INSERT INTO `creature_text` VALUES (19622, 12, 0, 'Anar\'anel belore!', 14, 0, 100, 0, 0, 11267, 0, 20201, 0, 'kaelthas SAY_SUMMON_PHOENIX1');
INSERT INTO `creature_text` VALUES (19622, 12, 1, 'By the power of the sun!', 14, 0, 100, 0, 0, 11266, 0, 20200, 0, 'kaelthas SAY_SUMMON_PHOENIX2');
INSERT INTO `creature_text` VALUES (19622, 13, 0, 'For... Quel\'...Thalas!', 14, 0, 100, 0, 0, 11274, 0, 20207, 0, 'kaelthas SAY_DEATH');
INSERT INTO `creature_text` VALUES (19622, 14, 0, '%s begins to cast Pyroblast!', 41, 0, 100, 0, 0, 0, 0, 20775, 0, 'kaelthas EMOTE_PYROBLAST');
INSERT INTO `creature_text` VALUES (19632, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Lykul Stinger');
INSERT INTO `creature_text` VALUES (19635, 0, 0, 'I love the smell of nether in the morning.', 12, 0, 100, 1, 0, 0, 0, 17179, 0, 'Captain Arathyn');
INSERT INTO `creature_text` VALUES (19635, 1, 0, 'It smells like...', 12, 0, 100, 1, 0, 0, 0, 17180, 0, 'Captain Arathyn');
INSERT INTO `creature_text` VALUES (19635, 2, 0, '...victory.', 12, 0, 100, 15, 0, 0, 0, 17181, 0, 'Captain Arathyn');
INSERT INTO `creature_text` VALUES (19664, 0, 0, 'Nothing like a tankard full to wash down one of my magnificent muffins.', 12, 0, 100, 0, 0, 0, 0, 18620, 0, 'Muffin Man Moser');
INSERT INTO `creature_text` VALUES (19664, 1, 0, 'Yea, that hit the spot.  Muffins for sale!  Get your fresh muffins right here!', 12, 0, 100, 7, 0, 0, 0, 18621, 0, 'Muffin Man Moser');
INSERT INTO `creature_text` VALUES (19664, 2, 0, 'I\'ve got muffins right over here.  Come on over!', 12, 0, 100, 0, 0, 0, 0, 18616, 0, 'Muffin Man Moser');
INSERT INTO `creature_text` VALUES (19664, 2, 1, 'Yea, yea, I\'m the muffin man.  You know why?  Because I sell muffins, that\'s why!', 12, 0, 100, 0, 0, 0, 0, 18617, 0, 'Muffin Man Moser');
INSERT INTO `creature_text` VALUES (19666, 0, 0, 'Never underestimate the other ethereal\'s greed!', 12, 0, 100, 0, 0, 0, 0, 17244, 0, 'Shadow Lord Xiraxis');
INSERT INTO `creature_text` VALUES (19667, 0, 0, 'Hail, Gezhe!  Seventeen more orders have been placed in the Hara\'samid Sector.  The markets of heavy veldarite have all but collapsed, as you predicted.', 12, 0, 100, 0, 0, 0, 0, 17184, 0, 'Consortium Nether Runner');
INSERT INTO `creature_text` VALUES (19667, 0, 1, 'Heraazi exports are on the rise, sir.  We\'ve already bribed the appropriate taxation officers and contraband will begin to arrive within days.', 12, 0, 100, 0, 0, 0, 0, 17186, 0, 'Consortium Nether Runner');
INSERT INTO `creature_text` VALUES (19667, 1, 0, 'By your leave, sir.', 12, 0, 100, 0, 0, 0, 0, 17185, 0, 'Consortium Nether Runner');
INSERT INTO `creature_text` VALUES (19685, 0, 0, 'Follow me, stranger.  This won\'t take long.', 12, 0, 100, 0, 0, 0, 0, 17239, 0, 'kservant SAY_KHAD_SERV_0');
INSERT INTO `creature_text` VALUES (19685, 1, 0, 'Shattrath was once the draenei capital of this world. Its name means \"dwelling of light.\"', 15, 0, 100, 0, 0, 0, 0, 17240, 0, 'kservant SAY_KHAD_SERV_1');
INSERT INTO `creature_text` VALUES (19685, 2, 0, 'When the Burning Legion turned the orcs against the draenei, the fiercest battle was fought here. The draenei fought tooth and nail, but in the end the city fell.', 15, 0, 100, 0, 0, 0, 0, 17268, 0, 'kservant SAY_KHAD_SERV_2');
INSERT INTO `creature_text` VALUES (19685, 3, 0, 'The city was left in ruins and darkness... until the Sha\'tar arrived.', 15, 0, 100, 0, 0, 0, 0, 17269, 0, 'kservant SAY_KHAD_SERV_3');
INSERT INTO `creature_text` VALUES (19685, 4, 0, 'Let us go into the Lower City.  I will warn you that as one of the only safe havens in Outland, Shattrath has attracted droves of refugees from all wars, current and past.', 15, 0, 100, 0, 0, 0, 0, 17270, 0, 'kservant SAY_KHAD_SERV_4');
INSERT INTO `creature_text` VALUES (19685, 5, 0, 'The Sha\'tar, or \"born from light\" are the naaru that came to Outland to fight the demons of the Burning Legion.', 15, 0, 100, 0, 0, 0, 0, 17242, 0, 'kservant SAY_KHAD_SERV_5');
INSERT INTO `creature_text` VALUES (19685, 6, 0, 'They were drawn to the ruins of Shattrath City where a small remnant of the draenei priesthood conducted its rites inside a ruined temple on this very spot.\n', 15, 0, 100, 0, 0, 0, 0, 17271, 0, 'kservant SAY_KHAD_SERV_6');
INSERT INTO `creature_text` VALUES (19685, 7, 0, 'The priesthood, known as the Aldor, quickly regained its strength as word spread that the naaru had returned and reconstruction soon began.  The ruined temple is now used as an infirmary for injured refugees.', 15, 0, 100, 0, 0, 0, 0, 17272, 0, 'kservant SAY_KHAD_SERV_7');
INSERT INTO `creature_text` VALUES (19685, 8, 0, 'It wouldn\'t be long, however, before the city came under attack once again.  This time, the attack came from Illidan\'s armies. A large regiment of blood elves had been sent by Illidan\'s ally, Kael\'thas Sunstrider, to lay waste to the city.', 15, 0, 100, 0, 0, 0, 0, 17245, 0, 'kservant SAY_KHAD_SERV_8');
INSERT INTO `creature_text` VALUES (19685, 9, 0, 'As the regiment of blood elves crossed this very bridge, the Aldor\'s exarchs and vindicators lined up to defend the Terrace of Light.  But then the unexpected happened.\n', 15, 0, 100, 0, 0, 0, 0, 17274, 0, 'kservant SAY_KHAD_SERV_9');
INSERT INTO `creature_text` VALUES (19685, 10, 0, 'The blood elves laid down their weapons in front of the city\'s defenders; their leader, a blood elf elder known as Voren\'thal, stormed into the Terrace of Light and demanded to speak to A\'dal.', 15, 0, 100, 0, 0, 0, 0, 17247, 0, 'kservant SAY_KHAD_SERV_10');
INSERT INTO `creature_text` VALUES (19685, 11, 0, 'As the naaru approached him, Voren\'thal kneeled before him and uttered the following words: \"I\'ve seen you in a vision, naaru. My race\'s only hope for survival lies with you. My followers and I are here to serve you.\"', 15, 0, 100, 0, 0, 0, 0, 17275, 0, 'kservant SAY_KHAD_SERV_11');
INSERT INTO `creature_text` VALUES (19685, 12, 0, 'The defection of Voren\'thal and his followers was the largest loss ever incurred by Kael\'s forces. And these weren\'t just any blood elves.  Many of the best and brightest amongst Kael\'s scholars and magisters had been swayed by Voren\'thal\'s influence.\n', 15, 0, 100, 0, 0, 0, 0, 17250, 0, 'kservant SAY_KHAD_SERV_12');
INSERT INTO `creature_text` VALUES (19685, 13, 0, 'The naaru accepted the defectors, who would be known as the Scryers; their dwelling lies in the platform above.  Only those initiated with the Scryers are allowed there.', 15, 0, 100, 0, 0, 0, 0, 17276, 0, 'kservant SAY_KHAD_SERV_13');
INSERT INTO `creature_text` VALUES (19685, 14, 0, 'The Aldor are followers of the Light and forgiveness and redemption are values they understand. However, they found hard to forget the deeds of the blood elves while under Kael\'s command.', 15, 0, 100, 0, 0, 0, 0, 17253, 0, 'kservant SAY_KHAD_SERV_14');
INSERT INTO `creature_text` VALUES (19685, 15, 0, 'Many of the priesthood had been slain by the same magisters who now vowed to serve the naaru.  They were not happy to share the city with their former enemies.', 15, 0, 100, 0, 0, 0, 0, 17277, 0, 'kservant SAY_KHAD_SERV_15');
INSERT INTO `creature_text` VALUES (19685, 16, 0, 'The Aldor\'s most holy temple and its surrounding dwellings lie on the terrace above.  As a holy site, only the initiated are welcome inside.\n', 15, 0, 100, 0, 0, 0, 0, 17278, 0, 'kservant SAY_KHAD_SERV_16');
INSERT INTO `creature_text` VALUES (19685, 17, 0, 'The attacks against Shattrath continued, but the city did not fall, as you can see. On the contrary, the naaru known as Xi\'ri led a successful incursion into Shadowmoon Valley - Illidan\'s doorstep.', 15, 0, 100, 0, 0, 0, 0, 17257, 0, 'kservant SAY_KHAD_SERV_17');
INSERT INTO `creature_text` VALUES (19685, 18, 0, 'There he continues to wage war on Illidan with the assistance of the Aldor and the Scryers.  The two factions have not given up on their old feuds, though.', 15, 0, 100, 0, 0, 0, 0, 17279, 0, 'kservant SAY_KHAD_SERV_18');
INSERT INTO `creature_text` VALUES (19685, 19, 0, 'Such is their animosity that they vie for the honor of being sent to assist the naaru there. Each day, that decision is made here by A\'dal.  The armies gather here to receive A\'dal\'s blessing before heading to Shadowmoon.', 15, 0, 100, 0, 0, 0, 0, 17280, 0, 'kservant SAY_KHAD_SERV_19');
INSERT INTO `creature_text` VALUES (19685, 20, 0, 'Khadgar should be ready to see you again.  Just remember that to serve the Sha\'tar you will most likely have to ally with the Aldor or the Scryers. And seeking the favor of one group will cause the others\' dislike.\n', 15, 0, 100, 0, 0, 0, 0, 17262, 0, 'kservant SAY_KHAD_SERV_20');
INSERT INTO `creature_text` VALUES (19685, 21, 0, 'Good luck stranger, and welcome to Shattrath City.', 15, 0, 100, 0, 0, 0, 0, 17281, 0, 'kservant SAY_KHAD_SERV_21');
INSERT INTO `creature_text` VALUES (19701, 0, 0, 'Death to the interlopers!', 12, 0, 100, 0, 0, 0, 0, 12837, 0, 'Bonechewer Evoker');
INSERT INTO `creature_text` VALUES (19701, 0, 1, 'For Spinebreaker!', 12, 0, 100, 0, 0, 0, 0, 12838, 0, 'Bonechewer Evoker');
INSERT INTO `creature_text` VALUES (19701, 0, 2, 'Your bones will be devoured!', 12, 0, 100, 0, 0, 0, 0, 12839, 0, 'Bonechewer Evoker');
INSERT INTO `creature_text` VALUES (19701, 0, 3, 'Protect the Warchief at all costs!', 12, 0, 100, 0, 0, 0, 0, 12836, 0, 'Bonechewer Evoker');
INSERT INTO `creature_text` VALUES (19710, 0, 0, 'You have approximately five seconds to live.', 14, 0, 100, 0, 0, 11109, 0, 20033, 0, 'ironhand SAY_AGGRO_1');
INSERT INTO `creature_text` VALUES (19710, 1, 0, 'With the precise angle and velocity...', 14, 0, 100, 0, 0, 11112, 0, 18662, 0, 'ironhand SAY_HAMMER_1');
INSERT INTO `creature_text` VALUES (19710, 1, 1, 'Low-tech, yet quite effective.', 14, 0, 100, 0, 0, 11113, 0, 20029, 0, 'ironhand SAY_HAMMER_2');
INSERT INTO `creature_text` VALUES (19710, 2, 0, 'A foregone conclusion.', 14, 0, 100, 0, 0, 11110, 0, 20031, 0, 'ironhand SAY_SLAY_1');
INSERT INTO `creature_text` VALUES (19710, 2, 1, 'The processing will continue as scheduled!', 14, 0, 100, 0, 0, 11111, 0, 20032, 0, 'ironhand SAY_SLAY_2');
INSERT INTO `creature_text` VALUES (19710, 3, 0, 'My calculations did not...', 14, 0, 100, 0, 0, 11114, 0, 20030, 0, 'ironhand SAY_DEATH_1');
INSERT INTO `creature_text` VALUES (19710, 4, 0, '%s raises his hammer menacingly...', 41, 0, 100, 0, 0, 0, 0, 20710, 0, 'ironhand EMOTE_HAMMER');
INSERT INTO `creature_text` VALUES (19720, 0, 0, 'Time to teach you a lesson in manners, little $g boy:girl;!', 12, 0, 100, 0, 0, 0, 0, 17353, 0, '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES (19720, 1, 0, 'Now I\'m gonna give you to the count of \'3\' to get out of here before I sick the dogs on you.', 12, 0, 100, 0, 0, 0, 0, 17354, 0, '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES (19720, 2, 0, '1...', 12, 0, 100, 0, 0, 0, 0, 29261, 0, '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES (19720, 3, 0, '2...', 12, 0, 100, 0, 0, 0, 0, 29260, 0, '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES (19720, 4, 0, 'Time to meet your maker!', 12, 0, 100, 0, 0, 0, 0, 17357, 0, '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES (19720, 5, 0, 'Alright, we give up! Don\'t hurt us!', 12, 0, 100, 0, 0, 0, 0, 17361, 0, '\"Dirty\" Larry');
INSERT INTO `creature_text` VALUES (19737, 0, 0, 'The ship\'s not ready yet.  We still need to calibrate the fuse length to make sure that it doesn\'t burn out and leave you up there alone.', 12, 0, 100, 0, 0, 0, 0, 17414, 0, 'Engineering Crewmember');
INSERT INTO `creature_text` VALUES (19737, 1, 0, 'Essentially, it\'s going to be a long, long time till we are ready to launch.  Maybe you should just head back home to your family.', 12, 0, 100, 0, 0, 0, 0, 17417, 0, 'Engineering Crewmember');
INSERT INTO `creature_text` VALUES (19737, 2, 0, 'Why not? A family can give you strength.', 12, 0, 100, 0, 0, 0, 0, 17420, 0, 'Engineering Crewmember');
INSERT INTO `creature_text` VALUES (19737, 3, 0, 'Couldn\'t you raise them?', 12, 0, 100, 0, 0, 0, 0, 17422, 0, 'Engineering Crewmember');
INSERT INTO `creature_text` VALUES (19737, 4, 0, 'Well, it\'s like I said; it\'s going to be a while before we get this ship ready for flight.  We\'ll keep you informed.', 12, 0, 100, 0, 0, 0, 0, 17424, 0, 'Engineering Crewmember');
INSERT INTO `creature_text` VALUES (19747, 0, 0, 'Make ready the chambers, another ally will soon join our ranks!', 14, 0, 100, 0, 0, 0, 0, 19416, 0, 'Baelmon the Hound-Master');
INSERT INTO `creature_text` VALUES (19747, 1, 0, 'Our ally has arrived!  Clear the way to the materialization chamber!', 14, 0, 100, 0, 0, 0, 0, 19417, 0, 'Baelmon the Hound-Master');
INSERT INTO `creature_text` VALUES (19747, 2, 0, 'Now, proceed to the translocator. Forge Camp Wrath awaits your arrival!', 14, 0, 100, 0, 0, 0, 0, 19418, 0, 'Baelmon the Hound-Master');
INSERT INTO `creature_text` VALUES (19747, 3, 0, 'Prepare yourself for eternal torture, mortal!', 14, 0, 100, 0, 0, 0, 0, 20366, 0, 'Baelmon the Hound-Master');
INSERT INTO `creature_text` VALUES (19747, 3, 1, 'WHAT?! Who dares to disturb the Burning Legion?', 14, 0, 100, 0, 0, 0, 0, 20365, 0, 'Baelmon the Hound-Master');
INSERT INTO `creature_text` VALUES (19747, 3, 2, 'I shall enjoy the smell of the grease from your marrow crackling over the fire!', 14, 0, 100, 0, 0, 0, 0, 20368, 0, 'Baelmon the Hound-Master');
INSERT INTO `creature_text` VALUES (19747, 3, 3, 'Nothing will prevent your doom!', 14, 0, 100, 0, 0, 0, 0, 20367, 0, 'Baelmon the Hound-Master');
INSERT INTO `creature_text` VALUES (19747, 3, 4, 'You DARE to attack me?!', 14, 0, 100, 0, 0, 0, 0, 20369, 0, 'Baelmon the Hound-Master');
INSERT INTO `creature_text` VALUES (19747, 3, 5, 'You will suffer slowly until the end of time for this affront!', 14, 0, 100, 0, 0, 0, 0, 20370, 0, 'Baelmon the Hound-Master');
INSERT INTO `creature_text` VALUES (19747, 4, 0, 'Release the hounds!', 14, 0, 100, 0, 0, 0, 0, 20395, 0, 'Baelmon the Hound-Master');
INSERT INTO `creature_text` VALUES (19763, 0, 0, 'Thank goodness you got here, it was almost dinner time!', 12, 0, 100, 3, 0, 0, 0, 17407, 0, 'Manni');
INSERT INTO `creature_text` VALUES (19764, 0, 0, 'I thought I was a goner for sure.', 12, 0, 100, 4, 0, 0, 0, 17409, 0, 'Moh');
INSERT INTO `creature_text` VALUES (19766, 0, 0, 'I don\'t know which is worse, getting eaten by fel orcs or working for that slave master Razelcraz! Oh well, thanks anyways!', 12, 0, 100, 66, 0, 0, 0, 17410, 0, 'Jakk');
INSERT INTO `creature_text` VALUES (19776, 0, 0, 'All my bags are packed, I\'m ready to go.', 12, 0, 100, 0, 0, 0, 0, 17413, 0, 'Experimental Pilot');
INSERT INTO `creature_text` VALUES (19776, 1, 0, 'You need to do what? All this science, I don\'t understand... Look, this is just my job, five days a week.', 12, 0, 100, 0, 0, 0, 0, 17416, 0, 'Experimental Pilot');
INSERT INTO `creature_text` VALUES (19776, 2, 0, 'I can\'t. I\'m not the man they think I am at home.  Besides, I didn\'t bring them out here.', 12, 0, 100, 0, 0, 0, 0, 17419, 0, 'Experimental Pilot');
INSERT INTO `creature_text` VALUES (19776, 3, 0, 'This isn\'t the kind of place to raise your kids. It\'s cold, and there\'d be no one to raise them.', 12, 0, 100, 0, 0, 0, 0, 17421, 0, 'Experimental Pilot');
INSERT INTO `creature_text` VALUES (19776, 4, 0, 'Oh no, no, no... I\'m a rocket man.', 12, 0, 100, 0, 0, 0, 0, 17423, 0, 'Experimental Pilot');
INSERT INTO `creature_text` VALUES (19827, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Dark Conclave Ravenguard');
INSERT INTO `creature_text` VALUES (19830, 0, 0, 'You cannot be serious!  We are severely understaffed and can barely keep this manaforge functional!', 12, 0, 100, 6, 0, 0, 0, 18166, 0, 'dawnforge SAY_ARCANIST_ARDONIS_1');
INSERT INTO `creature_text` VALUES (19830, 1, 0, 'Yes, my lord.', 12, 0, 100, 0, 0, 0, 0, 19189, 0, 'dawnforge SAY_ARCANIST_ARDONIS_2');
INSERT INTO `creature_text` VALUES (19831, 0, 0, 'We need you to send reinforcements to Manaforge Duro, Ardonis.  This is not a request, it\'s an order.', 12, 0, 100, 1, 0, 0, 0, 18165, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_1');
INSERT INTO `creature_text` VALUES (19831, 1, 0, 'You will do as ordered.  Manaforge Duro has come under heavy attack by mana creatures and the situation is out of control.  Failure to comply will not be tolerated!', 12, 0, 100, 0, 0, 0, 0, 18167, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_2');
INSERT INTO `creature_text` VALUES (19831, 2, 0, 'My lord!', 12, 0, 100, 0, 0, 0, 0, 19186, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_3');
INSERT INTO `creature_text` VALUES (19831, 3, 0, 'Yes, my lord.', 12, 0, 100, 0, 0, 0, 0, 19189, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_4');
INSERT INTO `creature_text` VALUES (19831, 4, 0, 'See to it, Ardonis!', 12, 0, 100, 5, 0, 0, 0, 19190, 0, 'dawnforge SAY_COMMANDER_DAWNFORGE_5');
INSERT INTO `creature_text` VALUES (19847, 0, 0, '%s reads from the book of the dead.', 16, 0, 100, 0, 0, 0, 0, 17446, 0, 'Levixus');
