INSERT INTO `creature_text` VALUES (19932, 0, 0, 'Something very, very disturbing. We\'re starting to see more of them across the timeways.', 12, 0, 100, 1, 0, 0, 0, 17542, 0, 'Andormu');
INSERT INTO `creature_text` VALUES (19932, 1, 0, 'Nozari, I\'m not talking about a greedy wizard looking to improve his station in life by making a small adjustment to the past.', 12, 0, 100, 1, 0, 0, 0, 17544, 0, 'Andormu');
INSERT INTO `creature_text` VALUES (19932, 2, 0, 'These beings aren\'t giving themselves the winning numbers for the Stormwind lottery, dear.', 12, 0, 100, 1, 0, 0, 0, 17545, 0, 'Andormu');
INSERT INTO `creature_text` VALUES (19932, 3, 0, 'My apologies, Nozari. I have been a bit \'on edge\' as of late. These creatures are attempting to alter the timeways.', 12, 0, 100, 1, 0, 0, 0, 17547, 0, 'Andormu');
INSERT INTO `creature_text` VALUES (19932, 4, 0, 'You do recall the last time that this occurred, yes? Let us hope that the master need not be involved.', 12, 0, 100, 1, 0, 0, 0, 17548, 0, 'Andormu');
INSERT INTO `creature_text` VALUES (19932, 5, 0, 'Absolutely not. We must not place ourselves at risk. With the master away, we are all that is in place to keep the stream intact. Others are due to arrive any moment now. Heroes and adventurers from Azeroth...', 12, 0, 100, 1, 0, 0, 0, 17550, 0, 'Andormu');
INSERT INTO `creature_text` VALUES (19933, 0, 0, 'Fascinating. What is it, Andormu?', 12, 0, 100, 1, 0, 0, 0, 17541, 0, 'Nozari');
INSERT INTO `creature_text` VALUES (19933, 1, 0, 'Hrm, are they tampering with the timeways?', 12, 0, 100, 1, 0, 0, 0, 17543, 0, 'Nozari');
INSERT INTO `creature_text` VALUES (19933, 2, 0, 'I hate it when you speak to me in that manner. I am your equal in every way, brother. Remember that the next time you are trapped amidst a temporal vortex, crying for help.', 12, 0, 100, 1, 0, 0, 0, 17546, 0, 'Nozari');
INSERT INTO `creature_text` VALUES (19933, 3, 0, 'Shall we?', 12, 0, 100, 1, 0, 0, 0, 17549, 0, 'Nozari');
