INSERT INTO `creature_text` VALUES (20474, 0, 0, 'Protect the conduit! Stop the intruders!', 12, 0, 100, 0, 0, 0, 0, 18441, 0, 'Ethereum Nexus-Stalker - Protect Conduct');
