INSERT INTO `creature_text` VALUES (20482, 1, 0, 'Protectorate transmission complete.', 12, 0, 100, 0, 0, 0, 0, 18191, 0, 'Ameer - Quest rewarded');
INSERT INTO `creature_text` VALUES (20501, 0, 0, '%s attempts to split in two.', 16, 0, 100, 0, 0, 0, 0, 18912, 0, 'Seeping Sludge');
INSERT INTO `creature_text` VALUES (20501, 1, 0, '%s breaks down into globules!', 16, 0, 100, 0, 0, 0, 0, 18479, 0, 'Seeping Sludge');
INSERT INTO `creature_text` VALUES (20551, 0, 0, 'I hope this damned thing works. *Araxes pounds on the portable transponder.*', 12, 0, 100, 1, 0, 0, 0, 18256, 0, 'Agent Araxes');
INSERT INTO `creature_text` VALUES (20551, 1, 0, 'I\'m here, commander.', 12, 0, 100, 1, 0, 0, 0, 18267, 0, 'Agent Araxes');
INSERT INTO `creature_text` VALUES (20551, 2, 0, 'We\'ve located the mark, commander. Ya-six and I have been separated. Requesting backup.', 12, 0, 100, 1, 0, 0, 0, 18268, 0, 'Agent Araxes');
INSERT INTO `creature_text` VALUES (20553, 0, 0, 'Araxes, come in... Are you there Araxes? Visibility is nil. Warp storms blocking us.', 12, 0, 100, 1, 0, 0, 0, 18249, 0, 'Image of Commander Ameer');
INSERT INTO `creature_text` VALUES (20553, 1, 0, 'Copy. Status report, soldier.', 12, 0, 100, 1, 0, 0, 0, 18250, 0, 'Image of Commander Ameer');
INSERT INTO `creature_text` VALUES (20553, 2, 0, 'Copy that, Araxes. Backup is on the way. Hold your position. I repeat, hold your position.', 12, 0, 100, 1, 0, 0, 0, 18269, 0, 'Image of Commander Ameer');
INSERT INTO `creature_text` VALUES (20553, 3, 0, 'Ameer, over and out.', 12, 0, 100, 1, 0, 0, 0, 18270, 0, 'Image of Commander Ameer');
INSERT INTO `creature_text` VALUES (20555, 0, 0, 'Who dares place this affront upon the altar of Goc?', 14, 0, 100, 53, 0, 0, 0, 20381, 0, 'Goc');
INSERT INTO `creature_text` VALUES (20555, 1, 0, 'Who dares rouse Goc from his restful slumber?', 14, 0, 100, 53, 0, 0, 0, 20317, 0, 'Goc');
INSERT INTO `creature_text` VALUES (20555, 2, 0, 'Your name is as insignificant to me as the names of the thousands who have died under the might of Goc. I will crush you and $N', 14, 0, 100, 53, 0, 0, 0, 20321, 0, 'Goc');
INSERT INTO `creature_text` VALUES (20564, 0, 0, 'Araxes! Send help! I\'m pinned down in the mines. I... I\'m not sure where I am exactly but I had the presence of mind to drop tracers on the ground behind me.', 12, 0, 100, 1, 0, 0, 0, 18281, 0, 'Image of Agent Ya-six');
INSERT INTO `creature_text` VALUES (20564, 1, 0, 'I can\'t follow the tracers back out, too many flesh beasts in the way. Send someone in... Follow the tracers... Ya-six, out.', 12, 0, 100, 1, 0, 0, 0, 18282, 0, 'Image of Agent Ya-six');
INSERT INTO `creature_text` VALUES (20600, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Maggoc');
INSERT INTO `creature_text` VALUES (20610, 0, 0, '%s calls for the aid of a nearby male!', 16, 0, 100, 0, 0, 0, 0, 19207, 0, 'Talbuk Doe');
INSERT INTO `creature_text` VALUES (20610, 1, 0, '%s seems to have weakened.', 16, 0, 100, 0, 0, 0, 0, 18606, 0, 'Talbuk Doe');
INSERT INTO `creature_text` VALUES (20677, 0, 0, 'You\'ve freed me!  The winds speak to my people once again and grant us their strength.  I thank you, stranger.', 12, 0, 100, 1, 0, 0, 0, 18358, 0, 'Morod the Windstirrer');
INSERT INTO `creature_text` VALUES (20678, 0, 0, 'I am free!  I will ask the spirit of fire to return to us so that my people may have the courage to fight their masters!  I thank you, $r.', 12, 0, 100, 15, 0, 0, 0, 18356, 0, 'Akoru the Firecaller');
INSERT INTO `creature_text` VALUES (20679, 0, 0, 'I\'m free!  The spirit of water returns to my people.  It will bring us the wisdom we need to survive in this harsh land.  I am in your debt, $n.', 12, 0, 100, 1, 0, 0, 0, 18357, 0, 'Aylaan the Waterwaker');
INSERT INTO `creature_text` VALUES (20680, 0, 0, 'That Broken worm gave you that staff, didn\'t he?  Did he also tell you he\'s the one that sold out his tribe?  No matter, you will both pay for this!', 14, 0, 100, 0, 0, 0, 0, 18349, 0, 'Arzeth the Powerless');
INSERT INTO `creature_text` VALUES (20747, 0, 0, '%s begins to metamorphose...', 16, 0, 100, 0, 0, 0, 0, 18589, 0, 'Silkwing Larva');
INSERT INTO `creature_text` VALUES (20756, 0, 0, 'Stupid puny thing.  Me smash!', 12, 0, 100, 0, 0, 0, 0, 19892, 0, 'Bladespire Chef on Aggro Text');
INSERT INTO `creature_text` VALUES (20756, 0, 1, 'Me mad. You get smash in face!', 12, 0, 100, 0, 0, 0, 0, 19893, 0, 'Bladespire Chef on Aggro Text');
INSERT INTO `creature_text` VALUES (20756, 0, 2, 'Raaar!!! Me smash $r!', 12, 0, 100, 0, 0, 0, 0, 1927, 0, 'Bladespire Chef on Aggro Text');
INSERT INTO `creature_text` VALUES (20756, 1, 0, 'Puny $r... you were a... horrible $g king : queen;.', 12, 0, 100, 0, 0, 0, 0, 21050, 0, 'Bladespire Chef on death Quest 11000 complete');
INSERT INTO `creature_text` VALUES (20756, 1, 1, 'Agh... me am...dead.', 12, 0, 100, 0, 0, 0, 0, 21032, 0, 'Bladespire Chef on death Quest 11000 complete');
INSERT INTO `creature_text` VALUES (20756, 1, 2, 'Me go to... Ogri\'la.', 12, 0, 100, 0, 0, 0, 0, 21033, 0, 'Bladespire Chef on death Quest 11000 complete');
INSERT INTO `creature_text` VALUES (20756, 1, 3, '$G King : Queen; think... there really is... an Ogri\'la?', 12, 0, 100, 0, 0, 0, 0, 21049, 0, 'Bladespire Chef on death Quest 11000 complete');
INSERT INTO `creature_text` VALUES (20756, 1, 4, 'Me honored... $g king : queen; kill me.', 12, 0, 100, 0, 0, 0, 0, 21034, 0, 'Bladespire Chef on death Quest 11000 complete');
INSERT INTO `creature_text` VALUES (20756, 1, 5, '$G King : Queen; $n, me die now.', 12, 0, 100, 0, 0, 0, 0, 21035, 0, 'Bladespire Chef on death Quest 11000 complete');
INSERT INTO `creature_text` VALUES (20762, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Gur\'zil');
INSERT INTO `creature_text` VALUES (20763, 0, 0, 'We\'re not too far from the Protectorate Watch Post, $n. This way!', 12, 0, 100, 0, 0, 0, 0, 18566, 0, 'Captured Protectorate Vanguard to Player');
INSERT INTO `creature_text` VALUES (20763, 1, 0, 'Commander! This fleshling rescued me!', 12, 0, 100, 0, 0, 0, 0, 18567, 0, 'Captured Protectorate Vanguard to Player');
