INSERT INTO `creature_text` VALUES (20802, 0, 0, 'Let\'s do this... Just keep me covered and I\'ll deliver the package.', 12, 0, 100, 0, 0, 0, 0, 18432, 0, 'Protectorate Demolitionist - Comienza escort');
INSERT INTO `creature_text` VALUES (20802, 1, 0, 'By the second sun of K\'aresh, look at this place! I can only imagine what Salhadaar is planning. Come on, let\'s keep going.', 12, 0, 100, 0, 0, 0, 0, 18433, 0, 'Protectorate Demolitionist - Waypoint 1');
INSERT INTO `creature_text` VALUES (20802, 2, 0, 'Look there, fleshling! Salhadaar\'s conduits! He\'s keeping well fed...', 12, 0, 100, 0, 0, 0, 0, 18435, 0, 'Protectorate Demolitionist - Waypoint 4');
INSERT INTO `creature_text` VALUES (20802, 3, 0, 'Alright, keep me protected while I plant this disruptor. This shouldn\'t take very long..', 12, 0, 100, 0, 0, 0, 0, 18436, 0, 'Protectorate Demolitionist - Waypoint 6');
INSERT INTO `creature_text` VALUES (20802, 4, 0, 'Done! Back up! Back up!', 12, 0, 100, 0, 0, 0, 0, 18437, 0, 'Protectorate Demolitionist - Disruptor');
INSERT INTO `creature_text` VALUES (20802, 5, 0, 'Looks like my work here is done. Report to the holo-image of Ameer over at the transporter.', 12, 0, 100, 0, 0, 0, 0, 18442, 0, 'Protectorate Demolitionist - Waypoint 7');
