INSERT INTO `creature_text` VALUES (20885, 0, 0, 'Don\'t worry about me; kill that worthless dullard instead!', 14, 0, 100, 1, 0, 11085, 0, 19979, 0, 'Dalliah the Doomsayer - Aggro Soccothrates First');
INSERT INTO `creature_text` VALUES (20885, 1, 0, 'It is unwise to anger me!', 14, 0, 100, 0, 0, 11086, 0, 19978, 0, 'Dalliah the Doomsayer - Aggro');
INSERT INTO `creature_text` VALUES (20885, 2, 0, 'Completely ineffective.  Just like someone else I know.', 14, 0, 100, 0, 0, 11087, 0, 19980, 0, 'Dalliah the Doomsayer - Kill');
INSERT INTO `creature_text` VALUES (20885, 2, 1, 'You chose the wrong opponent.', 14, 0, 100, 0, 0, 11088, 0, 19981, 0, 'Dalliah the Doomsayer - Kill');
INSERT INTO `creature_text` VALUES (20885, 3, 0, 'Reap the whirlwind!', 14, 0, 100, 0, 0, 11089, 0, 19982, 0, 'Dalliah the Doomsayer - Cast Whirlwind');
INSERT INTO `creature_text` VALUES (20885, 3, 1, 'I\'ll cut you to pieces!', 14, 0, 100, 0, 0, 11090, 0, 19983, 0, 'Dalliah the Doomsayer - Cast Whirlwind');
INSERT INTO `creature_text` VALUES (20885, 4, 0, 'That is much better.', 14, 0, 100, 0, 0, 11091, 0, 19984, 0, 'Dalliah the Doomsayer - Cast Heal');
INSERT INTO `creature_text` VALUES (20885, 4, 1, 'Ahh... just what I needed.', 14, 0, 100, 0, 0, 11092, 0, 19985, 0, 'Dalliah the Doomsayer - Cast Heal');
INSERT INTO `creature_text` VALUES (20885, 5, 0, 'Now I\'m really angry.', 14, 0, 100, 0, 0, 11093, 0, 19986, 0, 'Dalliah the Doomsayer - Death');
INSERT INTO `creature_text` VALUES (20885, 6, 0, 'More than you can handle, scryer?', 14, 0, 100, 1, 0, 11094, 0, 19987, 0, 'Dalliah the Doomsayer - Soccothrates at 25%');
INSERT INTO `creature_text` VALUES (20885, 6, 1, 'I suppose I\'ll end up fighting them all myself.', 14, 0, 100, 1, 0, 11095, 0, 19988, 0, 'Dalliah the Doomsayer - Soccothrates at 25%');
INSERT INTO `creature_text` VALUES (20885, 6, 2, 'I\'ve grown used to cleaning up your messes.', 14, 0, 100, 1, 0, 11096, 0, 19989, 0, 'Dalliah the Doomsayer - Soccothrates at 25%');
INSERT INTO `creature_text` VALUES (20885, 7, 0, 'Congratulations.  I\'ve wanted to do that for years.', 14, 0, 100, 66, 0, 11097, 0, 19990, 0, 'Dalliah the Doomsayer - Soccothratess dies');
INSERT INTO `creature_text` VALUES (20885, 8, 0, 'Why would I call on you?', 14, 0, 100, 396, 0, 0, 0, 20055, 0, 'Dalliah the Doomsayer - Conversation with Soccothrates part 1');
INSERT INTO `creature_text` VALUES (20885, 9, 0, 'When I need someone to prance around like an overstuffed peacock, I\'ll call on you.', 14, 0, 100, 396, 0, 0, 0, 20056, 0, 'Dalliah the Doomsayer - Conversation with Soccothrates part 2');
INSERT INTO `creature_text` VALUES (20885, 10, 0, 'What would you know about commitment, sheet-sah?', 14, 0, 100, 396, 0, 0, 0, 20057, 0, 'Dalliah the Doomsayer - Conversation with Soccothrates part 3');
INSERT INTO `creature_text` VALUES (20886, 0, 0, 'Have you come to kill Dalliah?  Can I watch?', 14, 0, 100, 1, 0, 11237, 0, 19968, 0, 'Wrath-Scryer Soccothrates - Aggro Dalliah First');
INSERT INTO `creature_text` VALUES (20886, 1, 0, 'At last, a target for my frustrations!', 14, 0, 100, 0, 0, 11238, 0, 19967, 0, 'Wrath-Scryer Soccothrates - Aggro');
INSERT INTO `creature_text` VALUES (20886, 2, 0, 'Yes, that was quite satisfying.', 14, 0, 100, 0, 0, 11239, 0, 19969, 0, 'Wrath-Scryer Soccothrates - Kill');
INSERT INTO `creature_text` VALUES (20886, 3, 0, 'On guard!', 14, 0, 100, 0, 0, 11241, 0, 19971, 0, 'Wrath-Scryer Soccothrates - Knock Away');
INSERT INTO `creature_text` VALUES (20886, 3, 1, 'Defend yourself, for all the good it will do....', 14, 0, 100, 0, 0, 11242, 0, 19972, 0, 'Wrath-Scryer Soccothrates - Knock Away');
INSERT INTO `creature_text` VALUES (20886, 4, 0, 'Knew this was... the only way out.', 14, 0, 100, 0, 0, 11243, 0, 19973, 0, 'Wrath-Scryer Soccothrates - Death');
INSERT INTO `creature_text` VALUES (20886, 5, 0, 'Having problems, Dalliah?  How nice.', 14, 0, 100, 1, 0, 11244, 0, 19974, 0, 'Wrath-Scryer Soccothrates - Dalliah at 25%');
INSERT INTO `creature_text` VALUES (20886, 5, 1, 'This may be the end for you, Dalliah.  What a shame that would be.', 14, 0, 100, 1, 0, 11245, 0, 19975, 0, 'Wrath-Scryer Soccothrates - Dalliah at 25%');
INSERT INTO `creature_text` VALUES (20886, 5, 2, 'I suggest a new strategy: you draw their attacks while I gather reinforcements.', 14, 0, 100, 1, 0, 11246, 0, 19976, 0, 'Wrath-Scryer Soccothrates - Dalliah at 25%');
INSERT INTO `creature_text` VALUES (20886, 6, 0, 'Finally!  Well done!', 14, 0, 100, 66, 0, 11247, 0, 19977, 0, 'Wrath-Scryer Soccothrates - Dalliah dies');
INSERT INTO `creature_text` VALUES (20886, 7, 0, 'Did you call on me?', 14, 0, 100, 397, 0, 11236, 0, 20051, 0, 'Wrath-Scryer Soccothrates - Conversation with Dalliah part 1');
INSERT INTO `creature_text` VALUES (20886, 8, 0, 'To do your heavy lifting, most likely.', 14, 0, 100, 396, 0, 0, 0, 20052, 0, 'Wrath-Scryer Soccothrates - Conversation with Dalliah part 2');
INSERT INTO `creature_text` VALUES (20886, 9, 0, 'Then I\'ll commit myself to ignoring you.', 14, 0, 100, 396, 0, 0, 0, 20053, 0, 'Wrath-Scryer Soccothrates - Conversation with Dalliah part 3');
INSERT INTO `creature_text` VALUES (20886, 10, 0, 'You\'re the one who should be-- Wait, we have company...', 14, 0, 100, 396, 0, 0, 0, 20054, 0, 'Wrath-Scryer Soccothrates - Conversation with Dalliah part 4');
INSERT INTO `creature_text` VALUES (20904, 0, 0, 'I knew the prince would be angry, but I... I have not been myself. I had to let them out! The great one speaks to me, you see. Wait--outsiders. Kael\'thas did not send you! Good... I\'ll just tell the prince you released the prisoners!', 14, 0, 100, 0, 0, 11222, 0, 19103, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 1, 0, 'The naaru kept some of the most dangerous beings in existence here in these cells. Let me introduce you to another....', 14, 0, 100, 0, 0, 11223, 0, 19104, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 2, 0, 'Yes, yes... another! Your will is mine!', 14, 0, 100, 0, 0, 11224, 0, 19106, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 3, 0, 'Behold, yet another terrifying creature of incomprehensible power!', 14, 0, 100, 0, 0, 11225, 0, 19107, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 4, 0, 'What is this? A lowly gnome? I will do better, oh great one.', 14, 0, 100, 0, 0, 11226, 0, 19109, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 5, 0, 'Anarchy! Bedlam! Oh, you are so wise! Yes, I see it now, of course!', 14, 0, 100, 0, 0, 11227, 0, 19110, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 6, 0, 'One final cell remains. Yes, O great one, right away!', 14, 0, 100, 0, 0, 11228, 0, 19111, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20904, 7, 0, 'Welcome, O great one. I am your humble servant.', 14, 0, 100, 0, 0, 11229, 0, 19114, 0, 'Warden Mellichar');
INSERT INTO `creature_text` VALUES (20912, 0, 0, 'It is a small matter to control the mind of the weak... for I bear allegiance to powers untouched by time, unmoved by fate. No force on this world or beyond harbors the strength to bend our knee... not even the mighty Legion! ', 14, 0, 100, 0, 0, 11122, 0, 19112, 0, 'skyriss SAY_INTRO');
INSERT INTO `creature_text` VALUES (20912, 1, 0, 'Bear witness to the agent of your demise!', 14, 0, 100, 0, 0, 11123, 0, 19113, 0, 'skyriss SAY_AGGRO');
INSERT INTO `creature_text` VALUES (20912, 2, 0, 'Your fate is written.', 14, 0, 100, 0, 0, 11124, 0, 19813, 0, 'skyriss SAY_KILL_1');
INSERT INTO `creature_text` VALUES (20912, 2, 1, 'The chaos I have sown here is but a taste....', 14, 0, 100, 0, 0, 11125, 0, 19814, 0, 'skyriss SAY_KILL_2');
INSERT INTO `creature_text` VALUES (20912, 3, 0, 'You will do my bidding, weakling.', 14, 0, 100, 0, 0, 11127, 0, 19816, 0, 'skyriss SAY_MIND_1');
INSERT INTO `creature_text` VALUES (20912, 3, 1, 'Your will is no longer your own.', 14, 0, 100, 0, 0, 11128, 0, 34363, 0, 'skyriss SAY_MIND_2');
INSERT INTO `creature_text` VALUES (20912, 4, 0, 'Flee in terror.', 14, 0, 100, 0, 0, 11129, 0, 19818, 0, 'skyriss SAY_FEAR_1');
INSERT INTO `creature_text` VALUES (20912, 4, 1, 'I will show you horrors undreamed of.', 14, 0, 100, 0, 0, 11130, 0, 19819, 0, 'skyriss SAY_FEAR_2');
INSERT INTO `creature_text` VALUES (20912, 5, 0, 'We span the universe, as countless as the stars!', 14, 0, 100, 0, 0, 11131, 0, 19821, 0, 'skyriss SAY_IMAGE');
INSERT INTO `creature_text` VALUES (20912, 6, 0, 'I am merely one of... infinite multitudes.', 14, 0, 100, 0, 0, 11126, 0, 19815, 0, 'skyriss SAY_DEATH');
INSERT INTO `creature_text` VALUES (20913, 0, 0, 'Now to begin the repairs.', 12, 0, 100, 0, 0, 0, 0, 18651, 0, 'Tashar');
INSERT INTO `creature_text` VALUES (20913, 1, 0, 'It\'s working! Ghabar will be pleased to hear the news.', 12, 0, 100, 0, 0, 0, 0, 18652, 0, 'Tashar');
INSERT INTO `creature_text` VALUES (20977, 0, 0, 'Where in Bonzo\'s Brass Buttons am I? And who are-- yaaghh, that\'s one mother of a headache!', 14, 0, 100, 0, 0, 11171, 0, 19108, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 1, 0, '\"Lowly\"? I don\'t care who you are, friend: no one refers to the mighty Millhouse Manastorm as \"lowly\"!', 14, 0, 100, 0, 0, 11172, 0, 19116, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 2, 0, 'I just need to get some things ready first. You guys go ahead and get started. I need to summon up some water....', 14, 0, 100, 0, 0, 11173, 0, 19117, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 3, 0, 'Fantastic! Next, some protective spells. Yeah, now we\'re cookin\'!', 14, 0, 100, 0, 0, 11174, 0, 19119, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 4, 0, 'And of course I\'ll need some mana. You guys are gonna love this; just wait....', 14, 0, 100, 0, 0, 11175, 0, 19120, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 5, 0, 'Aaalllriiiight!! Who ordered up an extra large can of whoop-ass?', 14, 0, 100, 0, 0, 11176, 0, 19121, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 6, 0, 'I didn\'t even break a sweat on that one!', 14, 0, 100, 0, 0, 11177, 0, 19824, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 6, 1, 'You guys feel free to jump in anytime.', 14, 0, 100, 0, 0, 11178, 0, 19825, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 7, 0, 'I\'m gonna light you up, sweet cheeks!', 14, 0, 100, 0, 0, 11179, 0, 19826, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 8, 0, 'Ice, ice baby.', 14, 0, 100, 0, 0, 11180, 0, 19827, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 9, 0, 'Heal me! For the love of all that\'s holy, heal me! I\'m dying!!', 14, 0, 100, 0, 0, 11181, 0, 19828, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 10, 0, 'You\'ll be hearing from my lawyer!', 14, 0, 100, 0, 0, 11182, 0, 19829, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20977, 11, 0, 'Who\'s bad? Who\'s bad? That\'s right: we bad!', 14, 0, 100, 0, 0, 11183, 0, 19146, 0, 'Millhouse Manastorm');
INSERT INTO `creature_text` VALUES (20983, 0, 0, '%s goes into a frenzy!', 16, 0, 100, 0, 0, 0, 0, 38630, 0, 'Mutated Farahlon Lasher');
INSERT INTO `creature_text` VALUES (21024, 0, 0, 'Stand back, $n. The spirits may lash out.', 12, 0, 100, 1, 0, 0, 0, 18749, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 1, 0, 'We wish to help you, spirit. We seek your wisdom.', 12, 0, 100, 1, 0, 0, 0, 18753, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 2, 0, 'You are right, spirit. It was my people that did this to you. Allow us to make it right.', 12, 0, 100, 1, 0, 0, 0, 18757, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 3, 0, 'Please tell us this: Who or what is Cyrukh?', 12, 0, 100, 1, 0, 0, 0, 18758, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 4, 0, 'The... The fire lord? An elemental lord was unearthed? Of course... Gul\'dan you were a damned fool!', 12, 0, 100, 6, 0, 0, 0, 18760, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 5, 0, 'There must be a way.', 12, 0, 100, 0, 0, 0, 0, 18762, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 6, 0, 'Do not doubt my comrades and I, spirit. Tell us how to conjure the fire lord. My allies will destroy him.', 12, 0, 100, 1, 0, 0, 0, 18764, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 7, 0, 'We will find these words. We will summon your master and we will destroy him.', 12, 0, 100, 1, 0, 0, 0, 18768, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 8, 0, '%s nods.', 16, 0, 100, 273, 0, 0, 0, 18756, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 9, 0, 'Now to recover the lost words!', 12, 0, 100, 5, 0, 0, 0, 18778, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 10, 0, '%s bows.', 16, 0, 100, 2, 0, 0, 0, 18752, 0, 'Earthmender Torlok');
INSERT INTO `creature_text` VALUES (21024, 11, 0, 'I hear what you hear, brother. Look behind you...', 12, 0, 100, 1, 0, 0, 0, 19336, 0, 'Earthmender Torlok to Cyrukh the Firelord');
INSERT INTO `creature_text` VALUES (21027, 0, 0, 'I sense the tortured spirits, $n. They are this way. Come quickly!', 12, 0, 100, 0, 0, 0, 0, 18668, 0, 'wilda SAY_WIL_START');
INSERT INTO `creature_text` VALUES (21027, 1, 0, 'Watch out!', 12, 0, 100, 0, 0, 0, 0, 32401, 0, 'wilda SAY_WIL_AGGRO1');
INSERT INTO `creature_text` VALUES (21027, 1, 1, 'Naga attackers! Defend yourself!', 12, 0, 100, 0, 0, 0, 0, 18688, 0, 'wilda SAY_WIL_AGGRO2');
INSERT INTO `creature_text` VALUES (21027, 2, 0, 'Grant me protection, $n. I must break through their foul magic!', 12, 0, 100, 0, 0, 0, 0, 18669, 0, 'wilda SAY_WIL_PROGRESS1');
INSERT INTO `creature_text` VALUES (21027, 3, 0, 'The naga of Coilskar are exceptionally cruel to their prisoners. It is a miracle that I survived inside that watery prison for as long as I did. Earthmother be praised.', 12, 0, 100, 0, 0, 0, 0, 18685, 0, 'wilda SAY_WIL_PROGRESS2');
INSERT INTO `creature_text` VALUES (21027, 3, 1, 'Lady Vashj must answer for these atrocities. She must be brought to justice!', 12, 0, 100, 0, 0, 0, 0, 18684, 0, 'wilda SAY_WIL_PROGRESS4');
INSERT INTO `creature_text` VALUES (21027, 3, 2, 'The tumultuous nature of the great waterways of Azeroth and Draenor are a direct result of tormented water spirits.', 12, 0, 100, 0, 0, 0, 0, 18675, 0, 'wilda SAY_WIL_PROGRESS5');
INSERT INTO `creature_text` VALUES (21027, 4, 0, 'Now we must find the exit.', 12, 0, 100, 0, 0, 0, 0, 18670, 0, 'wilda SAY_WIL_FIND_EXIT');
INSERT INTO `creature_text` VALUES (21027, 5, 0, 'It shouldn\'t be much further, $n. The exit is just up ahead.', 12, 0, 100, 0, 0, 0, 0, 18671, 0, 'wilda SAY_WIL_JUST_AHEAD');
INSERT INTO `creature_text` VALUES (21027, 6, 0, 'Thank you, $n. Please return to my brethren at the Altar of Damnation, near the Hand of Gul\'dan, and tell them that Wilda is safe. May the Earthmother watch over you...', 12, 0, 100, 0, 0, 0, 0, 18672, 0, 'wilda SAY_WIL_END');
