INSERT INTO `creature_text` VALUES (21049, 0, 2, 'Do not do this, Gul\'dan! The elements will never forgive us!', 12, 0, 100, 20, 0, 0, 0, 18697, 0, 'Spirit of the Past');
INSERT INTO `creature_text` VALUES (21049, 0, 3, 'Think of our younglings, Gul\'dan! You will destroy us all!', 12, 0, 100, 20, 0, 0, 0, 18699, 0, 'Spirit of the Past');
