INSERT INTO `creature_text` VALUES (21118, 0, 0, 'LET\'S ROCK!!', 14, 7, 100, 0, 0, 0, 0, 19143, 0, 'Razak Ironsides - Ravager invasion event');
INSERT INTO `creature_text` VALUES (21118, 1, 0, 'FIRE UP THE DEF GUNS!', 14, 7, 100, 0, 0, 0, 0, 19673, 0, 'Razak Ironsides - Ravager invasion event');
INSERT INTO `creature_text` VALUES (21118, 2, 0, 'Where\'s our back up?  I want them here now!', 14, 7, 100, 0, 0, 0, 0, 19140, 0, 'Razak Ironsides - Ravager invasion event');
INSERT INTO `creature_text` VALUES (21118, 3, 0, 'Last push boys!  Let \'em have it!', 14, 7, 100, 0, 0, 0, 0, 19453, 0, 'Razak Ironsides - Ravager invasion event');
INSERT INTO `creature_text` VALUES (21118, 4, 0, 'Nice work soldiers!  This is the last of them!', 14, 7, 100, 0, 0, 0, 0, 19455, 0, 'Razak Ironsides - Ravager invasion event');
INSERT INTO `creature_text` VALUES (21118, 4, 1, 'This is the last of them.  Don\'t get sloppy!', 14, 7, 100, 0, 0, 0, 0, 19454, 0, 'Razak Ironsides - Ravager invasion event');
INSERT INTO `creature_text` VALUES (21118, 5, 0, 'We can handle it from here.  Roughriders you are dismissed.', 14, 7, 100, 0, 0, 0, 0, 19674, 0, 'Razak Ironsides - Ravager invasion event');
INSERT INTO `creature_text` VALUES (21118, 6, 0, 'Come on you apes. You want to live forever?', 12, 7, 100, 0, 0, 0, 0, 19095, 0, 'Razak Ironsides - Ravager invasion event random combat text');
INSERT INTO `creature_text` VALUES (21118, 6, 1, 'Warm it up and give \'em everything you\'ve got.', 12, 7, 100, 0, 0, 0, 0, 19096, 0, 'Razak Ironsides - Ravager invasion event random combat text');
INSERT INTO `creature_text` VALUES (21118, 6, 2, 'I only have one rule. Everyone fights. No one quits.', 12, 7, 100, 0, 0, 0, 0, 19093, 0, 'Razak Ironsides - Ravager invasion event random combat text');
INSERT INTO `creature_text` VALUES (21129, 0, 0, 'Why have you captured us?', 12, 0, 100, 0, 0, 0, 0, 18750, 0, 'Scorned Spirit of Earth');
INSERT INTO `creature_text` VALUES (21129, 1, 0, 'Fool! Cyrukh the fire lord! When the tie was severed, Cyrukh awoke. It is the tears of Cyrukh that fill this land with molten rock.', 12, 0, 100, 0, 0, 0, 0, 18759, 0, 'Scorned Spirit of Earth');
INSERT INTO `creature_text` VALUES (21129, 2, 0, 'Yes... Yes... the words. The words of damnation. The words spoken by the dark one. Your master.', 12, 0, 100, 0, 0, 0, 0, 18766, 0, 'Scorned Spirit of Earth');
INSERT INTO `creature_text` VALUES (21129, 3, 0, 'Talk. More talk... Now release us, mortal.', 12, 0, 100, 0, 0, 0, 0, 18769, 0, 'Scorned Spirit of Earth');
INSERT INTO `creature_text` VALUES (21130, 0, 0, 'What is it that you hope to gain from this?', 12, 0, 100, 0, 0, 0, 0, 18751, 0, 'Scorned Spirit of Fire');
INSERT INTO `creature_text` VALUES (21130, 1, 0, 'He is insane now. Driven mad during the shattering. There is no rest for the elements.', 12, 0, 100, 0, 0, 0, 0, 18761, 0, 'Scorned Spirit of Fire');
INSERT INTO `creature_text` VALUES (21130, 2, 0, 'Yes... Gul\'dan...', 12, 0, 100, 0, 0, 0, 0, 18767, 0, 'Scorned Spirit of Fire');
INSERT INTO `creature_text` VALUES (21131, 0, 0, 'Wisdom? It was your people that caused the insufferable wrath of Cyrukh.', 12, 0, 100, 0, 0, 0, 0, 18754, 0, 'Scorned Spirit of Water');
INSERT INTO `creature_text` VALUES (21131, 1, 0, 'Yes, a way. Destroy Cyrukh. Destroy an elemental lord.', 12, 0, 100, 0, 0, 0, 0, 18763, 0, 'Scorned Spirit of Water');
INSERT INTO `creature_text` VALUES (21132, 0, 0, 'Why should we help you? The world is ruined. Destroyed.', 12, 0, 100, 0, 0, 0, 0, 18755, 0, 'Scorned Spirit of Air');
INSERT INTO `creature_text` VALUES (21132, 1, 0, 'You must utter the words...', 12, 0, 100, 0, 0, 0, 0, 18765, 0, 'Scorned Spirit of Air');
INSERT INTO `creature_text` VALUES (21134, 0, 0, 'A deep, bone chilling voice echoes from the %s...', 16, 0, 100, 0, 0, 0, 0, 18774, 0, 'Eye of the Citadel');
INSERT INTO `creature_text` VALUES (21134, 1, 0, 'How dare you trifle with my playthings, $n!', 12, 0, 100, 0, 0, 0, 0, 18775, 0, 'Eye of the Citadel');
INSERT INTO `creature_text` VALUES (21134, 2, 0, 'The orcs in my citadel will drink your blood and dovour you!', 12, 0, 100, 0, 0, 0, 0, 18776, 0, 'Eye of the Citadel');
INSERT INTO `creature_text` VALUES (21151, 0, 0, 'ACK! That\'s the worst thing I\'ve ever tasted! I wouldn\'t let my ram drink that!', 12, 0, 100, 1, 0, 0, 0, 18830, 0, 'Borgrim Stouthammer');
INSERT INTO `creature_text` VALUES (21174, 0, 0, 'Wretched, meddling insects! Release me, and perhaps I will grant you a merciful death!', 14, 0, 100, 0, 0, 10247, 0, 17339, 3, 'Magtheridon');
INSERT INTO `creature_text` VALUES (21174, 0, 1, 'Vermin! Leeches! Take my blood and choke on it!', 14, 0, 100, 0, 0, 10248, 0, 17340, 3, 'Magtheridon');
INSERT INTO `creature_text` VALUES (21174, 0, 2, 'Illidan is an arrogant fool! I will crush him and reclaim Outland as my own!', 14, 0, 100, 0, 0, 10249, 0, 17341, 3, 'Magtheridon');
INSERT INTO `creature_text` VALUES (21174, 0, 3, 'Away, you mindless parasites! My blood is my own!', 14, 0, 100, 0, 0, 10250, 0, 17342, 3, 'Magtheridon');
INSERT INTO `creature_text` VALUES (21174, 0, 4, 'How long do you believe your pathetic sorcery can hold me?', 14, 0, 100, 0, 0, 10251, 0, 17343, 3, 'Magtheridon');
INSERT INTO `creature_text` VALUES (21174, 0, 5, 'My blood will be the end of you!', 14, 0, 100, 0, 0, 10252, 0, 17344, 3, 'Magtheridon');
INSERT INTO `creature_text` VALUES (21181, 0, 0, 'Cyrukh has awoken!', 14, 0, 100, 0, 0, 0, 0, 19298, 0, 'Cyrukh the Firelord');
INSERT INTO `creature_text` VALUES (21181, 1, 0, 'Cyrukh comes for you!', 14, 0, 100, 0, 0, 0, 0, 19301, 0, 'Cyrukh the Firelord');
INSERT INTO `creature_text` VALUES (21181, 1, 1, 'You will suffer eternally!', 14, 0, 100, 0, 0, 0, 0, 19299, 0, 'Cyrukh the Firelord');
INSERT INTO `creature_text` VALUES (21181, 1, 2, 'Those that dare play with fire will be incinerated!', 14, 0, 100, 0, 0, 0, 0, 19302, 0, 'Cyrukh the Firelord');
INSERT INTO `creature_text` VALUES (21181, 1, 3, 'Little creature made of flesh, your wish is granted! Death comes for you!', 14, 0, 100, 0, 0, 0, 0, 19308, 0, 'Cyrukh the Firelord');
INSERT INTO `creature_text` VALUES (21193, 0, 0, 'Learning to read is a thirsty business. A toast, eh?', 12, 1, 100, 0, 0, 0, 0, 19063, 0, 'Gralga');
INSERT INTO `creature_text` VALUES (21212, 0, 0, 'Water... is life. It has become a rare commodity here in Outland; a commodity that we alone shall control. We are the Highborne, and the time has come at last for us to retake our rightful place in the world!', 14, 0, 100, 0, 0, 11531, 0, 20085, 0, 'vashj SAY_INTRO');
INSERT INTO `creature_text` VALUES (21212, 1, 0, 'I\'ll split you from stem to stern!', 14, 0, 100, 0, 0, 11532, 0, 20088, 0, 'vashj SAY_AGGRO1');
INSERT INTO `creature_text` VALUES (21212, 1, 1, 'Victory to Lord Illidan! ', 14, 0, 100, 0, 0, 11533, 0, 20089, 0, 'vashj SAY_AGGRO2');
INSERT INTO `creature_text` VALUES (21212, 1, 2, 'I spit on you, surface filth!', 14, 0, 100, 0, 0, 11534, 0, 20091, 0, 'vashj SAY_AGGRO3');
INSERT INTO `creature_text` VALUES (21212, 1, 3, 'Death to the outsiders!', 14, 0, 100, 0, 0, 11535, 0, 20090, 0, 'vashj SAY_AGGRO4');
INSERT INTO `creature_text` VALUES (21212, 2, 0, 'I did not wish to lower myself by engaging your kind, but you leave me little choice...', 14, 0, 100, 0, 0, 11538, 0, 19679, 0, 'vashj SAY_PHASE1');
INSERT INTO `creature_text` VALUES (21212, 3, 0, 'The time is now! Leave none standing! ', 14, 0, 100, 0, 0, 11539, 0, 20092, 0, 'vashj SAY_PHASE2');
INSERT INTO `creature_text` VALUES (21212, 4, 0, 'You may want to take cover. ', 14, 0, 100, 0, 0, 11540, 0, 20210, 0, 'vashj SAY_PHASE3');
INSERT INTO `creature_text` VALUES (21212, 5, 0, 'Straight to the heart!', 14, 0, 100, 0, 0, 11536, 0, 20086, 0, 'vashj SAY_BOWSHOT1');
INSERT INTO `creature_text` VALUES (21212, 5, 1, 'Seek your mark!', 14, 0, 100, 0, 0, 11537, 0, 20087, 0, 'vashj SAY_BOWSHOT2');
INSERT INTO `creature_text` VALUES (21212, 6, 0, 'Your time ends now!', 14, 0, 100, 0, 0, 11541, 0, 49785, 0, 'vashj SAY_SLAY1');
INSERT INTO `creature_text` VALUES (21212, 6, 1, 'You have failed!', 14, 0, 100, 0, 0, 11542, 0, 20094, 0, 'vashj SAY_SLAY2');
INSERT INTO `creature_text` VALUES (21212, 6, 2, 'Be\'lemer an-delei!', 14, 0, 100, 0, 0, 11543, 0, 20095, 0, 'vashj SAY_SLAY3');
INSERT INTO `creature_text` VALUES (21212, 7, 0, 'Lord Illidan, I... I am... sorry.', 14, 0, 100, 0, 0, 11544, 0, 20096, 0, 'vashj SAY_DEATH');
INSERT INTO `creature_text` VALUES (21213, 0, 0, 'Flood of the deep, take you!', 14, 0, 100, 0, 0, 11321, 0, 20931, 0, 'morogrim SAY_AGGRO');
INSERT INTO `creature_text` VALUES (21213, 1, 0, 'By the tides!', 14, 0, 100, 0, 0, 11322, 0, 20933, 0, 'morogrim SAY_SUMMON1');
INSERT INTO `creature_text` VALUES (21213, 1, 1, 'Destroy them, my subjects!', 14, 0, 100, 0, 0, 11323, 0, 20934, 0, 'morogrim SAY_SUMMON2');
INSERT INTO `creature_text` VALUES (21213, 2, 0, 'There is nowhere to hide!', 14, 0, 100, 0, 0, 11324, 0, 20935, 0, 'morogrim SAY_SUMMON_BUBL1');
INSERT INTO `creature_text` VALUES (21213, 2, 1, 'Soon it will be finished!', 14, 0, 100, 0, 0, 11325, 0, 20936, 0, 'morogrim SAY_SUMMON_BUBL2');
INSERT INTO `creature_text` VALUES (21213, 3, 0, 'It is done!', 14, 0, 100, 0, 0, 11326, 0, 20937, 0, 'morogrim SAY_SLAY1');
INSERT INTO `creature_text` VALUES (21213, 3, 1, 'Struggling only makes it worse.', 14, 0, 100, 0, 0, 11327, 0, 20938, 0, 'morogrim SAY_SLAY2');
INSERT INTO `creature_text` VALUES (21213, 3, 2, 'Only the strong will survive.', 14, 0, 100, 0, 0, 11328, 0, 20939, 0, 'morogrim SAY_SLAY3');
INSERT INTO `creature_text` VALUES (21213, 4, 0, 'Great currents of... Ageon...', 14, 0, 100, 0, 0, 11329, 0, 20940, 0, 'morogrim SAY_DEATH');
INSERT INTO `creature_text` VALUES (21213, 5, 0, '%s sends his enemies to their watery graves!', 16, 0, 100, 0, 0, 0, 0, 20701, 0, 'morogrim EMOTE_WATERY_GRAVE');
INSERT INTO `creature_text` VALUES (21213, 6, 0, 'The violent earthquake has alerted nearby Murlocs!', 41, 0, 100, 0, 0, 0, 0, 20700, 0, 'morogrim EMOTE_EARTHQUAKE');
INSERT INTO `creature_text` VALUES (21213, 7, 0, '%s summons watery globules!', 16, 0, 100, 0, 0, 0, 0, 20703, 0, 'morogrim EMOTE_WATERY_GLOBULES');
INSERT INTO `creature_text` VALUES (21214, 0, 0, 'Guards, attention! We have visitors....', 14, 0, 100, 0, 0, 11277, 0, 20000, 0, 'karathress SAY_AGGRO');
INSERT INTO `creature_text` VALUES (21214, 1, 0, 'Your overconfidence will be your undoing! Guards, lend me your strength!', 14, 0, 100, 0, 0, 11278, 0, 19854, 0, 'karathress SAY_GAIN_BLESSING');
INSERT INTO `creature_text` VALUES (21214, 2, 0, 'Go on, kill them! I\'ll be the better for it!', 14, 0, 100, 0, 0, 11279, 0, 19643, 0, 'karathress SAY_GAIN_ABILITY1');
INSERT INTO `creature_text` VALUES (21214, 2, 1, 'I am more powerful than ever!', 14, 0, 100, 0, 0, 11280, 0, 19645, 0, 'karathress SAY_GAIN_ABILITY2');
INSERT INTO `creature_text` VALUES (21214, 2, 2, 'More knowledge, more power!', 14, 0, 100, 0, 0, 11281, 0, 19644, 0, 'karathress SAY_GAIN_ABILITY3');
INSERT INTO `creature_text` VALUES (21214, 3, 0, 'Land-dwelling scum!', 14, 0, 100, 0, 0, 11282, 0, 20001, 0, 'karathress SAY_SLAY1');
INSERT INTO `creature_text` VALUES (21214, 3, 1, 'Alana be\'lendor!', 14, 0, 100, 0, 0, 11283, 0, 20002, 0, 'karathress SAY_SLAY2');
INSERT INTO `creature_text` VALUES (21214, 3, 2, 'I am rid of you.', 14, 0, 100, 0, 0, 11284, 0, 20003, 0, 'karathress SAY_SLAY3');
INSERT INTO `creature_text` VALUES (21214, 4, 0, 'Her... excellency... awaits.', 14, 0, 100, 0, 0, 11285, 0, 20004, 0, 'karathress SAY_DEATH');
INSERT INTO `creature_text` VALUES (21215, 0, 0, 'Finally, my banishment ends!', 14, 0, 100, 0, 0, 11312, 0, 19596, 0, 'leotheras SAY_AGGRO');
INSERT INTO `creature_text` VALUES (21215, 1, 0, 'Be gone, trifling elf.  I am in control now!', 14, 0, 100, 0, 0, 11304, 0, 19595, 0, 'leotheras SAY_SWITCH_TO_DEMON');
INSERT INTO `creature_text` VALUES (21215, 2, 0, 'We all have our demons....', 14, 0, 100, 0, 0, 11305, 0, 19597, 0, 'leotheras SAY_INNER_DEMONS');
INSERT INTO `creature_text` VALUES (21215, 3, 0, 'I have no equal!', 14, 0, 100, 0, 0, 11306, 0, 19993, 0, 'leotheras SAY_DEMON_SLAY1');
INSERT INTO `creature_text` VALUES (21215, 3, 1, 'Perish, mortal!', 14, 0, 100, 0, 0, 11307, 0, 19994, 0, 'leotheras SAY_DEMON_SLAY2');
INSERT INTO `creature_text` VALUES (21215, 3, 2, 'Yes... yes!', 14, 0, 100, 0, 0, 11308, 0, 19995, 0, 'leotheras SAY_DEMON_SLAY3');
INSERT INTO `creature_text` VALUES (21215, 4, 0, 'Kill, kill!', 14, 0, 100, 0, 0, 11314, 0, 19996, 0, 'leotheras SAY_NIGHTELF_SLAY1');
INSERT INTO `creature_text` VALUES (21215, 4, 1, 'That\'s right, yes!', 14, 0, 100, 0, 0, 11315, 0, 19997, 0, 'leotheras SAY_NIGHTELF_SLAY2');
INSERT INTO `creature_text` VALUES (21215, 4, 2, 'Who\'s the master now?', 14, 0, 100, 0, 0, 11316, 0, 19998, 0, 'leotheras SAY_NIGHTELF_SLAY3');
INSERT INTO `creature_text` VALUES (21215, 5, 0, 'No... no! What have you done? I am the master! Do you hear me? I am... aaggh! Can\'t... contain him.', 14, 0, 100, 0, 0, 11313, 0, 19501, 0, 'leotheras SAY_FINAL_FORM');
INSERT INTO `creature_text` VALUES (21215, 6, 0, 'At last I am liberated. It has been too long since I have tasted true freedom!', 14, 0, 100, 0, 0, 11309, 0, 19502, 0, 'leotheras SAY_FREE');
INSERT INTO `creature_text` VALUES (21215, 7, 0, 'You cannot kill me, fools! I\'ll be back, I\'ll....', 14, 0, 100, 0, 0, 11317, 0, 19999, 0, 'leotheras SAY_DEATH');
INSERT INTO `creature_text` VALUES (21216, 0, 0, 'I cannot allow you to interfere!', 14, 0, 100, 0, 0, 11289, 0, 20231, 0, 'hydross SAY_AGGRO');
INSERT INTO `creature_text` VALUES (21216, 1, 0, 'Better, much better.', 14, 0, 100, 0, 0, 11290, 0, 19607, 0, 'hydross SAY_SWITCH_TO_CLEAN');
INSERT INTO `creature_text` VALUES (21216, 2, 0, 'They have forced me to this....', 14, 0, 100, 0, 0, 11291, 0, 20235, 0, 'hydross SAY_CLEAN_SLAY1');
INSERT INTO `creature_text` VALUES (21216, 2, 1, 'I had no choice.', 14, 0, 100, 0, 0, 11292, 0, 20236, 0, 'hydross SAY_CLEAN_SLAY2');
INSERT INTO `creature_text` VALUES (21216, 3, 0, 'I am... released.', 14, 0, 100, 0, 0, 11293, 0, 20238, 0, 'hydross SAY_CLEAN_DEATH');
INSERT INTO `creature_text` VALUES (21216, 4, 0, 'Aaghh, the poison...', 14, 0, 100, 0, 0, 11297, 0, 19606, 0, 'hydross SAY_SWITCH_TO_CORRUPT');
