INSERT INTO `creature_text` VALUES (21661, 1, 7, 'You\'ll go nowhere, fool!', 12, 0, 100, 0, 0, 0, 0, 20164, 0, 'Cabal Skirmisher');
INSERT INTO `creature_text` VALUES (21685, 0, 0, 'You do not fight alone, $n! Together, we will banish this spawn of hellfire!', 14, 0, 100, 0, 0, 0, 0, 19304, 0, 'Oronok Torn-heart');
INSERT INTO `creature_text` VALUES (21685, 1, 0, 'We leave, then, Torlok. I have only one request...', 12, 0, 100, 1, 0, 0, 0, 19341, 0, 'Oronok Torn-heart');
