INSERT INTO `creature_text` VALUES (22424, 0, 0, '%s squawks and heads toward Veil Shalas. Hurry and follow!', 16, 0, 100, 0, 0, 0, 0, 20254, 0, 'Skywing');
INSERT INTO `creature_text` VALUES (22424, 1, 0, '%s pauses briefly before the tree and then heads inside.', 16, 0, 100, 0, 0, 0, 0, 20255, 0, 'Skywing');
INSERT INTO `creature_text` VALUES (22424, 2, 0, '%s seems to be looking for something. He wants you to follow.', 16, 0, 100, 0, 0, 0, 0, 20256, 0, 'Skywing');
INSERT INTO `creature_text` VALUES (22424, 3, 0, '%s flies to the platform below! You\'\'d better jump if you want to keep up. Hurry!', 16, 0, 100, 0, 0, 0, 0, 20257, 0, 'Skywing');
INSERT INTO `creature_text` VALUES (22424, 4, 0, '%s bellows a loud squawk!', 16, 0, 100, 0, 0, 0, 0, 20284, 0, 'Skywing');
INSERT INTO `creature_text` VALUES (22424, 5, 0, 'Free at last from that horrible curse! Thank you! Please send word to Rilak the Redeemed that I am okay. My mission lies in Skettis. Terokk must be defeated!', 12, 0, 100, 0, 0, 0, 0, 20295, 0, 'Skywing');
INSERT INTO `creature_text` VALUES (22424, 6, 0, '%s wanders off into the distance.', 16, 0, 100, 0, 0, 0, 0, 20297, 0, 'Skywing');
INSERT INTO `creature_text` VALUES (22431, 0, 0, 'It is time. The rite of exorcism will now commence...', 12, 0, 100, 0, 0, 0, 0, 20400, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 1, 0, 'Prepare yourself. Do not allow the ritual to be interrupted or we may lose our patient...', 12, 0, 100, 0, 0, 0, 0, 20401, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 2, 0, 'Back foul beings of darkness! You have no power here!', 12, 0, 100, 0, 0, 0, 0, 20404, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 3, 0, 'I... must not... falter', 12, 0, 100, 0, 0, 0, 0, 20407, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 4, 0, 'The Light is my guide... it is my sustenance!', 12, 0, 100, 0, 0, 0, 0, 20408, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 5, 0, 'The power of light compells you! Back to your pit!', 12, 0, 100, 0, 0, 0, 0, 20405, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 6, 0, 'Be cleansed with Light, human! Let not the demonic corruption overwhelm you.', 12, 0, 100, 0, 0, 0, 0, 20403, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22431, 7, 0, 'Back! I cast you back... corruptor of faith! Author of pain! Do not return, or suffer the same fate as you did here today!', 12, 0, 100, 0, 0, 0, 0, 20419, 0, 'Barada');
INSERT INTO `creature_text` VALUES (22432, 0, 0, 'Keep away. The fool is mine.', 12, 0, 100, 0, 0, 0, 0, 20402, 0, 'Colonel Jules');
INSERT INTO `creature_text` VALUES (22432, 1, 0, 'Ah! Cease the incantations, Anchorite! Cease, or I will show you such pain that your pathetic people have never imagined!', 12, 0, 100, 0, 0, 0, 0, 20414, 0, 'Colonel Jules');
INSERT INTO `creature_text` VALUES (22432, 2, 0, 'This is fruitless, draenei! You and your little helper cannot wrest control of this pathetic human. He is mine!', 12, 0, 100, 0, 0, 0, 0, 20416, 0, 'Colonel Jules');
INSERT INTO `creature_text` VALUES (22432, 3, 0, 'I see your ancestors, Anchorite! They writhe and scream in the darkness... they are with us!', 12, 0, 100, 0, 0, 0, 0, 20415, 0, 'Colonel Jules');
INSERT INTO `creature_text` VALUES (22432, 4, 0, 'I will tear your soul into morsels and slow roast them over demon fire.', 12, 0, 100, 0, 0, 0, 0, 20417, 0, 'Colonel Jules');
INSERT INTO `creature_text` VALUES (22448, 0, 0, 'I am Rexxar, son of the Mok\'Nathal, champion of the Horde.', 14, 0, 100, 397, 0, 0, 0, 20318, 0, 'Rexxar');
INSERT INTO `creature_text` VALUES (22448, 1, 0, 'And their torment at your hands is at an end. By my name, I shall put an end to your life.', 14, 0, 100, 113, 0, 0, 0, 20319, 0, 'Rexxar');
INSERT INTO `creature_text` VALUES (22448, 2, 0, 'Prepare yourself for the end.', 14, 0, 100, 15, 0, 0, 0, 20320, 0, 'Rexxar');
INSERT INTO `creature_text` VALUES (22448, 3, 0, 'I could not have achieved this victory without you. We will speak back at Thunderlord Stronghold.', 14, 0, 100, 397, 0, 0, 0, 20326, 0, 'Rexxar');
INSERT INTO `creature_text` VALUES (22452, 0, 0, 'You have brought me back too soon! I am weak yet... What is this! Who... It matters not! DIE!', 12, 0, 100, 1, 0, 0, 0, 20298, 0, 'Reanimated Exarch');
INSERT INTO `creature_text` VALUES (22454, 0, 0, 'Vengeance will not be yours, $n!', 12, 0, 100, 15, 0, 0, 0, 20301, 0, 'Fel Spirit to Player');
INSERT INTO `creature_text` VALUES (22458, 0, 0, 'Aright, listen up! Form a circle around me and move out!', 12, 0, 100, 0, 0, 0, 0, 20314, 0, '');
INSERT INTO `creature_text` VALUES (22458, 1, 0, 'Aright, $r, just keep us safe from harm while we work. We\'ll pay you when we return.', 12, 0, 100, 0, 0, 0, 0, 20328, 0, '');
INSERT INTO `creature_text` VALUES (22458, 2, 0, 'The dig site is just north of here.', 12, 0, 100, 0, 0, 0, 0, 20329, 0, '');
INSERT INTO `creature_text` VALUES (22458, 3, 0, 'We\'re here! Start diggin\'!', 12, 0, 100, 0, 0, 0, 0, 20330, 0, '');
INSERT INTO `creature_text` VALUES (22458, 4, 0, 'I think there\'s somethin\' buried here, beneath the sand!', 12, 0, 100, 396, 0, 0, 0, 20331, 0, '');
INSERT INTO `creature_text` VALUES (22458, 5, 0, 'Almost got it!', 12, 0, 100, 396, 0, 0, 0, 20332, 0, '');
INSERT INTO `creature_text` VALUES (22458, 6, 0, 'By Brann\'s brittle bananas! What is it!? It... It looks like a drum.', 12, 0, 100, 396, 0, 0, 0, 20333, 0, '');
INSERT INTO `creature_text` VALUES (22458, 7, 0, 'Have ye gone mad? You expect me to leave behind a drum without first beatin\' on it? Not this son of Ironforge! No sir!', 12, 0, 100, 0, 0, 0, 0, 20337, 0, '');
INSERT INTO `creature_text` VALUES (22458, 8, 0, 'Shut yer yaps! I\'m gonna bang on this drum and that\'s that!', 12, 0, 100, 0, 0, 0, 0, 20344, 0, '');
INSERT INTO `creature_text` VALUES (22458, 9, 0, 'IN YOUR FACE! I told you there was somethin\' here!', 12, 0, 100, 0, 0, 0, 0, 20348, 0, '');
INSERT INTO `creature_text` VALUES (22458, 10, 0, 'Don\'t just stand there! Help $g him:her; out!', 12, 0, 100, 0, 0, 0, 0, 20361, 0, '');
INSERT INTO `creature_text` VALUES (22458, 11, 0, 'You\'ve been a tremendous help, $r! Let\'s get out of here before more of those things show up! I\'ll let Dwarfowitz know you did the job asked of ya\' admirably.', 12, 0, 100, 0, 0, 0, 0, 20363, 0, '');
INSERT INTO `creature_text` VALUES (22459, 1, 0, 'Praise the naaru, I\'m saved. I will try to make it clear on my own.', 12, 7, 100, 5, 0, 0, 0, 20143, 0, 'Freed Sha\'tar Warrior');
INSERT INTO `creature_text` VALUES (22459, 1, 1, 'I\'m hurt, but I can still run. Thank you, stranger.', 12, 7, 100, 5, 0, 0, 0, 20144, 0, 'Freed Sha\'tar Warrior');
INSERT INTO `creature_text` VALUES (22459, 1, 2, 'My thanks, stranger.', 12, 7, 100, 5, 0, 0, 0, 20145, 0, 'Freed Sha\'tar Warrior');
INSERT INTO `creature_text` VALUES (22459, 1, 3, 'I\'m too weak to fight with you.  Please find my friends.', 12, 7, 100, 5, 0, 0, 0, 20146, 0, 'Freed Sha\'tar Warrior');
INSERT INTO `creature_text` VALUES (22464, 0, 0, 'Wow... a drum.', 12, 0, 100, 0, 0, 0, 0, 20334, 0, '');
INSERT INTO `creature_text` VALUES (22464, 1, 0, 'This discovery will surely rock the foundation of modern archaeology.', 12, 0, 100, 0, 0, 0, 0, 20335, 0, '');
INSERT INTO `creature_text` VALUES (22464, 2, 0, 'Yea, great. Can we leave now? This desert is giving me hives.', 12, 0, 100, 0, 0, 0, 0, 20336, 0, '');
INSERT INTO `creature_text` VALUES (22464, 3, 0, 'This reminds me of that one time where you made us search Silithus for evidence of sand gnomes.', 12, 0, 100, 0, 0, 0, 0, 20339, 0, '');
INSERT INTO `creature_text` VALUES (22464, 4, 0, 'Or that time when you told us that you\'d discovered the cure for the plague of the 20th century. What is that even? 20th century?', 12, 0, 100, 0, 0, 0, 0, 20340, 0, '');
INSERT INTO `creature_text` VALUES (22464, 5, 0, 'I don\'t think it can top the one time where he told us that he\'d heard that Arthas\'s \"cousin\'s\" skeleton was frozen beneath a glacier in Winterspring. I\'ll never forgive you for that one, Letoll. I mean honestly... Arthas\'s cousin?', 12, 0, 100, 0, 0, 0, 0, 20342, 0, '');
INSERT INTO `creature_text` VALUES (22464, 6, 0, 'I dunno. It can\'t possibly beat the time he tried to convince us that we\'re all actually a figment of some being\'s imagination and that they only use us for their own personal amusement. That went over well during dinner with the family.', 12, 0, 100, 0, 0, 0, 0, 20343, 0, '');
INSERT INTO `creature_text` VALUES (22464, 7, 0, 'Say, do you guys hear that?', 12, 0, 100, 0, 0, 0, 0, 20346, 0, '');
INSERT INTO `creature_text` VALUES (22473, 0, 0, 'It\'s only right that you know the name of the one who will take your life.', 14, 0, 100, 1, 0, 0, 0, 20323, 0, 'Baron Sablemane');
INSERT INTO `creature_text` VALUES (22473, 1, 0, 'Baron Sablemane. It will be on your lips as you gasp your dying breath.', 14, 0, 100, 1, 0, 0, 0, 20324, 0, 'Baron Sablemane');
INSERT INTO `creature_text` VALUES (22473, 2, 0, 'Let us begin.', 14, 0, 100, 15, 0, 0, 0, 20325, 0, 'Baron Sablemane');
INSERT INTO `creature_text` VALUES (22473, 3, 0, 'We will speak again, but away from here.', 14, 0, 100, 1, 0, 0, 0, 20327, 0, 'Baron Sablemane');
INSERT INTO `creature_text` VALUES (22483, 0, 0, 'AAAAEEEEEEIIIIIIII!!!!', 12, 7, 100, 5, 0, 0, 0, 20308, 0, 'Sand Gnome');
INSERT INTO `creature_text` VALUES (22483, 0, 1, 'AHHHH-YAAA-YAAA-YAAA!!!', 12, 7, 100, 5, 0, 0, 0, 20309, 0, 'Sand Gnome');
INSERT INTO `creature_text` VALUES (22483, 0, 2, 'AWOOOOGAAAA!', 12, 7, 100, 5, 0, 0, 0, 20310, 0, 'Sand Gnome');
INSERT INTO `creature_text` VALUES (22483, 0, 3, 'AYYAYAAYAA!', 12, 7, 100, 5, 0, 0, 0, 20311, 0, 'Sand Gnome');
INSERT INTO `creature_text` VALUES (22485, 0, 0, 'Guards!', 14, 6, 100, 0, 0, 0, 0, 4583, 0, 'Halu');
