INSERT INTO `creature_text` VALUES (22820, 0, 0, 'Akama, my friend!  Have you been informed?  I was found out by Vashj\'s followers.  Our secret is in peril!', 12, 0, 100, 0, 0, 11611, 0, 20498, 0, 'Seer Olum to Player');
INSERT INTO `creature_text` VALUES (22820, 1, 0, 'Illidan is looking for me.  I am not safe here... none of us are.', 12, 0, 100, 1, 0, 11612, 0, 20502, 0, 'Seer Olum to Player');
INSERT INTO `creature_text` VALUES (22820, 2, 0, 'Illidan will find me and when he does... he will pry the Ashtongue secret from me - such is the power of the Betrayer!  No... there is only one way.  My usefulness to the cause has come to an end. I must venture into the spirit world!', 12, 0, 100, 6, 0, 11613, 0, 20499, 0, 'Seer Olum to Player');
INSERT INTO `creature_text` VALUES (22820, 3, 0, 'My decision is final.  I will not be dissuaded.  I have devoted my life to our plan.  To jeopardize it now would betray all who have died for the cause long before me.', 12, 0, 100, 274, 0, 11614, 0, 20500, 0, 'Seer Olum to Player');
INSERT INTO `creature_text` VALUES (22820, 4, 0, 'I thank you, Akama.  You honor me by allowing me this moment, surrounded by my brothers.  Farewell, friend.  Guide our people well.', 12, 0, 100, 2, 0, 11615, 0, 20501, 0, 'Seer Olum to Player');
INSERT INTO `creature_text` VALUES (22832, 0, 0, 'There are few left in the world who can prepare the Ward of Waking, $N. We are fortunate that I am among them.', 12, 0, 100, 1, 0, 0, 0, 20516, 0, 'Morthis Whisperwing');
INSERT INTO `creature_text` VALUES (22832, 1, 0, '%s kneels, filling the vial with water from the moonwell.', 16, 0, 100, 0, 0, 0, 0, 20512, 0, 'Morthis Whisperwing');
INSERT INTO `creature_text` VALUES (22832, 2, 0, '%s combines the herbs with the moonwell water to create the Ward of Waking', 16, 0, 100, 0, 0, 0, 0, 20517, 0, 'Morthis Whisperwing');
INSERT INTO `creature_text` VALUES (22832, 3, 0, 'The potion is finished. I can only hope Clintar forgives us for interrupting his sojourn in the Emerald Dream.', 12, 0, 100, 1, 0, 0, 0, 20513, 0, 'Morthis Whisperwing');
INSERT INTO `creature_text` VALUES (22834, 0, 0, '%s snaps up to a kneeling position, but seems as though he\'s somewhere else.', 16, 0, 100, 0, 0, 0, 0, 20523, 0, 'Clintar Dreamwalker (line 0)');
INSERT INTO `creature_text` VALUES (22834, 1, 0, 'What are you doing in Ysera\'s realm, interloper? Leave us, I say! Your kind are not welcome here!', 12, 0, 100, 5, 0, 0, 0, 20524, 0, 'Clintar Dreamwalker (line 1)');
INSERT INTO `creature_text` VALUES (22834, 2, 0, '%s gets to his feet, beginning to recognize his surroundings.', 16, 0, 100, 0, 0, 0, 0, 20525, 0, 'Clintar Dreamwalker (line 2)');
INSERT INTO `creature_text` VALUES (22834, 3, 0, 'You -- you\'re not one of them! Let\'s get out of sight before they find us! Hurry!', 12, 0, 100, 1, 0, 0, 0, 20538, 0, 'Clintar Dreamwalker (line 3)');
INSERT INTO `creature_text` VALUES (22834, 4, 0, 'They\'re following me, $r! Quickly, tell me why you\'re here before we\'re discovered! If they catch up to me, it will mean my death and yours!', 12, 0, 100, 1, 0, 0, 0, 20532, 0, 'Clintar Dreamwalker (line 4)');
INSERT INTO `creature_text` VALUES (22844, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Ashtongue Battlelord');
INSERT INTO `creature_text` VALUES (22862, 0, 0, 'Come closer, demon! Death awaits!', 12, 0, 100, 0, 0, 0, 0, 20670, 0, 'Caalen');
INSERT INTO `creature_text` VALUES (22862, 0, 1, 'Illidan\'s lapdogs! Destroy them all!', 12, 0, 100, 0, 0, 0, 0, 20669, 0, 'Caalen');
INSERT INTO `creature_text` VALUES (22862, 0, 2, 'I\'ve a message for your master, scum!', 12, 0, 100, 0, 0, 0, 0, 20672, 0, 'Caalen');
INSERT INTO `creature_text` VALUES (22862, 0, 3, 'Pathetic worm... your master\'s days are over!', 12, 0, 100, 0, 0, 0, 0, 20671, 0, 'Caalen');
INSERT INTO `creature_text` VALUES (22862, 0, 4, 'For Xi\'ri!  For the Sha\'tar!', 12, 0, 100, 0, 0, 0, 0, 20667, 0, 'Caalen');
INSERT INTO `creature_text` VALUES (22862, 0, 5, 'Your end is at hand, Illidari!', 12, 0, 100, 0, 0, 0, 0, 20668, 0, 'Caalen');
INSERT INTO `creature_text` VALUES (22864, 0, 0, 'Onward, Scryers! Show Illidan\'s lackeys the temper of our steel!', 14, 0, 100, 0, 0, 0, 0, 20696, 0, 'Fyra Downstar');
INSERT INTO `creature_text` VALUES (22865, 0, 0, 'You disappoint me, Akama.  I wanted to question the treacherous worm myself!  I\'m beginning to question your allegiance, Broken.', 12, 0, 100, 1, 0, 11608, 0, 20628, 0, 'Illidan\'s Presence to Player');
INSERT INTO `creature_text` VALUES (22865, 1, 0, 'I do not sense lies in your voice, Akama.  That Kael\'thas would betray me does not come as a big surprise - I\'m not as oblivious as some would think. That one of your own was involved with him puts your loyalties into question.', 12, 0, 100, 5, 0, 11609, 0, 20629, 0, 'Illidan\'s Presence to Player');
INSERT INTO `creature_text` VALUES (22865, 2, 0, 'Send your Ashtongue into Tempest Keep and slay his most prized possession, the phoenix known as Al\'ar.  I must know that you\'re not on his side.  Do not think of betraying me, Broken.  We both know who owns your soul!', 12, 0, 100, 1, 0, 11610, 0, 20630, 0, 'Illidan\'s Presence to Player');
INSERT INTO `creature_text` VALUES (22871, 0, 0, 'I was the first you know. For me the wheel of death has spun many times. So much time has passed... I have a lot of catching up to do.', 14, 0, 100, 0, 0, 11512, 0, 21098, 0, 'Teron Gorefiend SAY_INTRO');
INSERT INTO `creature_text` VALUES (22871, 1, 0, 'Vengeance is mine!', 14, 0, 100, 0, 0, 11513, 0, 21097, 0, 'Teron Gorefiend SAY_AGGRO');
INSERT INTO `creature_text` VALUES (22871, 2, 0, 'I have use for you...', 14, 0, 100, 0, 0, 11514, 0, 21099, 0, 'Teron Gorefiend SAY_SLAY1');
INSERT INTO `creature_text` VALUES (22871, 2, 1, 'It gets worse.', 14, 0, 100, 0, 0, 11515, 0, 21100, 0, 'Teron Gorefiend SAY_SLAY2');
INSERT INTO `creature_text` VALUES (22871, 3, 0, 'What are you afraid of?', 14, 0, 100, 0, 0, 11517, 0, 21102, 0, 'Teron Gorefiend SAY_INCINERATE1');
INSERT INTO `creature_text` VALUES (22871, 3, 1, 'You will show the proper respect!', 14, 0, 100, 0, 0, 11520, 0, 21105, 0, 'Teron Gorefiend SAY_INCINERATE2');
INSERT INTO `creature_text` VALUES (22871, 4, 0, 'Death really isn\'t so bad.', 14, 0, 100, 0, 0, 11516, 0, 21101, 0, 'Teron Gorefiend SAY_DOOM_BLOSSOM1');
INSERT INTO `creature_text` VALUES (22871, 4, 1, 'I have something for you...', 14, 0, 100, 0, 0, 11519, 0, 21104, 0, 'Teron Gorefiend SAY_DOOM_BLOSSOM2');
INSERT INTO `creature_text` VALUES (22871, 5, 0, 'Give in.', 14, 0, 100, 0, 0, 11518, 0, 21103, 0, 'Teron Gorefiend SPELL_CRUSHING_SHADOWS');
INSERT INTO `creature_text` VALUES (22871, 6, 0, 'The wheel... spins... again.', 14, 0, 100, 0, 0, 11521, 0, 21106, 0, 'Teron Gorefiend SAY_DEATH');
INSERT INTO `creature_text` VALUES (22887, 0, 0, 'You will die in the name of Lady Vashj!', 14, 0, 100, 0, 0, 11450, 0, 21085, 0, 'najentus SAY_AGGRO');
INSERT INTO `creature_text` VALUES (22887, 1, 0, 'Stick around...', 14, 0, 100, 0, 0, 11451, 0, 21089, 0, 'najentus SAY_NEEDLE1');
INSERT INTO `creature_text` VALUES (22887, 1, 1, 'I\'ll deal with you later.', 14, 0, 100, 0, 0, 11452, 0, 21090, 0, 'najentus SAY_NEEDLE2');
INSERT INTO `creature_text` VALUES (22887, 2, 0, 'Your success was short-lived!', 14, 0, 100, 0, 0, 11455, 0, 21091, 0, 'najentus SAY_SLAY1');
INSERT INTO `creature_text` VALUES (22887, 2, 1, 'Time for you to go.', 14, 0, 100, 0, 0, 11456, 0, 21092, 0, 'najentus SAY_SLAY2');
INSERT INTO `creature_text` VALUES (22887, 3, 0, 'Be\'lanen dalorai!', 14, 0, 100, 0, 0, 11453, 0, 21088, 0, 'najentus SAY_SPECIAL1');
INSERT INTO `creature_text` VALUES (22887, 3, 1, 'Blood... will... flow!', 14, 0, 100, 0, 0, 11454, 0, 21087, 0, 'najentus SAY_SPECIAL2');
INSERT INTO `creature_text` VALUES (22887, 4, 0, 'Bal, lamer zhita!', 14, 0, 100, 0, 0, 11457, 0, 21095, 0, 'najentus SAY_ENRAGE1');
INSERT INTO `creature_text` VALUES (22887, 4, 1, 'My patience has run out! Die! Die!', 14, 0, 100, 0, 0, 11458, 0, 21096, 0, 'High Warlord Naj\'entus SAY_ENRAGE2');
INSERT INTO `creature_text` VALUES (22887, 5, 0, 'Lord Illidan will... crush you!', 14, 0, 100, 0, 0, 11459, 0, 21093, 0, 'High Warlord Naj\'entus SAY_DEATH');
