INSERT INTO `creature_text` VALUES (22917, 14, 0, 'You have won... Maiev. But the huntress... is nothing without the hunt. You... are nothing... without me.', 14, 0, 100, 0, 0, 11478, 0, 21506, 0, 'Illidan SAY_ILLIDAN_DEFEATED');
INSERT INTO `creature_text` VALUES (22924, 0, 0, 'It would\'ve taken years to find the Book of the Raven without you, young sparrowhawk. Return to your home in Nagrand with our gratitude.', 12, 0, 100, 1, 0, 0, 0, 20752, 0, 'Arthorn Windsong');
INSERT INTO `creature_text` VALUES (22924, 1, 0, 'This is it! This is really it -- the Book of the Raven!', 12, 0, 100, 1, 0, 0, 0, 20753, 0, 'Arthorn Windsong');
INSERT INTO `creature_text` VALUES (22924, 2, 0, 'There\'s something else here, too. After a description of a bloody battle and the downfall of the raven, there\'s a prophecy. Anzu will haunt the dreams of his enemies, twisting them to effect a return to this world...', 12, 0, 100, 1, 0, 0, 0, 20757, 0, 'Arthorn Windsong');
INSERT INTO `creature_text` VALUES (22924, 3, 0, 'It\'s all here... how the raven\'s followers captured and subdued the spirits of rivals, trapping them forever within stone statues! The tablet even mentions the raven god\'s name -- Anzu. I\'d thought it was lost to history!', 12, 0, 100, 1, 0, 0, 0, 20756, 0, 'Arthorn Windsong');
INSERT INTO `creature_text` VALUES (22924, 4, 0, 'The tablet has the confirmation we need, just as I thought. Now, we have to use this information to draw out and defeat this Anzu before he can do any more damage to the Emerald Dream!', 12, 0, 100, 1, 0, 0, 0, 20758, 0, 'Arthorn Windsong');
INSERT INTO `creature_text` VALUES (22924, 5, 0, 'Since the sparrowhawks have proven such a success in hunting down the raven stones, Watcher Elaira has trained another one to relay my voice at a great distance. He can help us communicate as you investigate the bird spirits revealed in the tablet!', 12, 0, 100, 1, 0, 0, 0, 21607, 0, 'Arthorn Windsong');
