INSERT INTO `creature_text` VALUES (23054, 3, 0, 'Lay down your arms and succumb to the might of Kil\'jaeden!', 14, 0, 100, 14, 0, 0, 0, 20796, 0, 'Kael\'thas Sunstrider to Player');
INSERT INTO `creature_text` VALUES (23083, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Shadowmoon Riding Hound');
INSERT INTO `creature_text` VALUES (23089, 0, 0, 'This door is all that stands between us and the Betrayer.  Stand aside, friends.', 12, 0, 100, 1, 0, 0, 0, 21563, 0, 'SAY_AKAMA_DOOR');
INSERT INTO `creature_text` VALUES (23089, 1, 0, 'I cannot do this alone...', 12, 0, 100, 274, 0, 0, 0, 21548, 0, 'SAY_AKAMA_ALONE');
INSERT INTO `creature_text` VALUES (23089, 2, 0, 'I thank you for your aid, brothers.  Our people will be redeemed!', 12, 0, 100, 66, 0, 0, 0, 21554, 0, 'SAY_AKAMA_SALUTE');
INSERT INTO `creature_text` VALUES (23089, 3, 0, 'Be wary, friends. The Betrayer meditates in the court just beyond.', 12, 0, 100, 0, 0, 11388, 0, 21555, 0, 'SAY_AKAMA_BETRAYER');
INSERT INTO `creature_text` VALUES (23089, 4, 0, 'We\'ve come to end your reign, Illidan. My people, and all of Outland, shall be free!', 14, 0, 100, 25, 0, 11389, 0, 20893, 0, 'SAY_AKAMA_FREE');
INSERT INTO `creature_text` VALUES (23089, 5, 0, 'The time has come! The moment is at hand!', 14, 0, 100, 22, 0, 11380, 0, 20894, 0, 'SAY_AKAMA_TIME_HAS_COME');
INSERT INTO `creature_text` VALUES (23089, 6, 0, 'I will deal with these mongrels! Strike now, friends! Strike at the Betrayer!', 14, 0, 100, 22, 0, 11390, 0, 21250, 0, 'SAY_AKAMA_MINIONS');
INSERT INTO `creature_text` VALUES (23089, 7, 0, 'The Light will bless these dismal halls once again.... I swear it.', 14, 0, 100, 1, 0, 11387, 0, 21514, 0, 'SAY_AKAMA_LIGHT');
INSERT INTO `creature_text` VALUES (23089, 8, 0, 'Let us finish what we\'ve started.  I will lead you to Illidan\'s abode once you\'ve recovered your strength.', 12, 0, 100, 1, 0, 0, 0, 21520, 0, 'SAY_AKAMA_FINISH');
INSERT INTO `creature_text` VALUES (23139, 0, 0, 'Come, $n. Lord Stormrage awaits.', 12, 0, 100, 0, 0, 0, 0, 21798, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 1, 0, 'Warriors of Dragonmaw, gather \'round! One among you has attained the rank of highlord! Bow your heads in reverence! Show your respect and allegiance to Highlord $n!', 14, 0, 100, 0, 0, 0, 0, 21799, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 2, 0, 'Lord Illidan will be here shortly.', 12, 0, 100, 0, 0, 0, 0, 21800, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 3, 0, 'All hail Lord Illidan!', 14, 0, 100, 0, 0, 0, 0, 21802, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 4, 0, 'Lord Illidan, this is the Dragonmaw that I, and others, have told you about. He will lead us to victory!', 12, 0, 100, 0, 0, 0, 0, 21803, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 5, 0, 'But... My lord, I do not understand. $n... He is the orc that has...', 12, 0, 100, 0, 0, 0, 0, 21805, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 6, 0, 'It will be done, my lord.', 12, 0, 100, 0, 0, 0, 0, 21823, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 7, 0, 'So you thought to make a fool of Mor\'ghor, eh? Before you are delivered to Lord Illidan, you will feel pain that you could not know to exist. I will take pleasure in exacting my own vengeance.', 12, 0, 100, 0, 0, 0, 0, 21824, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 8, 0, 'I will not drag this out any further than it needs, Lady Sinestra. You have bent my ear, now tell me what it is that you want from Dragonmaw.', 12, 0, 100, 0, 0, 0, 0, 21307, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 9, 0, 'The... The master? He lives?', 12, 0, 100, 0, 0, 0, 0, 21310, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 10, 0, '%s stammers.', 16, 0, 100, 0, 0, 0, 0, 21313, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23139, 11, 0, 'Ye... Yes, yes... Of course. We need only the crystals and ore from this place. There is... We will need mounts.', 12, 0, 100, 0, 0, 0, 0, 21314, 0, 'Overlord Mor\'ghor');
INSERT INTO `creature_text` VALUES (23141, 0, 0, 'You will not harm the $g boy:girl;, Mor\'ghor! Quickly, $n, climb on my back!', 12, 0, 100, 0, 0, 0, 0, 21825, 0, 'Yarzill');
INSERT INTO `creature_text` VALUES (23146, 0, 0, 'Who... Who is you?? What you want?', 12, 0, 100, 0, 0, 0, 0, 21300, 0, 'Dragonmaw Enforcer');
INSERT INTO `creature_text` VALUES (23146, 1, 0, 'Yes... Yes we move now... Please no hurt us...', 12, 0, 100, 0, 0, 0, 0, 21302, 0, 'Dragonmaw Enforcer');
INSERT INTO `creature_text` VALUES (23152, 0, 0, 'Pitiful wretches. You dared invade Illidan\'s temple? Very well, I shall make it your death bed!', 14, 0, 100, 0, 0, 0, 0, 20719, 0, 'Vagath');
INSERT INTO `creature_text` VALUES (23152, 1, 0, 'You\'ve sealed you fate, Akama. The Master will learn from your betrayal!', 14, 0, 100, 0, 0, 0, 0, 20745, 0, 'Vagath');
INSERT INTO `creature_text` VALUES (23162, 0, 0, 'Furious winds envelop Vakkiz as he writhes menacingly towards you.', 16, 0, 100, 0, 0, 0, 0, 21905, 0, 'Vakkiz the Windrager');
INSERT INTO `creature_text` VALUES (23162, 1, 0, '%s\'s flesh shatters in an unearthly rage!', 16, 0, 100, 0, 0, 0, 0, 21075, 0, 'Vakkiz the Windrager');
INSERT INTO `creature_text` VALUES (23163, 0, 0, '%s bares her fangs and hisses menacingly at those around her.', 16, 0, 100, 0, 0, 0, 0, 21902, 0, 'Gezzarak the Huntress');
INSERT INTO `creature_text` VALUES (23163, 1, 0, '%s focuses on $n.', 16, 0, 100, 0, 0, 0, 0, 21077, 0, 'Gezzarak the Huntress');
INSERT INTO `creature_text` VALUES (23191, 0, 0, 'Broken of the Ashtongue tribe, your leader speaks!', 14, 0, 100, 0, 0, 0, 0, 21342, 0, 'Akama SAY_BROKEN_FREE_0');
INSERT INTO `creature_text` VALUES (23191, 1, 0, 'The Betrayer no longer holds sway over us.  His dark magic over the Ashtongue soul has been destroyed!', 14, 0, 100, 0, 0, 0, 0, 21343, 0, 'Akama SAY_BROKEN_FREE_1');
INSERT INTO `creature_text` VALUES (23191, 2, 0, 'Come out from the shadows!  I\'ve returned to lead you against our true enemy!  Shed your chains and raise your weapons against your Illidari masters!', 14, 0, 100, 0, 0, 0, 0, 21344, 0, 'Akama SAY_BROKEN_FREE_2');
INSERT INTO `creature_text` VALUES (23191, 3, 0, 'I will not last much longer!', 14, 0, 100, 0, 0, 11385, 0, 21784, 0, 'Akama SAY_LOW_HEALTH');
INSERT INTO `creature_text` VALUES (23191, 4, 0, 'No! Not yet!', 14, 0, 100, 0, 0, 11386, 0, 21785, 0, 'Akama SAY_DEAD');
INSERT INTO `creature_text` VALUES (23196, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Bonechewer Behemoth');
INSERT INTO `creature_text` VALUES (23197, 0, 0, 'That is for Naisha!', 14, 0, 100, 0, 0, 11493, 0, 21489, 0, 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_TAUNT');
INSERT INTO `creature_text` VALUES (23197, 0, 1, 'Bleed as I have bled!', 14, 0, 100, 0, 0, 11494, 0, 0, 0, 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_TAUNT');
INSERT INTO `creature_text` VALUES (23197, 0, 3, 'Meet your end, demon!', 14, 0, 100, 0, 0, 11500, 0, 0, 0, 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_TAUNT');
INSERT INTO `creature_text` VALUES (23197, 1, 0, 'Their fury pales before mine, Illidan. We have some unsettled business between us.', 14, 0, 100, 6, 0, 11491, 0, 21070, 0, 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_APPEAR');
INSERT INTO `creature_text` VALUES (23197, 2, 0, 'My long hunt is finally over. Today, Justice will be done!', 14, 0, 100, 5, 0, 11492, 0, 21071, 0, 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_JUSTICE');
INSERT INTO `creature_text` VALUES (23197, 3, 0, 'There shall be no prison for you this time!', 14, 0, 100, 0, 0, 11495, 0, 22208, 0, 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_TRAP');
INSERT INTO `creature_text` VALUES (23197, 4, 0, '%s falls to the floor.', 16, 0, 100, 0, 0, 0, 0, 21317, 0, 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_DOWN');
INSERT INTO `creature_text` VALUES (23197, 5, 0, 'It is finished. You are beaten.', 14, 0, 100, 0, 0, 11496, 0, 21507, 0, 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_FINISHED');
INSERT INTO `creature_text` VALUES (23197, 6, 0, 'He\'s right. I feel nothing... I am... nothing.', 14, 0, 100, 0, 0, 11497, 0, 21508, 0, 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_OUTRO');
INSERT INTO `creature_text` VALUES (23197, 7, 0, 'Farewell, champions.', 14, 0, 100, 0, 0, 11498, 0, 21509, 0, 'Maiev Shadowsong SAY_MAIEV_SHADOWSONG_FAREWELL');
INSERT INTO `creature_text` VALUES (23208, 0, 0, 'Maybe one day $n will teach us a few of $g his : her; tricks?', 12, 0, 100, 0, 0, 0, 0, 21942, 0, 'Skyguard Pyrotechnician');
INSERT INTO `creature_text` VALUES (23222, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Bonechewer Brawler');
INSERT INTO `creature_text` VALUES (23224, 0, 1, 'Yes, unless I have anything to do with it.', 15, 0, 100, 0, 0, 0, 0, 21157, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 2, 'I see that happening sometime between tomorrow and the next decade. Definitely.', 15, 0, 100, 0, 0, 0, 0, 21158, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 3, 'Looks good for you...and bad for me.', 15, 0, 100, 0, 0, 0, 0, 21160, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 4, 'I\'ve consulted my fellow imps, and we think YES, except for that one imp.', 15, 0, 100, 0, 0, 0, 0, 21161, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 5, 'The outlook is positive, but I\'m still negative.', 15, 0, 100, 0, 0, 0, 0, 21162, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 6, 'It pains me to say this, but \"Yes\".', 15, 0, 100, 0, 0, 0, 0, 21163, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 7, 'When dwarves fly! They do? Then yes!', 15, 0, 100, 0, 0, 0, 0, 21164, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 8, 'Sure, but you\'re not going to like it.', 15, 0, 100, 0, 0, 0, 0, 21165, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 9, 'Be quiet \'bout what you hear and see around here, $r.', 15, 0, 100, 0, 0, 0, 0, 21166, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 10, 'Unfortunately... yes.', 15, 0, 100, 0, 0, 0, 0, 21169, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 11, 'I can\'t see why not, although, I can\'t see a lot of things right now.', 15, 0, 100, 0, 0, 0, 0, 21170, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 12, 'I would bet your soul on it.', 15, 0, 100, 0, 0, 0, 0, 21171, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 13, 'Yes, but if anyone asks... It wasn\'t me who told you.', 15, 0, 100, 0, 0, 0, 0, 21172, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 14, 'Didn\'t you already ask that once? Yes already!', 15, 0, 100, 0, 0, 0, 0, 21173, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 15, 'Please... Is Kil\'jaeden red?', 15, 0, 100, 0, 0, 0, 0, 21175, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 16, 'Yeah, sure. You just keep thinking that.', 15, 0, 100, 0, 0, 0, 0, 21176, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 17, 'I suppose.', 15, 0, 100, 0, 0, 0, 0, 21177, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 18, 'Definitely.', 15, 0, 100, 0, 0, 0, 0, 21178, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 19, 'Jump three times and dance for ten minutes and it will definitely happen!', 15, 0, 100, 0, 0, 0, 0, 21179, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 20, 'Word on the peninsula is YES!', 15, 0, 100, 0, 0, 0, 0, 21180, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 21, 'Oh, that one\'s for sure.', 15, 0, 100, 0, 0, 0, 0, 21181, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 22, 'Yes, but not in the way you imagine.', 15, 0, 100, 0, 0, 0, 0, 21182, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 23, 'Yes, yes, a thousand times, yes already!', 15, 0, 100, 0, 0, 0, 0, 21183, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 24, 'Yes, now stop pestering me!', 15, 0, 100, 0, 0, 0, 0, 21184, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 25, 'The answer will be a yes if you let me out of here.', 15, 0, 100, 0, 0, 0, 0, 21185, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 26, 'It\'s as sure as the warts on my backside!', 15, 0, 100, 0, 0, 0, 0, 21186, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 27, 'The answer is yes in here, I don\'t see why it would be any different out there.', 15, 0, 100, 0, 0, 0, 0, 21187, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 28, 'Three words - \"ab - so - lutely\"!', 15, 0, 100, 0, 0, 0, 0, 21188, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 29, 'Yes, but I hoped I would never have to answer that.', 15, 0, 100, 0, 0, 0, 0, 21189, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 30, 'Ask me again later - I\'m trying to scratch my nose and it\'s hard to concentrate.', 15, 0, 100, 0, 0, 0, 0, 21190, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 31, 'Why should I answer that?  Do you tell fortunes when people shake YOU?', 15, 0, 100, 0, 0, 0, 0, 21191, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 32, 'Yes, it will rain.  That\'s not what you asked?  Too bad!', 15, 0, 100, 0, 0, 0, 0, 21192, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 33, 'I\'m sorry, I can only speak Demonic.', 15, 0, 100, 0, 0, 0, 0, 21193, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 34, 'Yes!  I mean no!  I mean... which answer gets me out of here?', 15, 0, 100, 0, 0, 0, 0, 21194, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 35, 'Yes, No, Maybe so.', 15, 0, 100, 0, 0, 0, 0, 21195, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 36, 'It won\'t matter, you\'ll be dead by tomorrow.', 15, 0, 100, 0, 0, 0, 0, 21196, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 37, 'You should be asking \"Is that rogue behind me going to kill me?\"', 15, 0, 100, 0, 0, 0, 0, 21197, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 38, 'It\'s times like these that I wish I had a longer cooldown.', 15, 0, 100, 0, 0, 0, 0, 21198, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 39, '%s shrugs. Who knows?', 15, 0, 100, 0, 0, 0, 0, 21199, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 40, 'It\'s like my mother always said: [Demonic] \"Razxx khaj jhashxx xashjx.\"', 15, 0, 100, 0, 0, 0, 0, 21205, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 41, '%s is ignoring you.', 15, 0, 100, 0, 0, 0, 0, 21206, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 42, 'Why me?  Why not a goblin, or a gnome...', 15, 0, 100, 0, 0, 0, 0, 21207, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 43, 'What happens in the twisting nether, stays in the twisting nether.', 15, 0, 100, 0, 0, 0, 0, 21208, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 44, 'Avoid taking unnecessary gambles. Your lucky numbers are two, two and half, and eleven-teen.', 15, 0, 100, 0, 0, 0, 0, 21209, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 45, 'Wouldn\'t you like to know?', 15, 0, 100, 0, 0, 0, 0, 21210, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 46, 'Oh, oh, oh!  I can see this one clearly...  Nah, lost it.', 15, 0, 100, 0, 0, 0, 0, 21211, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 47, 'This was NOT in my contract!', 15, 0, 100, 0, 0, 0, 0, 21212, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 48, 'XRA RAHKI MAZIZRA!', 15, 0, 100, 0, 0, 0, 0, 21213, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 49, '4 8 15 16 23 42', 15, 0, 100, 0, 0, 0, 0, 21214, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 50, 'Are you making fun of me?', 15, 0, 100, 0, 0, 0, 0, 21215, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 51, 'What kind of imp do you think I am?', 15, 0, 100, 0, 0, 0, 0, 21216, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 52, 'Say please.', 15, 0, 100, 0, 0, 0, 0, 21217, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 53, 'Want to trade places?', 15, 0, 100, 0, 0, 0, 0, 21218, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 54, 'Do you ask this question to everything that\'s trapped in a ball?', 15, 0, 100, 0, 0, 0, 0, 21219, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 55, 'Hey! You try arranging furniture with some jerk shaking your house!', 15, 0, 100, 0, 0, 0, 0, 21220, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 56, 'I can make that happen. Just sign below the dotted line...', 15, 0, 100, 0, 0, 0, 0, 21221, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 57, 'Reply hazy and slightly damp. Dry thoroughly and try again.', 15, 0, 100, 0, 0, 0, 0, 21222, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 58, 'Concentrate (on releasing me from this infernal prison) and try again later.', 15, 0, 100, 0, 0, 0, 0, 21223, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 59, 'Please insert 25 silver pieces and try again.', 15, 0, 100, 0, 0, 0, 0, 21224, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 60, 'Are you my pal, Danny?', 15, 0, 100, 0, 0, 0, 0, 21225, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 61, 'You remember the time you tried to drill that hole in your head?', 15, 0, 100, 0, 0, 0, 0, 21226, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 62, 'Oh that\'s right, don\'t make any effort to make your own fortune!', 15, 0, 100, 0, 0, 0, 0, 21227, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 63, 'Two words - imp-possible!', 15, 0, 100, 0, 0, 0, 0, 21228, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 64, 'You need Arcane Intellect, because that answer is obvious!  NO!', 15, 0, 100, 0, 0, 0, 0, 21229, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 65, 'Not on your life!', 15, 0, 100, 0, 0, 0, 0, 21230, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 66, 'I don\'t have to be a fortune-telling imp to know the answer to that one - NO!', 15, 0, 100, 0, 0, 0, 0, 21231, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 67, 'The odds are 32.33 (repeating of course) percent chance of success.', 15, 0, 100, 0, 0, 0, 0, 21232, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 68, 'When Blackrock freezes over!', 15, 0, 100, 0, 0, 0, 0, 21233, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 69, 'Hahahaha, you\'re kidding right?', 15, 0, 100, 0, 0, 0, 0, 21234, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 70, 'Inconceivable!', 15, 0, 100, 0, 0, 0, 0, 21235, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 71, 'My sources say \"no\". Before the torture, that is.', 15, 0, 100, 0, 0, 0, 0, 21236, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 72, 'That\'s about as likely as me getting out of this ball.', 15, 0, 100, 0, 0, 0, 0, 21237, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 73, 'You picked the wrong time to shake me today, buddy!  Prepare for disappointment.', 15, 0, 100, 0, 0, 0, 0, 21238, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 74, 'Not unless you\'re some kind of super-person.  And don\'t kid yourself, you\'re not.', 15, 0, 100, 0, 0, 0, 0, 21239, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 75, 'That\'s about as likely as me getting a date with a succubus.', 15, 0, 100, 0, 0, 0, 0, 21240, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 76, 'My fortune telling powers are immeasurable - your chances are though: NO CHANCE', 15, 0, 100, 0, 0, 0, 0, 21241, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 77, 'NO - and don\'t try shaking me again for a better answer!', 15, 0, 100, 0, 0, 0, 0, 21242, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 78, 'Yes is my answer...', 15, 0, 100, 0, 0, 0, 0, 21243, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 79, '...NOT!', 15, 0, 100, 0, 0, 0, 0, 21244, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 80, 'I\'m gonna have to give this one the big N-O.', 15, 0, 100, 0, 0, 0, 0, 21245, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 81, 'The outlook is very bad - for YOU that is!  Haha, take it!', 15, 0, 100, 0, 0, 0, 0, 21246, 0, '');
INSERT INTO `creature_text` VALUES (23224, 0, 82, 'Survey says: BZZZT!', 15, 0, 100, 0, 0, 0, 0, 21247, 0, '');
INSERT INTO `creature_text` VALUES (23237, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Bonechewer Blood Prophet');
INSERT INTO `creature_text` VALUES (23239, 0, 0, '%s becomes enraged!', 16, 0, 100, 0, 0, 0, 0, 24144, 0, 'Bonechewer Combatant');
INSERT INTO `creature_text` VALUES (23257, 0, 0, 'Look who\'s here.  It\'s $n, our top gun.', 12, 0, 100, 0, 0, 0, 0, 21885, 0, 'Skyguard Windcharger');
INSERT INTO `creature_text` VALUES (23257, 0, 1, '$n, terror of the skies, how are you doing?', 12, 0, 100, 0, 0, 0, 0, 21893, 0, 'Skyguard Windcharger');
INSERT INTO `creature_text` VALUES (23257, 0, 2, 'A-TEN-HUT, officer $n on the deck!', 12, 0, 100, 0, 0, 0, 0, 21891, 0, 'Skyguard Windcharger');
